#page:before {
background-image: url(/*[[foto izquierda]]*/) !important;
left: /*[[Mi1]]*/% !important;
top: /*[[Mi2]]*/% !important;
height: /*[[Mi3]]*/% !important;
}
      
 
#page:after {
