right: /*[[Md1]]*/% !important;
top: /*[[Md2]]*/% !important;
height: /*[[Md3]]*/% !important;
}  
  
#page:before {
content: "" !important;
width: 100% !important;
position: fixed !important;
bottom: 0;
display: block !important;
background-repeat: no-repeat;
background-position: center bottom;
background-size: contain !important;/*
z-index: -2;  */
}
  
