css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("i-comparateur.com") {
  body {
    background: #222;
    color: #aaa;
  }
  
  p, .BRip .BRipRow strong {
    color: #ccc;
  }
  
  a, .SBotContent a {
    color: #f70;
  }
  
  a:hover, .ULCatsBig a:hover, .ULCatsMed a:hover {
    color: #fc7;
  }
  
  #_CatLink, #_BrandNameLink, #_BrandURLLink, #_LinkWriteRating, .BMod .Shop .Content a {
    color: #f70;
  }
  
  .BMod .Main .Label, .BMod .Main .Label a {
    color: #f70;
  }
  
  .BMod .Main .Label a:hover {
    color: #fc7;
  }
  
  .STop, .STopFirst .L, .STopFirst .R, .SMain, .SBot {
    color: #aaa;
    background: #222;
  }
  
  .STopFirst .L {
    width: 648px;
  }
  
  .SCols {
    background: #444 url("http://www.i-comparateur.com/img/icomp/site-cols-bg.gif") top left repeat-y;
  }
  
  .BMini:hover, .BMini:hover a {
    background: #666;
    color: #bbb;
  }
  
  .MasterInfo .UniBg, .MasterInfo .UniBg a {
    color: #f70;
    font-weight: bold;
  }
  
  .BSort div a:hover, .MasterInfo .UniBg a:hover {
    background: rgba(0, 0, 0, 0.2);
    color: gold !important;
  }
  
  .BSort .OngOn {
    background: rgba(0, 0, 0, 0.2);
    color: #f70 !important;
    font-weight: bold;
  }
  
  .BMod:hover {
    background: #666;
  }
  
  .BMod .Shop .Content strong {
    color: goldenrod;
  }
  
  .STopSearch .Field {
    background: url("http://www.i-comparateur.com/img/icomp/site-search-bg.png") top left no-repeat;
    filter:invert(0.85);
  }
  
  .STopSearch .Submit, .STopSearch .Check {
    filter:invert(1);
  }
  
  .BTabsTop, .BRip .BRipRow, .TList .Z {
    background: #666;
  }
  
  .TList tr:hover td {
    background: #888;
    color: #444;
  }
  
  .Pr {
    color: #e33;
  }
}
