css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("meyerweb.com") {

* {
 text-decoration: none!important;
 font-family: Comic Sans, Comic Sans MS, cursive!important;
 text-shadow: none!important;
 box-shadow: none!important;
 transition: all 1.5s ease!important;
 background: #444;
 color: #aaa;
}

textarea {
	background: #666;
  color: #ccc;
  border: 1px solid #888;
  margin-bottom: 10px;
}
  
h1 {
  font-family: Arial, sans-serif;
  border-bottom: 0px solid;
}
  
#footer {
 display: none!important;
}

#calendar #today, code, h3#egtop span {
 border: none!important;
}

#extra {
 margin: 2em 0 0!important;
 padding: 2em 0 0!important;
 right: 2px!important;
 width: 19.85em!important;
}

[href] {
 color: #0ff!important;
}

h2, h3, h3 a, h4, [href]:hover, #calendar caption a, 
#calendar th.first, #calendar #today, .date,
#calendar th.last, #calendar #prevMonth, strong a, .arch #archLink a, .css #cssLink a, .tools #toolsLink a, .write #writeLink a, .speak #speakLink a, .other #otherLink a, .write #main ul li:hover cite a, .write #main ul li:hover a, table#eg th {
 color: #ff0!important;
}

.entry:hover p, strong, code, dl dd:hover, dt, #main p, .write #main ul li:hover, table#eg td {
 color: #fff!important;
}
 
.banner h2 {
 color: #000!important;
}

dd, .speak #main ul li {
 color: #888!important;
}

.entry:hover .entry ul {
 color: #bbb!important;
 background: rgba(115,90,0,.5)!important;
}

p:hover, .entry ul:not(.meta):hover {
 color: #000!important;
 background: rgba(215,190,0,.5)!important;
}

body, #main, #calendar td, table#eg td.resources, table#eg th.resources {
 background: #000!important;
}

table#eg td.dates, table#eg th.dates {
 background: #200!important;
}

table#eg td.event, table#eg th.event {
 background: #002!important;
}

pre, code {
 background: rgba(123,23,23,.9)!important;
}

h3#egtop span {
 background: rgba(0,0,0,0)!important;
}

/* Orange */

#calendar table {
 background: -moz-radial-gradient(center, ellipse cover, rgba(178,118,0,1) 0%, rgba(0,0,0,0) 100%)!important;
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,rgba(178,118,0,1)), color-stop(100%,rgba(0,0,0,0)))!important;
background: -webkit-radial-gradient(center, ellipse cover, rgba(178,118,0,1) 0%,rgba(0,0,0,0) 100%)!important;
background: -o-radial-gradient(center, ellipse cover, rgba(178,118,0,1) 0%,rgba(0,0,0,0) 100%)!important;
background: -ms-radial-gradient(center, ellipse cover, rgba(178,118,0,1) 0%,rgba(0,0,0,0) 100%)!important;
background: radial-gradient(ellipse at center, rgba(178,118,0,.35) 0%,rgba(0,0,0,0) 100%)!important;
}

/* Red */

#calendar th, #calendar #today {
 background: -moz-radial-gradient(center, ellipse cover, rgba(180,0,0,1) 0%, rgba(0,0,0,1) 72%)!important;
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,rgba(180,0,0,1)), color-stop(72%,rgba(0,0,0,1)))!important;
background: -webkit-radial-gradient(center, ellipse cover, rgba(180,0,0,1) 0%,rgba(0,0,0,1) 72%)!important;
background: -o-radial-gradient(center, ellipse cover, rgba(180,0,0,1) 0%,rgba(0,0,0,1) 72%)!important;
background: -ms-radial-gradient(center, ellipse cover, rgba(180,0,0,1) 0%,rgba(0,0,0,1) 72%)!important;
background: radial-gradient(ellipse at center, rgba(180,0,0,1) 0%,rgba(0,0,0,1) 72%)!important; z-index: 999!important;
}

/* Green */

#calendar th.first, 
#calendar th.last, #calendar tbody .first, #calendar tbody .last, .arch #archLink a, .css #cssLink a, .tools #toolsLink a, .write #writeLink a, .speak #speakLink a, .other #otherLink a {
 background: -moz-radial-gradient(center, ellipse cover, rgba(0,100,0,1) 0%, rgba(0,0,0,1) 72%)!important;
background: -webkit-gradient(radial, center center, 0px, center center, 72%, color-stop(0%,rgba(0,100,0,1)), color-stop(100%,rgba(0,0,0,1)))!important;
background: -webkit-radial-gradient(center, ellipse cover, rgba(0,100,0,1) 0%,rgba(0,0,0,1) 72%)!important;
background: -o-radial-gradient(center, ellipse cover, rgba(0,100,0,1) 0%,rgba(0,0,0,1) 72%)!important;
background: -ms-radial-gradient(center, ellipse cover, rgba(0,100,0,1) 0%,rgba(0,0,0,1) 72%)!important;
background: radial-gradient(ellipse at center, rgba(0,100,0,1) 0%,rgba(0,0,0,1) 72%)!important;
}

/*  Blue */

#calendar caption a, .navigation .prev a, .navigation .next a, h3#egtop {
 background: -moz-radial-gradient(center, ellipse cover, rgba(0,0,150,1) 0%, rgba(0,0,0,1) 72%)!important;
background: -webkit-gradient(radial, center center, 0px, center center, 72%, color-stop(0%,rgba(0,0,150,1)), color-stop(100%,rgba(0,0,0,1)))!important;
background: -webkit-radial-gradient(center, ellipse cover, rgba(0,0,150,1) 0%,rgba(0,0,0,1) 72%)!important;
background: -o-radial-gradient(center, ellipse cover, rgba(0,0,150,1) 0%,rgba(0,0,0,1) 72%)!important;
background: -ms-radial-gradient(center, ellipse cover, rgba(0,0,150,1) 0%,rgba(0,0,0,1) 72%)!important;
background: radial-gradient(ellipse at center, rgba(0,0,150,1) 0%,rgba(0,0,0,1) 72%)!important;
}

.navigation .prev a:hover, .navigation .next a:hover {
 background: -moz-radial-gradient(center, ellipse cover, rgba(0,0,255,1) 0%, rgba(0,0,0,1) 72%)!important;
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,rgba(0,0,255,1)), color-stop(72%,rgba(0,0,0,1)))!important;
background: -webkit-radial-gradient(center, ellipse cover, rgba(0,0,255,1) 0%,rgba(0,0,0,1) 72%)!important;
background: -o-radial-gradient(center, ellipse cover, rgba(0,0,255,1) 0%,rgba(0,0,0,1) 72%)!important;
background: -ms-radial-gradient(center, ellipse cover, rgba(0,0,255,1) 0%,rgba(0,0,0,1) 72%)!important;
background: radial-gradient(ellipse at center, rgba(0,0,255,1) 0%,rgba(0,0,0,1) 72%)!important;
}


}
