css:
@-moz-document domain("css-tricks.com") {
* {
 text-decoration: none!important;
 text-shadow: none!important;
 box-shadow: none!important;
 color: #888!important;
 transition: color, opacity .5s ease .1s!important;
}

  a {
    color: #55d;
  }

[class*="-ad"], 

.bsap,
.module-blogpost::before,
.module-hotlink::before,
.module-blogpost::after,
.module-hotlink::after 
    
{
 display: none!important;
}

article a,
footer a,

.button,
.gsc-wrapper.gsc-thinWrapper,
.gsc-webResult.gsc-result, 
.gsc-results .gsc-imageResult,
.gwn-slider__content__earn__span,
.jfmfs-friend,
.submit_gray,
.submit_blue, 
.video-nav .current,
.wp-polls INPUT

{
 border: none!important;
}

body, .group

{
 background-image: none!important;
}

.page-wrap {
 width: 100%!important;
 max-width: none!important;
}

.comments-title {
clear: both;
padding-top: 30px!important;
color: white;
}

.main-nav a {
width: 150px!important;
}

.main-nav a svg {
 fill: none!important;
}

.content-wrap {
 width: 100%!important;
}

.grid {
 padding: 40px 0px 40px 5px!important;
}

.grid-2-3 {
 width: 77%!important;
}

.grid-1-3 {
 width: 23%!important;
}

element.style {
}

[class*='grid-'] {
 padding-right: 0px!important;
 margin: 0 0 10px 0!important;
}

.commentlist .comment-avatar img {
 padding-right: 40px!important;
}

.type-forum {
 padding: 5px 0px 10px 5px!important;
}

.commentlist>.comment, .CommentForm>.comment, .comment ul>.comment {
 margin: 0!important;
 padding: 5px 0px 2px 5px!important;
}
  
.commentlist .children, .CommentForm .children, .comment ul .children {
 margin: 0!important;
 padding: 0px 0px 2px 5px!important;
}

.gsc-thinWrapper {
 width: 95%!important;
}


}

@-moz-document domain("css-tricks.com") {
[href] {
 color: #0ff!important;
}
[href]:visited {
 color: #f0f!important;
}

.token.comment {
color: #999!important;
}

[href]:hover,
[class*="module-"]:hover a, 
[class*="module-"]:hover h4,

aside .module:hover h4,
input[type="checkbox"]:checked,
table tr th span,

.article-header h1,
.almanac-list>li:hover a,
.button:hover,
.bbpress-title a,
.breadcrumbs-header .breadcrumb_last,
.commentPreviewButton.active,
.comments-title,
.comment-wrap:hover .comment-author,
.comment-wrap:hover .comment-author a,
.forum-titles > li,
.forums-module:hover a,
.gsc-webResult .gsc-result:hover .gs-title,
.list-of-snippets>ul>li:nth-child(odd):hover a, 
.list-of-snippets>ul>li:nth-child(even):hover a,
.main-nav a:hover, 
.main-nav a:hover span,
.module:hover h2 a,
.related-articles-title,
.type-forum:hover a, 
.type-topic:hover a,
.topic-title,
.topic-select option:hover,
.video-nav .current

{
 color: #fff !important;
}
 
[class*="module-"] a, 
[class*="module-"]:hover p,
[class*="module-"]:hover label, 
article li:hover,
p:hover,

.almanac-list>li:hover .children a, 
.commentlist .children li:hover .comment-content p,
.comment-wrap:hover p,
.footer-shoptalk p>a, 
.forums-module:hover p,
.gsc-webResult .gsc-result:hover .gs-snippet,
.main-nav a,
.text li:hover,
.type-forum:hover p,
.type-forum:hover li,
.type-topic:hover p

{
 color: #ddd!important;
}

tt,
.module h3,
.module h4,
.posting-code h3,
.posting-code h4,
.site-footer h2

{
 color: #ffae00!important;
}

[class*="module-"],
[class*="module-"] p,
[class*="module-"] label,
li,
p [class*="module-"] h4,

.almanac-list>li>ul>li a,
.type-forum a
    
{
 color: #555!important;
}
        
.footer-shoptalk p {
 color: #000!important;
}
.language-css,
.language-javascript,
.language-markup {
color: #ddd!important;
}

.token.selector {
color: #eef7be!important;
}
    
.token.function {
color: #9df45f!important;
}

p code,
.token.keyword {
color: #f4755f!important;
}

.token.atrule {
color: #24e7f2!important;
}

.token.tag {
color: #f7aa5e!important;
}

.token.id {
color: #5f9bf4!important;
}
    
.token.pseudo-class {
color: #f78a8a!important;
}
        
.gsc-webResult .gsc-result:hover .gs-title b,
.gsc-webResult .gsc-result:hover .gs-snippet b,
.token.pseudo-element {
color: #04e596!important;
}
            
.token.string {
color: #35e504!important;
}

.token.punctuation {
color: #5fedf4!important;
}
        
.token.attr-name {
color: #a384f9!important;
}
            
.token.attr-value {
color: #f9f984!important;
}
    
.token.property {
color: #f7aa5e!important;
}

.token.boolean {
color: #192!important;
}

.token.number {
color: #192!important;
}

textarea:focus, 
input[type="text"]:focus,
input[type="email"]:focus, 
input[type="password"]:focus,
input[type="url"]:focus, 
input[type="search"]:focus 

{
 color: #000!important;
 background-color: #fff!important;
}

textarea,
input[type="text"],
input[type="email"],
input[type="password"],
input[type="url"],
input[type="search"]

{
 color: #888!important;
 background-color: #111!important;
}




}

@-moz-document domain("css-tricks.com") {
html, body,
figure,
[class*="module"] .intro-lodge, 
[class*="module"], 

.article-header,
.cse .gsc-control-cse,
.grid,
.gsc-control-cse,
.gsc-webResult.gsc-result,
.home .content-wrap,
.page-wrap,
.single .content-wrap,
.top,
.type-forum

{
 background-color: #222!important;
}

.almanac-page,
.bbpress .breadcrumbs,
.bbpress .content-wrap,
.breadcrumbs-header,
.browser-support-table .yep,
.commentlist>.comment,
.forums-login-form,
.home .content-wrap,
.mailchimp-signup,
.meta,
.page-template-video-archive-php .content-wrap,
.page-template-page-snippet-php .content-wrap,
.commentPreviewButton,
.site-footer,
.snippets-nav-wrapper,
.type-topic

{
 background: #000!important;
}

[class*="module-"],

.button,
.bbpress-title a,
.main-nav a.videos svg:hover,
.topic-select option,
.topic-select

{
 background-color: rgba(0,0,0,0)!important;
}

svg:hover,

.commentlist .children li 

{
 background: transparent!important;
}

blockquote,

.explanation,
.list-of-snippets>ul>li:nth-child(even)

{
 background-color: #111!important;
}

p code

{
 background-color: #333!important;
}

.list-of-snippets>ul>li:nth-child(odd) 

{
 background-color: #222!important;
}

/* Green */

.commentPreviewButton.active,
.video-nav .current 

{
background: radial-gradient(ellipse at center, rgba(0,100,0,1) 0%,rgba(0,0,0,1) 72%)!important;
}

/* Blue */

.button
  
{
background: radial-gradient(ellipse at center, rgba(0,0,100,1) 0%,rgba(0,0,0,1) 72%)!important;
}

.button:hover

{
background: radial-gradient(ellipse at center, rgba(0,0,200,1) 0%,rgba(0,0,0,1) 72%)!important;
}
}

@-moz-document domain("css-tricks.com") {
img, 

.module-subscribe .square-rss,
.module-subscribe .square-itunes,
.promo-footer-part,
.social-list li,
.snippet-top-level-nav a 

{
 opacity: .3!important;
}

img:hover, 

.comment-wrap:hover img,
.commentlist .children li:hover img,
.footer-shoptalk img,
.forums-module:hover img,
.gsc-webResult .gsc-result:hover img,
.module-subscribe .square-rss:hover,
.module-subscribe .square-itunes:hover,
.promo-footer-part:hover,
.social-list li:hover,
.snippet-top-level-nav a:hover,
.snippet-top-level-nav a.active,
.type-topic:hover img

{
 opacity: 1!important;
}
  .mega-header .author-byline-inside {
    background: #222;
  }
}
