css:
@-moz-document domain("wanikani.com") {
/*TODO
        Change all hex to rgb
        Clean the source
        Paint the arrow
        Get rid of duplicates
        Fix Tumblr
        Figure out how to select specific domains
    */
	body {
		background-color: rgb(67, 72, 88) !important;
		background-image: none !important;
		text-shadow: rgb(57, 62, 78) !important;
		color: white !important;
	}
	.review-status,
	header p,
	.standard p,
	.standard ol,
	:not(.span6) > .forum-topics-list table td,
	:not(.span6) > .forum-topics-list a,
	.span6 .small-caps :not(.see-more),
	.topic-title :not(.forum-topics-list) :not(.dashboard-sub-section),
	.icon-pushpin,
	br+ a :not(.forum-topics-list) :not(.dashboard-sub-section),
	.timeago :not(.forum-topics-list) :not(.dashboard-sub-section) {
		color: rgb(225, 225, 225) !important;
		text-shadow: 0 1px 0 rgb(57, 62, 78) !important;
	}
	#chat p {
		color: #666 !important;
		text-shadow: 0 1px 0 #fff,0 0 10px #fff !important;
	}
	.dashboard section.review-status ul li {
		border-left-color: rgb(47, 52, 68) !important;
	}
	.dashboard section.srs-progress {
		color: rgb(225, 225, 225) !important;
		box-shadow: inset 1px 10px 9px 0px rgba(0, 0, 0, 0.3) !important;
	}
	code {
		text-shadow: 0 1px 0 #fff !important;
	}
	.page-list,
	.forum-topics-list:not(.dashboard-sub-section) table tbody tr:hover {
		background-color: rgb(67, 72, 88) !important;
	}
	.page-list ul>li>a,
	.page-list ul>li>span {
		border: 1px solid rgb(67, 72, 88) !important;
	}
	.page-list-header span {
		text-shadow: 0 1px 0 rgb(57, 62, 78) !important;
	}
	.navbar-inner {
		background-image: linear-gradient(to bottom, rgb(82, 87, 103), rgb(77, 82, 98)) !important;
		border-bottom-color: rgb(77, 82, 98) !important;
	}
	.navbar .nav>li>a {
		text-shadow: 0 1px 0 rgb(57, 62, 78);
		color: rgb(225, 225, 225) !important;
	}
	html#main .navbar .nav>li:not(.lessons):not(.reviews)>a>span:first-child,
	html#public-profile .navbar .nav>li:not(.lessons):not(.reviews)>a>span:first-child {
		box-shadow: 0 1px 0 rgb(57, 62, 78), inset -2px 2px 0 rgba(0, 0, 0, 0.15);
	}
	header nav #back-dashboard {
		box-shadow: 0 1px 0 rgb(57, 62, 78), inset -2px 2px 0 rgba(0, 0, 0, 0.15) !important;
	}
	#lesson #batch-items ul li span,
	#reviews-summary #incorrect .apprentice>ul>li,
	#reviews-summary #incorrect .guru>ul>li,
	#reviews-summary #incorrect .master>ul>li,
	#reviews-summary #incorrect .enlightened>ul>li,
	#reviews-summary #incorrect .burned>ul>li,
	#reviews-summary #correct .apprentice>ul>li,
	#reviews-summary #correct .guru>ul>li,
	#reviews-summary #correct .master>ul>li,
	#reviews-summary #correct .enlightened>ul>li,
	#reviews-summary #correct .burned>ul>li,
	#reviews-summary #review-stats [id*="review-stats-"],
	#lessons-summary #radicals>div>ul>li,
	#lessons-summary #kanji>div>ul>li,
	#lessons-summary #vocabulary>div>ul>li {
		box-shadow: 3px 3px 0 rgb(57, 62, 78) !important;
	}
	#lesson #batch-items ul li[data-index=quiz] span {
		background-color: #848484 !important;
	}
	footer:not(.pure-u-1):not(#copyright):not(#last-session-date) {
		background-image: url("https://raw.githubusercontent.com/misabiko/Wanikani-Grayish-Blueish-Dark/master/Assets/footer-bg-invert.gif");
	}
	#supplement-info,
	div #correct,
	div #incorrect,
	div #radicals,
	div #kanji,
	div #vocabulary,
	div #review-stats-radicals,
	div #review-stats-kanji,
	div #review-stats-vocabulary {
		background-color: rgb(77, 82, 98) !important;
		box-shadow: 3px 3px 0 rgb(57, 62, 78) !important;
	}
	#item-info #all-info {
		background-color: rgb(67, 72, 88) !important;
	}
	#reviews-summary #incorrect .apprentice h3 span,
	#reviews-summary #incorrect .guru h3 span,
	#reviews-summary #incorrect .master h3 span,
	#reviews-summary #incorrect .enlightened h3 span,
	#reviews-summary #incorrect .burned h3 span,
	#reviews-summary #correct .apprentice h3 span,
	#reviews-summary #correct .guru h3 span,
	#reviews-summary #correct .master h3 span,
	#reviews-summary #correct .enlightened h3 span,
	#reviews-summary #correct .burned h3 span,
	#loading {
		background-color: rgb(77, 82, 98) !important;
	}
	#supplement-voc-breakdown ul li a,
	#item-info #related-items a,
	#item-info #related-items a:active,
	#item-info #related-items a:hover,
	#item-info #related-items a:visited,
	#item-info h2,
	#item-info #all-info:hover,
	.active,
	#kana-chart ul li span{
		color: white !important;
	}
	#lesson #supplement-info .radical,
	#lesson #supplement-info .kanji,
	#lesson #supplement-info .vocabulary,
	#item-info #related-items ul span {
		box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.2) inset, 0 0 10px rgba(27, 27, 27, 0.5) !important;
	}
	#next-btn:hover,
	#prev-btn:hover {
		background-color: rgba(104, 111, 125, 1) !important;
	}
	#kana-chart ul li:not(.empty):hover  {
		background-color: rgb(87, 92, 108) !important;
		box-shadow: 3px 3px 0 rgb(67, 72, 88) !important;
	}
	#kana-chart ul li {
		border-right: 1px solid rgb(67, 72, 88);
	}
	#kana-chart ol {
		border-bottom: 1px solid rgb(67, 72, 88);
	}
	#kana-chart ol li.active {
		border-bottom: 3px solid white;
	}
	#kana-chart ol li:not(.active):not(.backspace):hover {
		border-bottom: 3px solid #333;
	}
	#additional-content ul .wrap-up-selected span, #additional-content ul .wrap-up-selected span:hover:before {
		background-color: rgb(117, 122, 138) !important;
	}
	.highlight-kanji {
		background-color: #dd0093 !important;
	}
	.highlight-radical {
		background-color: #0af !important;
	}
	.highlight-vocabulary {
		background-color: #9300dd !important;
	}
	.highlight-reading {
		background-color: #8e8e8e !important;
	}
	#lesson #supplement-info {
		background-color: rgb(77, 82, 98) !important;
		box-shadow: 3px 3px 0 rgb(57, 62, 78) !important;
	}
	#lesson #supplement-info #supplement-rad-related-kanji ul li a,
	#lesson #supplement-info #supplement-kan-breakdown ul li a,
	#lesson #supplement-info #supplement-kan-related-vocabulary ul li a,
	#lesson #supplement-info #supplement-voc-breakdown ul li a {
		color: white !important;
	}
	#lesson #supplement-info blockquote {
		background-color: rgb(81, 86, 104) !important;
		color: #EAEAEA !important;
	}
	#quiz #question-type.meaning {
		background-image: linear-gradient(rgb(60, 65, 81), rgb(77, 82, 98)) !important;
		border-top: 1px solid rgb(50, 55, 71) !important;
		border-bottom: 1px solid rgb(67, 72, 88) !important;
		color: rgb(225, 225, 225) !important;
		text-shadow: -1px -1px 0 rgba(0, 0, 0, 0.1) !important;
	}
	#answer-form input[type=text] {
		box-shadow: 3px 3px 0 rgb(57, 62, 78) !important;
	}
	#additional-content ul li span {
		background-color: rgb(77, 82, 98) !important;
		box-shadow: 3px 3px 0 rgb(57, 62, 78) !important;
		color: white !important;
	}

	#additional-content ul li:not(#option-audio) span:hover:before {
		background-color: rgb(77, 82, 98) !important;
		box-shadow: 3px 3px 0 rgb(57, 62, 78) !important;
		color: white !important;
	}
	#information {
		background-color: rgb(77, 82, 98) !important;
		box-shadow: 3px 3px 0 rgb(57, 62, 78) !important;
	}
}
