css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("twitter.com") {

.dashboard {
float: center;
width: 100% !important}

.DashboardProfileCard-bg{height:230px;position:absolute; width:100%;margin-left:0%;margin-right:0%;border-radius: 0px 0px 0 0; border:0px;margin-top:-10px;}

.DashboardProfileCard {
border: 0px solid #e1e8ed;
background: transparent;
border-radius: 0px;}
::-webkit-scrollbar{
	width: 15px;
	height: 0px;
	background-color: #a1a1a1;
	opacity: 0%;
}
    
::-webkit-scrollbar-thumb {
  background-color: #f1f1f1;
  border-top-left-radius: 10px;
  border-top-right-radius: 10px;
  border-bottom-right-radius: 10px;
  border-bottom-left-radius: 10px;
}

.DashboardProfileCard-avatarLink {
background-color: transparent;
border-radius: 100px;
margin-left:4%;
padding-top:5%;
position:relative;}

.DashboardProfileCard-userFields {
position: relative;
text-align:center;
font-family:segoe ui, helvetica, verdana;
top: 1%;
left: 0%;
width: 190px;
background: #fcfcfc; opacity:0.6;
padding:15px;}

.DashboardProfileCard-avatarImage {
border-radius: 100px;
height: 120px;
width: 120px;
border: 0px solid #fff;
color: transparent;}

.ProfileCardStats {
margin-left: 0%;
margin-right:-2px;
padding: 2%;
text-align:center;
background:#fafafa; opacity:0.8;
position:relative !important;
padding-top:25px;
margin-top:-1px;}

.ProfileAvatar {
background: transparent;
border: 0px solid #fff;
border-radius: 100px;
box-shadow: 0 1px 1px rgba(136,153,166,0.15);
height: 200px;
position: relative;
width: 200px;}

.ProfileAvatar-image, .ProfileAvatar-placeholderImage {
border-radius: 100px;
height: 100%;
left: 40px;
position: relative;
top: -15px;
width: 100%;}

.wtf-module.has-content {display: none}

.Trends{display:none;left:-500px}

.tweet{font-family:segoe ui, helvetica;}

.module ul:last-child li:last-child .list-link, .module ul:last-child li.last-item .list-link, .module .list-link.last, .module .flex-module:last-child, .module .contrast-flex-module:last-child{display:none}

body.three-col .content-main {
float: center;
margin: 0 0px;
width: 100%;
position:relative;
opacity:1;}

a{color:#fafafa}
.ProfileTweet-screenname {
color: #fafafa;
font-size: 13px;
}
.content-main, .content-narrow, .page-canvas, .permalink {
border-radius: 0px;
}
.top-timeline-tweetbox .tweet-user {
border-radius: 0px 0px 0 0;
position: relative; margin-bottom:1%;margin-top:1%;
background-color:#898989}

.top-timeline-tweetbox .timeline-tweet-box {
border-radius: 0px 0px 0 0;
border: 0px solid #898989;
width:107%;
margin-left:-69px
}
.top-timeline-tweet-box-user-image {
display:none;
padding-left:-500px;
}
.tweet-form .tweet-content {
position: relative;
margin-bottom: 8px;
width:100%;
margin-left:0px; margin-right:0px;}

.top-timeline-tweetbox .RichEditor-scrollContainer, .inline-reply-tweetbox .RichEditor-scrollContainer {
max-height:none;width:100%
}
.RichEditor div[contenteditable], .RichEditor div[contenteditable]:focus, .RichEditor div[contenteditable].fake-focus {
border: 0;
border-radius: 0;
box-shadow: none;
font-size: 14px;
padding: 8px 10px;
}
.size32 {
width: 32px;
height: 32px;
border-radius: 50px;
}
.stream, .stream-container, .stream-items {
position: relative;
margin-left: 0;
list-style: none;
}
*:focus {outline: none;}

.WhoToFollow.is-visible {
display: none;right:500px;
}
.ProfilePage.three-col .AppContent-main.content-main{background-color:#bcbcbc;}
a.list-link.js-nav{display:none}
.ProfileHeading{background-color:#898989}
#page-outer{
height: 100%}
.tweet {border-radius:10px; margin-bottom:5px;
border-bottom: 0px solid #e1e8ed;
cursor: pointer;
}
.ep-TweetPerformance.ep-Section .ep-EngagementsTable .ep-EngagementsTableHeader .ep-EngagementsTableMetricType {
padding-bottom: 10px;
font:#fafafa}

.global-nav-inner{
background: #121212;
height: 46px;border-bottom:0px;
}

.nav .session .dropdown-toggle {
width: 32px;
height: 32px;
border: 0;
opacity: 1;}

.dropdown-toggle {
border: 0;
opacity: 0;
border-radius:50px;}

body {
background-color:#898989;
font-family:segoe ui, helvetica;
}
}
