@-moz-document domain("discordapp.com") {
/*---MAIN---*/

.tab-bar.TOP {border-bottom: 0px !important;}

.tab-bar.TOP .tab-bar-item.selected {
	border-bottom: 2px solid #1ED760 !important;
	color: #FFFFFF;
}

.search-bar {background-color: #181818 !important;}
.private-channels .search-bar {background-color: #282828 !important; box-shadow: none !important;}
.search-bar .search-bar-inner {background-color: transparent !important;}

.search-bar input {
	background-color: #FFF !important;
	color: black !important;
	border-radius: 24px !important;
}
.search-bar-clear:before {background: #FFF !important;}

.account {border-top: 0px !important;}

.account .btn,
#voice-connection .btn {
	border-radius: 30px;
	border: 1px solid grey;
	background-color: transparent;
}

.account .btn:hover,
#voice-connection .btn:hover {
	border-radius: 30px;
	border: 1px solid #FFF;
	background-color: transparent;
}

.modal .modal-inner {box-shadow: 0 1px 70px #000;}
.modal-content, .modal-inner {background-color: #181818 !important;}
.modal .modal-image {background-color: #181818 !important;}

.download-apps .platforms .platform.active {border-color: #179443 !important;}
.download-apps .platforms .platform.active .download-button {background-color: #179443 !important;}
.download-apps .platforms .platform.active p {color: #1ec057 !important;}
.download-apps .footer a {color: #1DB954 !important;}

.alert.form.has-icon:before {background-color: transparent !important;}
.alert.form .form-inner h4 {color: #D2D2D2 !important;}

/*--SVG--*/

.alert.form.has-icon {background-image: url(https://imgh.us/.alert.form.has-icon.svg) !important;}
.create-guild-container .action.create .action-icon {background: url(https://imgh.us/.action.create_.action-icon.png) center center no-repeat !important; background-size:109px 78px !important;}
