.channel-notices .channel-notice.invite {background-image: url(https://imgh.us/.channel-notices_.channel-notice.invite.svg) !important;}
.icon.icon-file {background: url(https://imgh.us/.icon.icon-file.svg) no-repeat !important;}
.icon.unknown {background: url(https://imgh.us/.icon.unknown.svg) no-repeat !important;}
.icon.icon-file.unknown {background: url(https://imgh.us/.icon.icon-file.svg) no-repeat !important;}
.icon.webcode, .icon.one {background: url(https://imgh.us/.icon.webcode.svg) !important;}
.icon.icon-file.webcode {background: url(https://imgh.us/.icon.icon-file.webcode.svg) no-repeat !important;}
.icon.code, .icon.two {background: url(https://imgh.us/.icon.code.svg) !important;}
.icon.icon-file.code {background: url(https://imgh.us/.icon.icon-file.code.svg) no-repeat !important;}
.icon.document, .icon.three {background: url(https://imgh.us/.icon.document.svg) !important;}
.icon.icon-file.document {background: url(https://imgh.us/.icon.icon-file.document.svg) no-repeat !important;}
.icon.exclamation {background-image: url(https://imgh.us/.icon.exclamation.svg) !important;}
.icon.share {background-image: url(https://imgh.us/.icon.share.svg) !important;}
.icon.apps {background-image: url(https://imgh.us/.icon.apps.svg) !important;}
.icon.mobile {background-image: url(https://imgh.us/.icon.mobile.svg) !important;}
.icon.twitter {background-image: url(https://imgh.us/.icon.twitter.svg) !important;}
.icon.apple.active {background-image: url(https://imgh.us/.icon.apple.active.svg) !important}
.icon.windows.active {background-image: url(https://imgh.us/.icon.windows.active.svg) !important}
.icon.ios.active {background-image: url(https://imgh.us/.icon.ios.active.svg) !important}
.icon.android.active {background-image: url(https://imgh.us/.icon.android.active.svg) !important}
.messages-popout .empty-placeholder .image {background-image: url(https://imgh.us/.messages-popout_.empty-placeholder_.image.svg) !important;}
.permission-checkmark {background: url(https://imgh.us/.permission-checkmark.svg) no-repeat !important; height: 12px !important;}
.streamer-mode-enabled .streamer-mode-enabled-image, .empty-icon-streamer-mode {background: url(https://imgh.us/.streamer-mode-enabled_.streamer-mode-enabled-image.svg) center center no-repeat !important;}
.empty-icon-streamer-mode {background-size: 160px 110px !important;}

/*--/SVG--*/

.instant-invite-modal {background: transparent !important;}

.modal .modal-image .image {
	display: block !important;
	margin: auto !important;
}

.modal .download-button {text-align: center;}

.radio-theme.light label {display: none;}
.radio-theme.dark label {
    width: 513px;
    background: url("https://imgh.us/theme_1.png") !important;
    background-size: cover;
	border-color: #282828 !important;
}

.callout-backdrop {background: rgba(0,0,0,0.3) !important;}

.change-log, .markdown-modal-footer {background-color: #1C1C1C !important;}
.markdown-modal-header, .markdown-modal-footer {border: 0px !important;}
.markdown-modal pre, .markdown-modal pre code {
	background: #282828;
	border: 3px solid #282828;
}

.scroller-wrap .scroller::-webkit-scrollbar {width: 16px;}

.scroller-wrap .scroller::-webkit-scrollbar-thumb:hover {background-color: #404040 !important;}

.scroller-wrap .scroller::-webkit-scrollbar-thumb {
	border: 0px !important;
	border-radius: 0;

	background-color: #333333 !important;
}

.scroller-wrap .scroller::-webkit-scrollbar-track-piece {
	border: 0px !important;
	background-color: transparent !important;
}

.title-wrap, #bd-customcss-detach-container {
	border-bottom: 0px !important;
	background-color: #282828 !important;
}

.title-wrap .topic a {color: #1DB954 !important;}

#user-profile-modal .guilds .guild:hover {
	background: #181818 !important;
	color: #D2D2D2 !important;
}

#user-profile-modal .guilds .avatar-large {background-color: #282828 !important;}

#user-profile-modal .empty {background: #181818;}

#user-profile-modal .tab-bar-container {
	border-bottom: 0px !important;
	background-color: #181818 !important;
}

#user-profile-modal footer {background: #181818;}

.sub-header, #user-profile-modal .tab-bar {
	background-color: #181818 !important;
	border-bottom: 0px !important;
}

.tab-bar.TOP .tab-bar-item:not(.selected):hover {
	color: #FFFFFF;
	border-bottom: 0px !important;
}

.spinner-wandering-cubes .spinner-item {
	background: #1DB954;
}

.popout .btn,
.sub-header .btn,
.btn.add-friend,
.btn-help:hover,
#bd-customcss-detach-container #bd-customcss-detach-controls-buttons button,
#friends .btn,
#friends .btn:hover,
.upload-modal,
.upload-drop-modal .bgScale,
#friends .friends-header .tab-bar .tab-bar-item.tab-bar-item-primary:not(.selected) {
	background: #1DB954 !important;
	border: 0px !important;
}

#friends .friends-header .tab-bar .tab-bar-item:not(.selected) {transition: all 200ms ease-in-out;}
#friends .friends-header .tab-bar .tab-bar-item:hover:not(.selected) {background: rgba(0,0,0,0.2) !important;}

.upload-modal .footer, .upload-modal .footer .button, .upload-modal .footer .button span,
.channel-textarea-guard button {
	background-color: #179443 !important;
	color: white;
}

.upload-modal .inner .file .icon.image {border: 2px solid #1aa64b;}
.upload-modal .inner, .upload-drop-modal .inner {border: 2px dashed #8edca9;}

.need-help-modal .header,
.icon-friends {background-color: #1DB954 !important;}

.need-help-modal .header input[type=text] {box-shadow: 0 0 0;}

.need-help-modal .form-inner .failed, .need-help-modal .form-inner.loading, .need-help-modal .footer {
	border-top: 1px solid #181818;
	background-color: #282828;
}

/*---/MAIN---*/

/*---GUILDS---*/

.channel-notices .channel-notice.invite {background-color: #333333 !important;}
.channel-notices .channel-notice .message .btn {background-color: #4d4d4d !important;}

.account .btn-group {border: 0px;}

#voice-connection .btn-group {border: 0px;}

.account .avatar-small {border: 1px solid grey;}

.guilds-wrapper .guilds .guild .guild-inner:hover {
	background: #18AC4C !important;
}

.guilds-wrapper .guilds .friends-icon {
	background-color: #1ED760 !important;
	width: 50px;
	height: 50px;
	background-image: url(data:image/png;base64,);
	background-size: 50px 50px;
	background-position: 50%;
	background-repeat: no-repeat;
}

.guilds-wrapper .guilds .guild .guild-inner {
	border-radius: 50%;
	width: 50px;
	background-position: center;
	background: transparent !important;
}

.guilds-wrapper .guilds .guild.selected .guild-inner {
	border-radius: 50% !important;
	width: 50px;
	background-position: center;
	background: transparent !important;
}

.guild-header header {
	background-color: #282828 !important;
	box-shadow: 0 0 0;
}

/*---Canary & PTB---*/

.popout-menu {background: #333333 !important;}
.popout-menu .popout-menu-item:hover {background: #404040 !important;}
.popout-menu .popout-menu-item:not(.leave):not(.invite):hover {color: #A0A0A0;}
.popout-menu .popout-menu-item {color: #A0A0A0;}

/*---/Canary & PTB---*/

.create-guild-container .create-or-join .form-inner {
	background-color: #fff;
	color: #1ED760;
	border: 1px solid grey !important;
}
.create-guild-container .action {
	background-color: #282828;
	color: #FFF;
}
.create-guild-container .create-or-join .actions {
	background-color: #181818 !important;
	border-radius: 50% !important;
}
.create-guild-container .create-or-join .actions .or {
	background: url(https://imgh.us/.or.svg) center center no-repeat !important;
	-webkit-background-size: 52px,52px !important;
	background-size: 52px,52px !important;
	color: #D2D2D2 !important;
}

.channel.channel-text.selected:before,
.private-channels .channel.selected:before {border-left: 4px solid #1ED760 !important;}

.channel.channel-text.selected,
.private-channels .channel.selected {background: none;}

.private-channels .search-result.selected:before, 
.private-channels .search-result:hover:before {border-left: 0px !important;}

.private-channels .search-result .avatar-xsmall {
	width: 40px !important;
	height: 40px !important;
	background-size: 40px 40px !important;
}

.private-channels .search-result.selected, 
.private-channels .search-result {
	margin-left: 12px;
	background: #333333 !important;
}

.private-channels .search-result:hover {background: #4D4D4D !important;}

.channel.channel-text:hover,
.private-channels .channel:not(.selected) {background: none;}

.channel.channel-text:before,
.private-channels .channel:not(.selected):before {border-left: 0px !important;}

.messages-wrapper a,
.messages-wrapper a:hover,
.markdown-modal a,
.form .control-group label a,
.form .control-group label a:hover {color: #1ED760 !important;}

.guilds, #voice-connection {background-color: #181818 !important;}

.guilds-add {border: 1px solid #535559;}
.guilds-add:hover {border: 1px solid #FFF; color: #535559;}

.account, .private-channels .scroller, .guild-channels {background-color: #282828 !important;}

#voice-connection {
	border-top: 1px solid #181818;
	border-bottom: 1px solid #181818;
}

/*---/GUILDS---*/

/*---CHAT---*/

.emoji-picker .categories .item.people {background-image: url(https://discordapp.com/assets/0509bf058793ef8ddd770a977e63614c.svg);}
.emoji-picker .categories .item.custom {background-image: url(https://discordapp.com/assets/bbabd25029321a8260849de01e8c13fc.svg);}
.emoji-picker .categories .item.nature {background-image: url(https://discordapp.com/assets/aafe899a4d7ad7717acc64f5b8f36342.svg);}
.emoji-picker .categories .item.food {background-image: url(https://discordapp.com/assets/0eb5a2b715ff649e9757a232996817ab.svg);}
.emoji-picker .categories .item.activity {background-image: url(https://discordapp.com/assets/8674c1028a63d29fb605be53eeedf1bf.svg);}
.emoji-picker .categories .item.travel {background-image: url(https://discordapp.com/assets/13e86ddf4302f697243425b2f954d00b.svg);}
.emoji-picker .categories .item.objects {background-image: url(https://discordapp.com/assets/6d9a5b47dc3bf7adf54f19b1e3f6c5ad.svg);}
.emoji-picker .categories .item.symbols {background-image: url(https://discordapp.com/assets/83095e6af6c7cf33669e3ab169296867.svg);}
.emoji-picker .categories .item.flags {background-image: url(https://discordapp.com/assets/ccda5cf0a64abbd18fdb9f914b55292e.svg);}
.emoji-picker .categories .item.recent {background-image: url(https://discordapp.com/assets/0f1f2dff98b5e2099086e303d9be0dad.svg);}

.emoji-picker .category, .emoji-picker .header {
	border-top: 1px solid #282828;
	background: #282828 !important;
}
.emoji-item.selected {background-color: #181818 !important;}
.emoji-picker .categories .item.selected {border-bottom: 4px solid #1ED760;}

.highlight {
	background-color: rgba(27, 193, 86, 0.1) !important;
	color: #1ED760;
	border-left: 4px solid transparent;
	border-right: 4px solid transparent;
	border-radius: 24px;
}
.highlight:hover {color: #1DB954 !important;}

.invite-button {background-color: #202020 !important;}
.messages-popout-wrap {background: #202020 !important;}
.messages-popout-wrap .header, .messages-popout-wrap .footer {background: #1C1C1C !important;}

.messages-popout-wrap .messages-popout .message-group {
	background: #282828 !important;
	border: 0px !important;
}

.message-group-blocked-btn {
	background: #242424 !important;
}

.message-group-blocked {
	background: #181818 !important;
	border-color: #3d3d3d !important;
}

.message-group-blocked .message-group:hover {
	background: #181818 !important;
}

.message-group-blocked-btn:hover {
	background: #202020 !important;
}

.chat .welcome-message h1 {color: #1DB954 !important;}

.messages-popout-wrap .messages-popout .message-group:hover .action-buttons {
	box-shadow: none !important;
	background-color: transparent !important;
}

.bot-tag {background-color: #18AC4C;}
.message-group .avatar-large {left: 10px;}

.chat .has-more button {
	background: #282828 !important;
	color: white;
}

.chat>.content .messages .divider span {background: #181818; border-radius: 8px;}
.messages-wrapper .messages .divider:before {background-color: transparent !important;}
.messages-wrapper .messages .divider-red + .divider {margin-top: 20px !important;}
.messages-wrapper .messages .divider.divider-red span {color: #1DB954 !important;}
.messages-wrapper .messages .divider.divider-red > div {background: #1DB954 !important;}
.messages-wrapper, .flex-spacer, #friends {background-color: #181818 !important;}

.flex-wrapper {background: #282828 !important;}

.typing {background: transparent !important}

.channel-textarea-inner, .quick-message, .note textarea:focus {
	color: white !important;
	border-radius: 3px;
	border: 0px;
	background-color: rgba(255,255,255,0.1) !important;
}

.channel-textarea-autocomplete-inner {
	background: #282828 !important;
	border:none !important;
	border-radius: 5px 5px 5px 5px;
}

.channel-textarea-autocomplete-inner:after {display:none;}
.channel-textarea-autocomplete-inner header {
	background: #282828 !important;
	border-radius:5px 5px 0 0;
	border:none !important;
}
.channel-textarea-autocomplete ul li {color: rgba(255,255,255,.6) !important;}
.channel-textarea-autocomplete ul li.active {background: #4D4D4D !important;}

.message-group.is-local-bot-message {
	background: none !important;
	box-shadow: none !important;
	border-bottom: 1px solid rgba(255,255,255,.04) !important;
}

.message-group .comment .markup code.inline,
.message-group .comment .markup pre code {background: #2f2f2f !important;}
.message-group .comment .markup pre {
	border: 0px !important;
	border-radius: 0px !important;
}
.message-group {border-bottom-color: rgba(255,255,255,.04) !important;}
.message-group:hover {background-color: #282828 !important;}
.message-group .comment .edited {
  font-size: 0;
  width: 13px!important;
  height: 13px!important;
  display: inline-block;
  background-image: url(https://imgh.us/.message-group_.comment_.edited.svg);
  background-size: 100%;
  opacity: .3;
  line-height: 23px
}

.confirm-message-modal .form-inner .message-group {
	background-color: #181818 !important; 
	border-radius: 3px !important; 
	padding-bottom: 18px !important;
}
.confirm-message-modal .form-inner .message-group .comment .markup,
.confirm-message-modal .form-inner .message-group h2 strong {color: #D2D2D2 !important}

.message-group .comment .attachment .upload > .progress .progress-bar {background-color: #1DB954 !important;}
.message-group .comment .attachment .upload > .filename,
.message-group .comment .attachment a {color: #1DB954 !important;}

/*---/CHAT---*/

/*---FRIENDS---*/

.friends-header {background: #282828 !important;}

.friends-table {
	background: #181818 !important;
	margin-top:0 !important;
}

.friends-header,
.friends-table .friends-table-header {
	border-bottom: 1px solid hsla(0,0%,100%,.1) !important;
}

.friends-table .friends-row:hover {background: #282828 !important;}

.friends-table .friends-row .friends-column-guilds .avatar-small {background-color: #18AC4C !important}

/*---/FRIENDS---*/

/*---MEMBER LIST---*/

.channel-members-loading {display: none;}
.channel-members {background-color: #181818 !important;}
.channel-members .member:hover {background: #181818 !important;}
.channel-members .member.popout-open {background: #282828 !important;}

.channel-members .member .avatar-small {
    width: 40px;
    height: 40px;
    background-size: 40px 40px;
    z-index: 9;
}

.channel-members .member .avatar-small:before {
    z-index: 11;
    content: "";
    width: 38px;
    height: 38px;
    position: absolute;
    top: 0;
    left: 0;
    opacity: 1;
    border-radius: 50%;
    border: 1px solid transparent;
}

.channel-members .member .avatar-small:after {
    z-index: 10;
    content: "";
    width: 40px;
    height: 40px;
    position: absolute;
    top: 0;
    left: 0;
    background: #000;
    opacity: 0;
	border-radius: 50%;
}
.channel-members .avatar-small .status {z-index: 12 !important;}
.channel-members .member .avatar-small:hover:after {opacity: .9 !important;}
.channel-members .member .avatar-small:hover:before {border-color: white !important;}
.channel-members .member .avatar-small:hover {transform: scale(1.08);}
.channel-members .member .avatar-small:active {transform: scale(1) !important;}
.channel-members .member:hover > .avatar-small:before {border-color: grey;}
.channel-members .member:hover > .avatar-small:after {opacity: .9;}

.user-popout .header, #user-profile-modal .header {background: #1C1C1C !important;}

.popout header {background: #141414 !important;}
.popout.popout-bottom header:before, .popout.popout-bottom-right header:before {border-bottom-color: #121314 !important;}
.popout.popout-invert.popout-top header:before, .popout.popout-invert.popout-top-right header:before {border-top-color: #121314 !important;}
.popout section {background-color: #242424 !important;}
.instant-invite-popout {box-shadow: 0px 0px 7px #000;}
#autocomplete-popout .scroller {background-color: #292a2b !important;}
#autocomplete-popout .row.selected, #autocomplete-popout .row:hover {background: #272828 !important;}

.user-popout .avatar-wrapper .avatar-popout, #user-profile-modal .avatar-profile {
	border: 4px solid #181818 !important;
	background-color: #141414 !important;
}

#user-profile-modal .avatar-profile .status {border: 4px solid #181818 !important;}

.user-popout .avatar-wrapper .activity {margin-bottom: 3px;}

.user-popout .body, .user-popout .footer {
	border-color: #1C1C1C;
	background: #282828;
}

.member-roles .member-role {
	background-color: #181818;
	color: #FFF;
	border: 0px;
}

/*---/MEMBER LIST---*/

/*---SETTINGS---*/

.streamer-mode-enabled {background: #181818 !important;}

.settings-connections-wrapper .authed-app, .webhooks .webhook {
	background: #282828 !important;
	border: 0px !important;
}

#webhook-name {height: 42px !important; margin-top: 11px !important;}
#webhook-name:hover {background-color: #444444 !important;}
#webhook-name:focus {border-bottom: 0px !important;}

.settings-connections-wrapper .authed-app *, .webhooks .webhook * {
	color: #D2D2D2 !important;
}

.guild-settings-modal-emoji .guild-settings-modal-emoji-header p,
.guild-settings-modal-emoji .no-emoji p {color: #D2D2D2 !important;}

.markdown-modal,
.region-select-modal,
.region-select-modal .region-select-modal-header {
	color: #FFF;
	background-color: #181818 !important;
}

.region-select-modal .region-select-modal-option {
	background-color: #1C1C1C !important;
	border: 2px solid #1C1C1C;
}
.region-select-modal .region-select-modal-option:hover {border-color: #1DB954 !important;}

.avatar-uploader .avatar-uploader-inner {background-color: white !important;}

.user-settings-modal .voice-settings .reset-voice-settings {color: #1ED760 !important;}
.user-settings-modal .voice-settings .reset-voice-settings:hover {color: #1DB954 !important;}

.game-input {color: #FFF;}
.hover-roll {color: #cccccc;}
.games-row {border-top: 0px;}

.radio .radio-inner span:after {
	background: #1ED760 !important;
	border: 0px !important;
}

.now-playing {background: #1DB954 !important;}
.now-playing.no-detection {background: #282828 !important;}
.now-playing:not(.no-detection) {animation: none !important;}

.btn-primary {
	border-radius: 30px !important;
	background-color: #1db954 !important;
	border: 0px !important;
	transition: none !important;
	max-height: 40px !important;
}

.btn.red {
	border-radius: 30px !important;
	background-color: #f04747 !important;
	border: 0px !important;
	transition: none !important;
	max-height: 40px !important;
