.checkbox .checkbox-inner.alt input[type=checkbox]+span {border-color: #404040 !important;}

.checkbox .checkbox-inner input[type=checkbox]:checked + span:after { 
    background: white; 
    border: none; 
    transform: translate(11px, 0px);
}

.checkbox .checkbox-inner input[type=checkbox]:checked + span { 
    background: #ADB8E1; 
    border: 2px solid #1ED760;
    height: 22px; 
    width: 39px; 
    border-radius: 100px;
}

