css:
@-moz-document domain("www.fantasyplanet.cz") {
.site-content {
    /* background: url(http://fantasyplanet.jiri-sika.cz/wp-content//themes/merlin-fantasyplanet/images/bg-90transparent.png) 0 0 repeat; */
    
    background: #EDD59D;
}
.primary-navigation {
    background: #B6895A;
    -webkit-border-top-left-radius: 8px;
    -webkit-border-top-right-radius: 8px;
    -moz-border-radius-topleft: 8px;
    -moz-border-radius-topright: 8px;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
}
.widget-header {
    background-color: #785E42;-webkit-border-top-left-radius: 5px;
-webkit-border-top-right-radius: 5px;
-moz-border-radius-topleft: 5px;
-moz-border-radius-topright: 5px;
border-top-left-radius: 5px;
border-top-right-radius: 5px;
}
a:link,
a:visited {
    color: #5E4224;
}
a {
    color: #5C4832;
    text-decoration: none;
}

.widget-title {
    display: inline-block;
    -webkit-border-top-left-radius: 5px;
    -webkit-border-top-right-radius: 5px;
    -moz-border-radius-topleft: 5px;
    -moz-border-radius-topright: 5px;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
   
}

.more-link:link, .more-link:visited {
    color: #785E42;
    background: #FFEDC2;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 5px;
}

body.custom-background {
    background: url('http://inkscapers.cz/Pozadi.jpg')  0 0;
}
.comment {
    padding: 0.8em 0;
    border-top: 0px solid #ccc;
    -ms-word-wrap: break-word;
    word-wrap: break-word;
}

.comment .comment-meta {
    float: left;
    width: 100%;
    padding: 0;
    border-bottom: 0px solid #eee;
}

.site-main form {
    background: #D7C18E;
    padding: 1em !important;
}

.widget.clearfix {
    background: #FFEDC2 none repeat scroll 0 0;
    padding-bottom: 10px;
}

.comment footer {
    background-color: #D7C18E;
}

ul.wb-books-list li {
    background: #FFEDC2 none repeat scroll 0 0;
    float: left;
    font-size: 1em;
    height: 290px;
    list-style: outside none none;
    margin: 5px 0.5%;
    padding: 1%;
    text-align: center;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 5px;
    width: 19%;
}

#post-slider-container {display:none!important;}

.short-messages-container {
    width: 26%;
    max-width: 380px;
    float: right;
    background-color: #FFEDC2;
    height: auto;
    margin: 0 0 1em 0;
}

#logo img {display:none}
#logo a{
    margin:1em;
    background: url('http://inkscapers.cz/logo.png') no-repeat 0 0;
    width: 315px;
    display: block;
    height: 122px;}
}
