    box-shadow: 0 1px 2px rgba(0, 0, 0, .14);
}
.top_home_link {
    position: relative;
    overflow: hidden;
}
.top_home_link:after {
    z-index: 0;
    position: absolute;
    top: calc(50% - 5px);
    left: calc(50% - 5px);
    content: '';
    border-radius: 50%;
    background: rgba(0, 0, 0, .5);
    opacity: 1;
}
.top_home_link .top_home_logo {
    margin: 13px auto 0;
}
/* Для userstyles */

.top_home_link:hover:after {
    width: 0;
    height: 0;
    animation-name: logo;
    animation-duration: .5s;
    animation-timing-function: ease-in-out;
    animation-fill-mode: both;
    transition-duration: .2s;
}
/* Для userstyles */

.top_home_link:active:after {
    transform: scale(17) !important;
}
@-moz-keyframes logo {
    0% {
        transform: scale(0);
        opacity: 1;
    }
    100% {
        width: 10px;
        height: 10px;
        border-radius: 50%;
        transform: scale(20);
        opacity: 0;
    }
}
@-moz-keyframes logocircle {
    0% {
        transform: scale(0);
        opacity: 1;
    }
    100% {
        width: 10px;
        height: 10px;
        border-radius: 50%;
        transform: scale(10);
        opacity: .2;
    }
}
@-webkit-keyframes logocircle {
    0% {
        transform: scale(0);
        opacity: 1;
    }
    100% {
        width: 10px;
        height: 10px;
        border-radius: 50%;
        transform: scale(10);
        opacity: .2;
    }
}
@-webkit-keyframes logo {
    0% {
        transform: scale(0);
        opacity: 1;
    }
    100% {
        width: 10px;
        height: 10px;
        border-radius: 50%;
        transform: scale(20);
        opacity: 0;
    }
}
