}*/
/* actual scrubber button */
.html5-scrubber-button, .ytp-scrubber-button {
   background: #383A3B !important;
   border-color: #1B1B1B !important;
   box-shadow: 0px 0px 3px #00FF00, 0px 0px 5px #00FF00, 0px 0px 0px #00FF00 !important;
   transition: box-shadow 0.2s, background 0.2s !important;
}
/* scrubber button on hover */
.html5-scrubber-button:hover, .ytp-scrubber-button:hover {
   background: #00FF00 !important;
   border-color: #1B1B1B !important;
   box-shadow: 0px 0px 6px #00FF00, 0px 0px 8px #00FF00, 0px 0px 10px #00FF00 !important;
}
/* volume control */
.ytp-volume-slider-track, .ytp-volume-slider-handle:before {
   background: #00FF00 !important; /* Old browsers */
   background: -moz-linear-gradient(left, #1b1b1b 0%, #00FF00 100%) !important;
