css:
@-moz-document domain("zeldauniverse.net") {
a {color:#eee;}

/*background for site*/
body {background:#07351b; color:#ddd;}
#main div > div > .content{background:#22493b;}

/*navigation colours*/
.navigationHeader, .navigationFooter {background:#0e4132!important; border-color:#0e4132; border-top-color:#07351b!important;}
#mainMenu > ul {background:#052816;}
#mainMenu > ul > li.active a {background:#052816!important; border-color:#07351b!important;}
#mainMenu, #mainMenu > ul > li a{border-color:#07351b!important; }
ul.navigationMenuItems > li > a {color:#bed6cd!important;}
.mainMenu > ul > li.active > a, .mainMenu > ul > li.active > a:hover {color:#bed6cd;}
.button.active {color:#052816; background:#1b5540!important;}

/*index page*/
.tabularBox, .container {background:#1b5540; border-color:#0e4132;}
.wbbBoardList .wbbBoardNode2, .containerList > li:nth-child(even) {background:#1b5540;}
.sidebar, .sidebar .collapsibleButton {background:#0e4132!important;}
.sidebar > div > fieldset:not(:last-child), .sidebar > div > div:not(:last-child) {background:#0e4132!important; border-color:#1b5540;}
.sidebar .sidebarBoxHeadline > h3 {color:#bed6cd;}
.wbbBoardList .wbbBoardNode1 {background:#0e4132;}
.wbbBoardList .wbbBoard:hover, .containerList > li:hover {
    background: none;}
.framed > canvas, .framed > img {background:#1b5540; border-color:#052816;} .framed > .icon {border:none; background:none;}
.wbbBoardList .icon, .navigation > ul.navigationItems .icon, .navigation > ul.navigationMenuItems .icon, .navigation > ul.navigationIcons .icon {color:#bed6cd;} .badgeUpdate {background:#e5d096!important;}

/*menu*/
#topMenu {background:#052816!important;}
#topMenu .userPanelItems li a:hover, #topMenu .searchBar {background:#05311c!important;}

.interactiveDropdown {
    background-color: #0e4132!important;
    border: 1px solid #22493b!important;
    color: inherit!important;}
.interactiveDropdown > .interactiveDropdownItemsContainer {
    border-color: #22493b!important;
}

li.notificationItem.interactiveDropdownItemOutstanding.interactiveDropdownItemOutstandingIcon.interactiveDropdownItemShadow {
    background: #124e3c!important;
}

.interactiveDropdown > .interactiveDropdownItemsContainer > .interactiveDropdownItems > li:not(.loading):not(.noItems).interactiveDropdownItemOutstanding {background-image:none}

.interactiveDropdown .interactiveDropdownItems li, li.notificationItem.interactiveDropdownItemOutstandingIcon.interactiveDropdownItemShadow {
    background: #0e4132!important;
}

.interactiveDropdown > .interactiveDropdownItemsContainer > .interactiveDropdownItems > li:not(.loading):not(.noItems):not(:last-child) {
    border-bottom: 1px solid #1b5540;
}

.interactiveDropdown .pointer span {
    border-bottom-color: #0e4132 !important;
}

.interactiveDropdown > .pointer {
    border-bottom-color: #22493b;}


/*threadposts*/
.dialogTitlebar {background:#1b5540;}
.dialogContent {background:#22493b;}

.popover > .popoverContent {background:#1b5540; color:#ddd;}
.messageBody {color:#ddd;} .messageBody a {color:#FFFCB9;}
.message .messageContent {background:#1b5540!important;}
.messageSidebarOrientationLeft .messageHeader::before, .messageSidebarOrientationLeft .messageHeader::after {
    border-color: transparent #1b5540 transparent transparent !important;}
.message {background:#0e4132!important; border-color:#0e4132!important;}
.message.messageSidebarOrientationLeft .messageContent {border:none;}
.messageSignature {
    background: rgba(5,49,28,0.25);
    padding: 10px!important;
border-radius: 3px;}
.messageText {color:inherit!important}

.messageHeader + .messageBody > div:first-child, .messageBody > div:not(.messageFooter):not(:first-child), .message .messageSignature  {
    border-top: none!important;}
.button.buttonPrimary, input[type='submit']:not([disabled]), input[type='button']:not([disabled]).buttonPrimary, button:not([disabled]).buttonPrimary, .button, .buttonGroupNavigation .button, .button, input[type='reset']:not([disabled]), input[type='submit']:not([disabled]), input[type='button']:not([disabled]), button:not([disabled]), .messageText .button , .tabMenuContainer > .menu ul li a, .ui-tabs-anchor {border:none; background:#05311c!important; color:#bed6cd;}


.button:hover, input[type='reset']:not([disabled]):hover, input[type='submit']:not([disabled]):hover, input[type='button']:not([disabled]):hover, button:not([disabled]):hover, .buttonGroupNavigation .button:hover, .button.buttonPrimary:hover, input[type='submit']:not([disabled]):hover, input[type='button']:not([disabled]).buttonPrimary:hover, button:not([disabled]).buttonPrimary:hover, .messageText .button:hover {
    background-color: #0e4132;}

.button.buttonPrimary:hover .icon, input[type='submit']:not([disabled]):hover .icon, input[type='button']:not([disabled]).buttonPrimary:hover .icon, button:not([disabled]).buttonPrimary:hover .icon, .button.buttonPrimary .icon, input[type='submit']:not([disabled]) .icon, input[type='button']:not([disabled]).buttonPrimary .icon, button:not([disabled]).buttonPrimary .icon {color:#bed6cd}


.container.containerPadding.spoilerBox {
    background: transparent;
    border: transparent;
}
    .messageFooterNote { background:none;border:none;}

.messageText blockquote {background:rgba(5,49,28,0.25)!important;}
.quoteBox header {border:none;}
.quoteBox::before {
    font-size: 25px;
    left: 20px;
    top: 5px;}
.message.dividers .userCredits { border:none;}
.message .newMessageBadge {background:#1b5540; color:#bbb;}

/* thread index */
.table > tbody > tr > td, .cke_wysiwyg_div table > tbody > tr > td {background: #1b5540!important;}
.table > tbody > tr:nth-child(even) > td, .cke_wysiwyg_div table > tbody > tr:nth-child(even) > td {background:#0e4132!important;}


/*profile*/
.tabMenuContent{border-color:#1b5540;}
.commentList > li:not(.commentAdd), .commentList > li:not(.commentAdd):hover:nth-child(2n) {background:#0e4132;}
    .commentList > li:not(.commentAdd):hover {background:#0e4132;}
.commentList > li:nth-child(2n+1) .commentResponseList .commentResponse:nth-child(2n+1) , .commentList > li:nth-child(2n) .commentResponseList .commentResponse:nth-child(2n+1), input:not([type='radio']):not([type='checkbox']), textarea, button {background:#05311c!important;border-color:#1b5540;}
.commentResponse, .commentOptionContainer {border-color:#1b5540!important;}
#main > div {border-right:1px solid #22493b}
.tabMenu > ul > li.ui-state-active > a, .tabMenuContainer > .menu {background:#0e4132!important; border-color:#1b5540;border-bottom:none;}
.tabMenu > ul {background:#05311c;border:none;}
.tabMenuContent {background:#0e4132!important}
fieldset > legend {border-color:#05311c;}
.commentResponseList > li:hover {background:none!important;}
td a {color:#ddd!important;}
.sidebar fieldset > nav ul > li.active {background:#1b5540}
}
