css:
@-moz-document domain("www.hello-online.org") {
html, body {
    background-color: #151515!important;
}

#idol_header_bottom, #content{
    background: #333!important;
}

#content {
    color: #D0D0D0;
}

.text1 {
    color: burlywood;
}

.text4 {
    background: inherit;
    color: #98C060;
}
.ntext2 {
    background: inherit;
    color: #888;
}

.message.error {
	background-color: #f3e3e6!important;
    border-color: #e599aa!important;
    color: #80001c!important;
}


/* forms */
input, textarea, select {
	background-color: #111!important;
    border-color: black!important;
    color: silver;
}

input[type='submit'] {
	background: #111!important;
    border-color: #111!important;
    border-style: solid!important;
    color: #999!important;
    box-shadow: none!important;
    text-decoration: none;
}
input[type='submit']:hover {
	background: #4A981F!important;
    border-color: #4A981F!important;
    border-style: solid;
    color: #333!important;
}

hr {
    border-color: #444!important;
}

/* TOP nav bar */
#header_bar, #header_bar_bg  {
	background: #151515;   
}

#user_navigation a {
    color: #AAA;
}

#user_link:hover, 
#notify_link:hover, 
#inbox_link:hover{
    background-color: #28411B;
}

#user_link.menu_active, 
#notify_link.menu_active, 
#inbox_link.menu_active{
	background-color: #4A981F;
}

.HeaderMenu {
	background: #252525;
}

.ipsType_sectiontitle {
	color: #98C060;
    border-bottom-color: #444;
}
.ipsType_sectiontitle a {
	color: #580;
}
.ipsType_sectiontitle a:hover {
	color: #DD8800;
}
#user_link_menucontent #links li {
    text-shadow: none;
}

/* login form pop-up*/
.popupWrapper {
    background-color: #91C163;
	box-shadow: none;
}
.popupInner {
    background-color: #252525;
    box-shadow: none;
}
.popupInner h3 {
	background: #91C163!important;
    border-color: #91C163!important;
	box-shadow: none!important;
    text-shadow: none;
    color: #444;
    font-weight: bold;
}
.popupInner a {
    color: #4A981F;
}
.ipsBox_notice, .ipsForm_submit {
    background: #3C3C3C;
    border-color: #444;
}
.ipsButton_secondary {
    background: #9A8!important;
    border-color: #9A8!important;
    color: #444!important;
    box-shadow: none;
}

.ipsType_pagetitle {
	color: #D0D0D0;
}

.entry_content.ipsPad {
	background: #292929!important;
    color: #AAA;
}

/* darken banners */
#branding_bg, #idol_header {
    filter: brightness(66%);
    -webkit-filter: brightness(66%);
    -moz-filter: brightness(66%);
	position: relative;
}

#branding_bg {
	z-index: 10;   
}

/* global search bar */
#search_wrap {
    background: #111;
    box-shadow: none;
}

#search_options, #search_options_menucontent {
    background: #4F4F4F;
    border-color: #4F4F4F;
    color: white;
    box-shadow: none;
}

#search_options_menucontent .title {
    color: #98C060;
}
#search .submit_input {	
    background: #333 url(http://www.hello-online.org/public/style_images/green/search_icon.png) no-repeat 50%!important;
    border-color: #333!important;
}
#search .submit_input:hover {
	background: #4A981F url(http://www.hello-online.org/public/style_images/green/search_icon.png) no-repeat 50%!important;
    border-color: #4A981F!important;    
    color: black;
}

/* search results */
#main_search_form .search_app.active {
	background: #4A981F;
    border-color: #2A7800;
}
#main_search_form .search_msg {
   border-color: #444;
}
body#ipboard_body fieldset.submit, body#ipboard_body p.submit  {
    background: #3C3C3C!important;
    border-color: #444!important;
}

/* create account */

.ipsSteps {
	border-bottom: 0px;
}

.ipsBox {
	background: #292929!important;
}

.ipsBox_container {
    background: #292929!important;
    border-color: #292929!important;
}

/* main content */

#maincontentbox, .rightSideBlock {
    background: #292929!important;
    box-shadow: none;
    border-color: #292929;
}

#maindiv > table > tbody > tr:nth-of-type(1) > td:nth-of-type(2) > span:nth-of-type(1) {
	color: #98C060!important;
}

#maindiv table > tbody > tr > td[colspan='2']:not(.text3) > span:nth-of-type(1):not(.text3) {
	color: #98C060!important;
}
#maindiv > table > tbody > tr > td[colspan='2'] > span:nth-of-type(1) {
	color: slategray;
}    

/* right block */

.producthook_border {
	border-color: #444;   
}

.prodtopic {
    color: #98C060;
}

.producthook_title a {
	color: silver;   
}

.producthook_artist a {
    color: burlywood;
}

.producthook_comments a {
	color: #888!important;
}
.status_mini_wrap {
	background: #666;
    border-bottom: 0px;
}


/* footer */
#footer_utilities {
	background-color: #151515;
}
#footer_utilities a {
	color: #4F4F4F;
    text-shadow: none;
}


/*****************
	NEWS FEED
******************/

/* left box header*/
#categorytree .tl, #categorytree .tr {
    background: #28411B;
}
/* left box content*/
#categorytree .ml, #categorytree .ml * {
    background: #222222;
}
.newslist a,
.smalltext a,
#main_column .entry_footer .has_more a {
    color: #6F6F6F!important;   
}
.newslist a:hover,
.smalltext a:hover,
#main_column .entry_footer p a:hover  {
    color: #BBB!important;   
}


/* inter-background*/
.bg3 {
	background: #292929;
}
/* left box footer*/
.box-big .box-a .br {
	display: none;   
}

#pollbox #branding, #pollbox #content {
    background-color: #9A8!important;
}

/*poll images */
img[src='/hello/images/left_arrow.gif'] {
	display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url('data:image/gif;base64,R0lGODlhCgAKAKECAJSl/+///////////yH5BAEKAAEALAAAAAAKAAoAAAITjH+ACYoNGWxh2jsNdtquGn1gAQA7') no-repeat!important;
    width: 10px;
    height: 10px;
    padding-left: 10px;
}

img[src='/hello/images/right_arrow.gif'] {
	display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url('data:image/gif;base64,R0lGODlhCgAKAKECAJSl/+///////////yH5BAEKAAEALAAAAAAKAAoAAAISBIKJZrrcEoRvWhuupIq59SUFADs=') no-repeat!important;
    width: 10px;
    height: 10px;
    padding-left: 10px;
}

div.bbc_spoiler_wrapper {
    border: 2px solid #777;
    background: #131513;
    color: #888;
}

/* comments */
img[src='/hello/images/index25.jpg'], 
img[src='/hello/images/dotted_vert.gif'], 
img[src='/hello/images/frame/homepage13.gif']  {
    filter: brightness(50%);
    -webkit-filter: brightness(50%);
    -moz-filter: brightness(50%);
}

img[src='http://www.hello-online.org/public/style_images/green/icon_users.png'] {
    filter: brightness(150%);
    -webkit-filter: brightness(150%);
    -moz-filter: brightness(150%);
}

/* add content - help */
tr > td.align-justify > span:first-child {
	color: #98C060!important;
}


/*****************
	 PIC BOARD
******************/

#main_column .entry_footer .has_more a {
 	color: #D0D0D0;   
}

#main_column .entry_footer_end {
	background: #444;
}

/* comment */
#maindiv table:nth-of-type(3) table table:nth-of-type(3) tr:nth-of-type(4n) td div {
    background: #444!important;
}
#maindiv table:nth-of-type(3) table table:nth-of-type(3) tr:nth-of-type(4n):nth-last-of-type(2) td div {
    display: none;
}

/*****************
	TRACKER
******************/
.tb2 > tbody > tr:nth-of-type(even) > td {
    background: #202020;
}
.tb2 > tbody > tr:nth-of-type(odd) > td {
    background: #252525;
}
.tb2 > tbody > tr:hover:not(:first-of-type) > td:not([colspan='10']) {
	background: #303C25;
}
.tb2 > tbody > tr:first-of-type > td {
	background: #292929;
}
#torrtable .tb2 > tbody > tr:last-of-type > td {
	background: #292929;
}

a.tracker:visited {
	color: #11555F
}

/* Tracker Images */
img[src='/hello/images/button_hp_showall2.gif'],
img[src='/hello/images/button_hp_new2.png'],
img[src='/hello/images/button_akb_new2.png'],
img[src='/hello/images/button_hp_others.png']
{
	-webkit-filter: brightness(66%);
}

img[src='/hello/images/button_hp_showall_off2.gif'],
img[src='/hello/images/button_hp_new_off2.png'],
img[src='/hello/images/button_akb_new_off2.png'],
img[src='/hello/images/button_hp_others_off.png']
{
	-webkit-filter: brightness(35%);
}

/*Pagination*/
#torrtable .tb2 > tbody > tr:last-of-type > td,
#maindiv > table:nth-child(6),
#maindiv > div > table > tbody > tr:nth-child(4) > td > table > tbody > tr:last-child {
	color: #566;
}


/********************
	BLOG
********************/
.ipsPad a {
	color: #888;
}

/* comments*/
.ipsComment {
    border-color: #444;
    border-bottom: 0px;
    background: #2C2C2C;
    box-shadow: none;
}
.ipsComment_author span[itemprop='name'] {
    color: #A87;
}
.ipsComment_controls a {
    color: skyblue;
}
.ipsButton_secondary {
    color: #4c4c4c;
    background: #d0d0d0;
    box-shadow: none;
}

#fast_reply {
	background: #444;
    border-color: #444;
}

.entry_header, #main_blog_wrapper .entry_header {
	background: #4F5F4C!important;
    border-color: #4F5F4C!important;
}
.ipsType_pagetitle a, .ipsType_pagetitle {
	color: #98C060;
}
.entry_header .desc a {
    color: #D0D0D0;
}

/********************
	FORUM
********************/
#board_statistics {
    border-top-color: #252525;
    background: #252525;
}
#stat_links a {
	color: #580;
}

.statistics_head {
    color: silver;
}
.statistics_brief a {
	color: #444;
}
#board_statistics span[itemprop='name'], 
.active_users span[itemprop='name'] {
    color: silver;
}

.topic_buttons li a, .topic_buttons li span, .ipsButton {
    background: #296FA3;
    border-color: #296FA3;
    color: #222;
    box-shadow: none;
}
.topic_buttons li a:hover, .ipsButton:hover {
	background: #5EB1EE;
    color: #222;
}

/* forum rules */
.desc, .desc.blend_links a, p.posted_info {
	color: #A6A6A6;   
}

/* new content */
.ipsSideMenu h4 {
	color: antiquewhite;
}
.ipsSideMenu ul li{
	border-color: #555; 
}
.ipsSideMenu li a{
	color: #D0D0D0; 
}
.ipsSideMenu ul li.active a {
	background: #F0B2D9!important;
    color: #252525;
}


/* most popular of the month annoying ads */
#maindiv > table:first-of-type > tbody > tr:nth-of-type(7) > td:nth-of-type(2) > table > tbody > tr > td:nth-of-type(2){
    display: none;
}

/* occupy gained space with search*/
#maindiv > table:first-of-type > tbody > tr:nth-of-type(7) > td:nth-of-type(2) > table > tbody > tr > td:nth-of-type(1) form {
    display: table;
    margin: auto;
}

#maindiv > table:first-of-type > tbody > tr:nth-of-type(7) > td:nth-of-type(2) > table > tbody > tr > td:nth-of-type(1) table tr:nth-last-child(2) {
    text-align: center;
}

/* try to center control panel */

/* product hook (i.e cdJapan YesAsia product bar) */
.rightSideBlock {
	display: none;
}


}
