css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document  domain("alamo.edu") {
   
    /****************** Font colors *********************/
    
    span, pre, h1, h2, h3, h4, h5, h6, .list-view > ul > li > a, .list-view > nav > ul > li > a, .accessible-toggler,  ag-list div,
    .ig-header, .ig-footer .ig-content .item-group-container {
        color: #bbb!important;
    }
    .dockbar a {
    color: #aaa;
    font-weight: bold;
    text-shadow: none;
}

      
    select,
    textarea,
    input[type="text"],
    input[type="password"],
    input[type="datetime"],
    input[type="datetime-local"],
    input[type="date"],
    input[type="month"],
    input[type="time"],
    input[type="week"],
    input[type="number"],
    input[type="email"],
    input[type="url"],
    input[type="search"],
    input[type="tel"],
    input[type="color"],
    .uneditable-input {
        color: #42B0FE!important;
    }

   
    
  /****************** Background Images *********************/
    
    html,
    body,
    #inbox,
    #breadcrumbs,
    #tinymce,
    .toolbarView,
    .discussion_entry,
    .discussion-entry-reply-area,
    .conversations,
    #create_message_form,
    #create_message_form form,
    .scrollable-list,
    .section,
    .ui-tabs .ui-tabs-panel{
        color: #C7D7C0!important;
        background: #111 url("http://farm3.staticflickr.com/2819/8990145169_02b831c707_o.png") !important;
        border-color: #333!important;
    }
    
    #cas #header #app-name {
    background-color: transparent;
    }
    
    .accd-title {
    background-color: rgb(56,46,64);
    color: white;
    height: 20px;
    line-height: 20px;
    padding: 10px;
    border-radius: 5px;
    font-size: 16px;
    }      
    .portlet-title {
    float: none;
    font-weight: bold;
    min-width: 50%;
    padding: 5px;
    text-shadow: 1px 1px transparent;
    }
                      
     ag-list div,
    .ig-header,
    .context_module 
    .ig-footer .ig-content .item-group-container {
        background-color: #111!important;
    }               
          
    /****************** Background & Border Colors *********************/ 
    
    .portlet {
       background-color: transparent;
       border: 1px solid #444;
    }
    
    .portlet-topper {
       background-image: none!important;
       background: transparent;
       border-color: #444;
       -webkit-box-shadow: 1px 1px 0 #333;
       box-shadow: 1px 1px 0 #333;
    }
    
    .dockbar {
       background-image: none!important;
       background: #222!important;
       border-bottom: 1px solid #444;
   }
    
    .site-breadcrumbs ul {
    background-color: transparent;
    border: 1px solid #444;
    border-color: #444;
    margin: 0;
    }
    
    .accordion-content {
    color: #bbb;
    font-size: 11px;
    border-left: 1px solid #333;
    border-right: 1px solid #333;
    border-bottom: 1px solid #333;
    background-color: transparent;
}
    table.tablesorter tbody td {
    color: #bbb;
    padding: 5px 8px;
    background-color: transparent;
    vertical-align: middle;
    border-bottom: 1px solid #333;
       
}
    
    table.tablesorter thead tr th, table.tablesorter tfoot tr th {
    background-color: #222;
    font-size: 11px;
    font-weight: bold;
    color: #bbb;
    height: 25px;
}
    .accordion-header-left, .accordion-header-middle, .accordion-header-right {
    height: 25px;
    width: 100%;
    background-image: none;
        background: #222!important;
          border-bottom: 1px solid #444;
    }
    input[type="password"], input[type="file"], input[type="submit"], input[type="button"], input[type="reset"], select, textarea, .textarea, button, .aui-field-input-text {
    background-image: none;
        background-color: transparent;
    background-repeat: no-repeat;
    border: 1px solid;
    border-color: #333 #444 #333 #444;
    font: 1em Arial, Helvetica, Verdana, sans-serif;
    padding: 5px 1px;
}

    
    #cas #content #login #fm1 .row input[type="text"], #cas #content #login #fm1 .row input[type="password"] {
    border: 1px solid #222!important;
    }
    
    #cas .flc-screenNavigator-view-container #header, #cas .flc-screenNavigator-view-container #content {
    background: transparent;
     background: #111 url("http://farm3.staticflickr.com/2819/8990145169_02b831c707_o.png") !important;
    }
    
    #cas #content #login #fm1 .row input[type="text"], #cas #content #login #fm1 .row input[type="password"] {
    border: 1px solid #333;
    background: transparent;
    }
    
    #cas #content #login #fm1 .row.check {
    border-bottom: 1px solid #222!important;
    color: #777;
    }
    
    #cas #content #login #fm1, .accd-box2, .accd-box {
    background: transparent;
    }
}
