css:
@-moz-document domain("workflowy.com") {
/*=============================================
        [WorkFlowy Theme] - Cardy
=============================================*/
/*

  Cardy
		Cardy is the style of WorkFlowy.
        It's like an information card.
        Memorize the world of things in card style.

  Panel, Card, Dock.
        Panel is the item of one sentence.
        Panels are gathered as a card.
        Cards are stacked in one dock.
        Dock is the thinking way of myself.

  by wineroses. (http://d.hatena.ne.jp/wineroses/)
  
*/
  
  #header{background:black;border:0;}
  .saveButton,.showCompletedButton,.menu-button{background:#000;}
  #documentView{background:cadetblue;}
  .page{color:black;background:white;line-height:1.4em;border-radius:5px;}
  .selected{border-top:none;}
  .parent>.name>.content{color:white;background:orange;margin:2px;padding:2px;border-radius:3px;}
  .parent>.name>.parentArrow{display:none !important;}
  .project.selected>.name .content{font-size:large;font-weight:bold;line-height:1.4em;}
  .project.selected>.children>.project{color:black;word-break:break-all;background:white;padding:5px;margin:-10px;margin-bottom:20px;border-radius:5px;margin-left:0px;border:1px gray solid;}
  .project.selected>.children>div>.name .content{font-weight:bold;padding-bottom:5px;}
  .contentTag{color:darkorange !important;}
  .contentLink{color:darkblue !important;}
  .contentUnderline{border-bottom:2px aqua solid;text-decoration:none;}
  .contentBold{border-bottom:2px pink solid;}
  .contentItalic{background:lime;}
  .notes>.content{font-size:medium !important;color:darkblue !important;line-height:1.4em !important;}
  .bullet{background-image:none;}
  .project.open>.name>.bullet{border:1px orange solid;border-radius:3px;}
  .bullet,#bulletBucket .bulletBucketBullet{background:orange;border-radius:3px;}
  .children{position:relative;margin:0px;border:0px;padding: 0px;}
  .selected>.children>.project .project{margin:0px;margin-bottom:3px;border-top:1px solid #ddd;}
  .project.highlighted::before,.project.moving::before,.project.tapped::before,.draggableNameClone::before {background:none !important;}
  #moveDropLine{background:orange;}
  
}
