css:
@-moz-document domain("inbox.google.com") {
/* Hope you like the style! If you for some reason "out of the blue", want to contact me, you can find me on luvis.se
Have a nice day! */

/* section header "day -title" */

.ai-b8-bQ {
    background: transparent;
    margin-right: 48px;
    -webkit-flex: 1;
}
.section-header {
    color: #EAEAEA;
}
/* side bar */

.lQ {
    background: #4A7FBE;
    bottom: 0;
    color: #ECEAEA;
    line-height: 40px;
    overflow-x: hidden;
    overflow-y: hidden;
    padding: 12px 8px 52px;
    position: absolute;
    white-space: nowrap;
    width: 216px;
    top: 0;
}
/* main settings */

body {
    font-family: 'arial', sans-serif;
    font-size: 83%;
    margin: 0;
    min-width: 400px;
    overflow-x: hidden;
    overflow-y: scroll;
    background: #5A9BE8;
    color: #EAEAEA;
}

.I {
    background-color: #5A9BE8;
}

/* top bar */

.b4, .b4 .at {
    background: #5A9BE8!important;
}
.hn:before {
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.14), 0 -1px 4px rgba(0, 0, 0, 0.28);
}
.b4 .at::before {
    box-shadow: none;
}
/* main email list */

.h8 {
    color: #EAEAEA;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}
.an {
    background: #4A7FBE;
    line-height: 20px;
    padding: 6px 0;
    position: relative;
}
.aa::before,
.an::before {
    bottom: 0;
    box-shadow: none;
    content: '';
    display: block;
    left: 0;
    pointer-events: none;
    position: absolute;
    right: 0px;
    top: 0;
}
.g6, .lo {
    color: #96C6FF!important;
}

.rw {
    overflow: hidden;
    white-space: nowrap;
    color: #EAEAEA;
    font-weight: lighter;
}

.ss,
.qG {
    color: #FFFFFF;
    font-weight: bold;
}
.kj {
    background: transparent;
    color: #00acc1;
}
.le {
    color: #EAEAEA;
}
.qi {
    color: #5A9BE8;
}
.bO {
    color: #5A9BE8;
}
/* active email */

.j6 {
    color: #EAEAEA;
}
.b5 {
    color: #212121;
}
.aV {
    color: #212121;
}
.az {
    color: #5A9BE8;
}
.X,
.ak,
.aa {
    background: #fff;
}
.ga.oY {
    background-color: #5A9BE8;
}
.bc {
    background-color: #5A9BE8;
    border-top: 0px;
    box-sizing: border-box;
    cursor: text;
    padding: 16px 20px;
    position: relative;
    width: 100%;
    z-index: 2;
}
.ac-P .bJ,
.ac-P-bh .bJ {
    background: #4A7FBE;
    color: #fff;
}
.bJ {
    color: #5A9BE8;
}
.dR {
    color: #5A9BE8;
}

.az {
    background-color: #4A7FBE;
}

.section-header {
    color: #fff;
}
.az .ai-b8-bQ {
    background: #5EA5E2;
    padding-left: 8px;
    padding-bottom: 3px;
    padding-top: 3px;
}
}
