css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.qwant.com") {

  body { background: #212121 !important }
  .sidebar {background-color: #212121!important;}
  .result_fragment:empty {background-image: url("http://www.qwant.com/img/loader-grey.gif");}
  .home__links__tab, #feedback__message-mobile {color: #FFF!important;}
  .wrapper-dropdown {color: #036AD1!important;}
  .result__favicon {background: radial-gradient(#212121 65%, #212121 65%) repeat scroll 0% 0%;}
  .results-page h2, .results-page h2 a { color: #999999!important;}
  .results-column--images h2 { background: #212121 !important; }
  .results-column--images {background: #212121 none repeat scroll 0% 0%;}
  .results-column--videos h2 { background: #212121 !important; }
  .result_fragment:first-of-type {background: #212121 !important; }
  .results-column--videos .result { background: #111111 !important; }
  .results-column--videos { background-color: #212121 !important; }
  .result_fragment { background-color: #212121 !important; }
  .results-column--knowledge .result_fragment * { background: #212121 !important; }
  .results-page--web .results-column--knowledge, .movie_st__info { background-color: #212121 !important;}
  .form_page {background-color: #212121!important;}
  .feedback__container { background-color: #4B4B4B!important;}
  .feedback__close {background: #4B4B4B!important;}
  .button--white {background: #404040!important;}
  .settings__form__option, .movie_st__info,.movie_st__info h1, .movie_st__info--synopsis{color: #FFF!important;}
  .wrapper-dropdown .dropdown{background-color: #111111!important;}
  .wrapper-dropdown .dropdown li a {color: #47D6E5!important;
  color: #999999!important;}
  .load_bar {background-image: linear-gradient(90deg, #2B62F0, #278DFF, #20D8FF);}
  .results-page--all .ribbon_box {background: #212121 !important;}
  .results-column--all {background: #212121 !important;}
  .ia-weather-current {background: #111!important;
    border-right: 2px solid #036AD1;}
  #ia-weather-forecast-five{background-image: none!important;
    background-color: #111;}
  .results-column--knowledge-ribbon {background: #111;}
  .knowledge-ribbon__title, .knowledge-ribbon__description, .knowledge-ribbon__url {color: #FFF!important;}
  .trends--people__person__title {color: #FFF!important;}
  .trends--people__left .icon, .trends--people__right .icon{color: #FFF!important;}
  .trends--people__carousel::before, .trends--people__carousel::after {background: #212121;}
  .trends--social__link__normal {color: rgba(0, 118, 255, 0.9);}
  .trends a {color: #FFF}
  .add-qwant__main__container {background-color: #212121!important;}
  .trends--general__trend--center .trends--general__info, .trends--general__trend--right .trends--general__info {background-color: #212121; color: #FFF;}
  .form_page h1{color: #036AD1;}
  .results-column--music-ribbon {background-color: #111; color: #FFF;}
  .footer {background-color: #111;}
  .results-column--images .result .result__link {background-color: #111;}
  .music_discography-albums-container, .artist_album_list--container {background-color: #212121;}

}

@-moz-document domain("lite.qwant.com") {
  body { background: #212121 !important;}
  .cellfooter{ background: #111111 !important;}
  p {color: #AAA;}
  .resultimgs a { background: #212121 !important;}
  .grey {background: #212121 !important;}
}

@-moz-document domain("www.qwantjunior.com"), domain("edu.qwantjunior.com") {
  body { background: #212121 !important;}
  .edu_footer {background-color: #111111!important;}
  .results-page--web.results-page--with-knowledge .results-column--knowledge {background: #212121 !important;}
  .results-column--knowledge .result_fragment * {background: #212121 !important;}
  .results-column--images h2 { background: #212121 !important; }
  .results-column--videos h2 { background: #212121 !important; }
  .results-column--images .result_fragment {background: #212121 !important;}
  .results-column--videos .result { background: #111111 !important; }
  .results-column--videos { background-color: #212121 !important; }
  .panel{background: #212121 !important;}
  .footer {background-color: #111;}
  
}

@-moz-document domain("blog.qwant.com") {
  body{ background: #212121 !important;}
  a {color: #FFF;}
  .hentry.has-post-thumbnail {background: #181818!important;}
  .entry-content {color: #FFF;}
  .entry-footer {background: #111;}
  .entry-footer a {color: #FFF;}
  .comments-area {background: #212121;
    color: #FFF;}
  .comment-author {color: #036AD1;}
  .comment-metadata a {color: #AAA;}
  .comment-list .reply a{border-color: #FFF;
    color: #FFF;}
  .comment-list .reply a:hover {border-color: #036AD1;
    color: #036AD1;}
  h1, h2, h3, h4, h5, h6, p, ul, li, span {border-bottom: #AAA 1px}
  .page-header {background: #111;
  color: #036AD1;}
  .pagination {background: #212121;
    color: #FFF;}
  .hentry {background-color: #212121;}
  
}

@-moz-document domain("boards.qwant.com") {
    body{ background: #212121 !important;}
  .application {background-image: none;}
  .sidebar {background: #212121;}
  .edu_header, .header {background: #212121;}
  .search__input::-moz-placeholder {color: #FFF;}
  * {background: #212121; color: #FFF;}
  .board {background: #212121;}
  .board--plus .board--plus--text {color: #FFF;
    text-shadow: none;}
  .board__header h3 {color: #036AD1;}
  .boards__filter {background: #212121;}
  .category_menu__list--boards {background: #212121;
  box-shadow: none;}
  .category_menu__list__item--active a {color: #036AD1;}
  .wrapper-dropdown .dropdown .dropdown__item {color: #FFF;}
  .footer {background: #111111;}
  
}
