css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("laposte.fr"), domain("laposte.net") {

  body {
    background-color:#333!important;}
  
  #header{
    background-color:#333!important; }
  
  #header-nav{
    background-color:#3D3D3D!important;
    border-top: 1px solid #3D3D3D!important;
    border-left: 1px solid #3D3D3D!important;
    border-bottom: 1px solid #3D3D3D!important;}
  
  .panel{background: none repeat scroll 0% 0% #292929}
  
  .header--sections > ul:nth-child(1) > li:nth-child(1) > div:nth-child(2){background-color: #3D3D3D!important;}
  .header--sections > ul:nth-child(1) > li:nth-child(2) > div:nth-child(2){background-color: #3D3D3D!important;}
  .header--sections > ul:nth-child(1) > li:nth-child(3) > div:nth-child(2){background-color: #3D3D3D!important;}
  .header--sections > ul:nth-child(1) > li:nth-child(4) > div:nth-child(2){background-color: #3D3D3D!important;}
  .header--sections > ul:nth-child(1) > li:nth-child(5) > div:nth-child(2){background-color: #3D3D3D!important;}
  
  .mega-layer--shop > div:nth-child(1) > div:nth-child(1) > div:nth-child(1){background-color:#000!important;}
  .header--actions > ul:nth-child(1) > li:nth-child(2) > div:nth-child(2) > div:nth-child(1){background-color:#000!important;}
  div.panel-no-overflow{background-color:#000!important;}
  
  .mediatron{background-color: #1A1A1A!important;}
  
  .btn-header__action__shop{background-color: #717171!important;}
  .btn-header__action__track{background: none repeat scroll 0% 0% #717171!important;}
  .btn-header__action__location{background-color: #717171!important;}
  
  li.active > a:nth-child(1){background-color: #333!important;}
  
  #selection-produits-et-services > ul:nth-child(1) > li:nth-child(1) > div:nth-child(1){background: none no-repeat scroll center center #717171!important;}
  #selection-produits-et-services > ul:nth-child(1) > li:nth-child(2) > div:nth-child(1){background: none no-repeat scroll center center #717171!important;}
  #selection-produits-et-services > ul:nth-child(1) > li:nth-child(3) > div:nth-child(1){background: none no-repeat scroll center center #717171!important;}
  #selection-produits-et-services > ul:nth-child(1) > li:nth-child(4) > div:nth-child(1){background: none no-repeat scroll center center #717171!important;}
  #selection-produits-et-services > ul:nth-child(1) > li:nth-child(5) > div:nth-child(1){background: none no-repeat scroll center center #717171!important;}
  #selection-produits-et-services > ul:nth-child(1) > li:nth-child(6) > div:nth-child(1){background: none no-repeat scroll center center #717171!important;}
  
  section.lp-section:nth-child(6) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1){background-color:#1A1A1A!important;}
  
  .footer{background-color: #292929!important;
border-top: 1px solid #292929!important;}
}
