css:
@-moz-document domain("jpopsuki.eu") {
html, body {
	background: #333;
    color: #B0B0B0;
}

#head, #header, #foot {
    background: #252525;
}

input, select, textarea, .inputtext {
	background: #151515;
    border: 1px solid black;
    color: #D0D0D0;
}

a {
    color: #B1B8D0;
}

a:hover {
	color: #DD8800;
}

/* login page */

#logo {
    background: url('data:image/png;base64,') no-repeat top center;
}

#logo > a {
    color: white;
}
#logo > a:hover {
	color: #DD8800;
}

#copyleft {
   background: url('data:image/png;base64,') no-repeat center center;
}


/* index */
#searchbars, #menu, #userinfo, #alerts  {
    background: #B1B8D0;
}

#menu ul li a {
 	color: black;   
}

#searchbars input {
	background: #151515;
    border: 1px solid black;
    color: #D0D0D0;
}

.pad, .box {
	background: #252525;   
}

pre, blockquote {
    color: #666;
}

table > tbody > tr > td {
    background: #252525;
}

.colhead, .colhead a, .colhead_dark, .colhead_dark a {
	color: #333;   
}

.head, .colhead_dark td, .colhead td {
    background: #B1B8D0;
}

.head a {
    color: #555;
}

/* edit profile */
#userform > table > tbody > tr {
    background: #244446;
}

/* torrents */

.tags > a {
	color: #888;
}

#swaplink_single {
    color: #8F384A;
}

.colhead img, .colhead_dark img {
    filter: brightness(20%);
    -webkit-filter: brightness(20%);
}

/* request */

.rowa td {
	background: #252525;
}

.rowb td {
	background: #2B2B2B;
}

/* forum */

.forum_group_header > td {
	background: #C6CDE9;    
}

.box > .body {
	background: #222;   
}

tbody > tr > .body {
	background: #303030;   
}

tr.colhead_dark > td > span > a {
	color: #8F384A;
}

tr.colhead_dark > td > span:nth-child(1) > strong > a {
	color: inherit;
}


a.permission_1,.permission_1 {
	background-color:rgba(255, 0, 0, 0.7);
