css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("harmonia-pony.fr") {
  
  
  body {
    padding: 112px 0;
  }
  
    #page-body {
    margin: auto;
    max-width: 1100px;
    opacity: 0.9;
  }
  
  #phpbb {
    min-width: 100%; 
  }
  
  #wrap {
    padding: 0 0px;
  }
  
  .headerbar {
    height: 64px;
    background-color: #263238;
    margin-top: -12px;
    background-image: none;
  }
  
  .bg1, .bg2, .bg3 {
    background-color: #3E3E3E !important;
    color: #fDFDFD;
    padding-top: 10px;
    padding-bottom: 10px;
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);
    border-radius: 2px;
  }
  
  .harmoniaButton:hover, .button1:hover, .button2:hover, #tabs a:hover {
    color: #eeeeee !important;
    background-color: #339999;
    box-shadow: 0 5px 9px 0 rgba(0, 0, 0, 0.18);
  }
  
  #search-box #keywords {
    background-color: #5F5F5F;
  }
  
  #search {
    margin-top: 5px;
  }
  
  .harmoniaButton, .harmoniaButton:visited, .harmoniaButton:link, .harmoniaButton:hover, .button1, .button2, #tabs a {
    background-color: #336666;
    border-radius: 2px;
    padding: 5px 2rem;
    margin: 3px;
    border: none;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
    color: #e2e0e1 !important;
    text-shadow: none;
    transition: all 300ms;
    -moz-transition: all 300ms;
    -webkit-transition: all 300ms;
    font-family: "Fine Again";
    font-size: 14px !important;
    font-weight: normal !important;
    will-change: opacity, transform;
    -webkit-transition: all .3s ease-out;
    -moz-transition: all .3s ease-out;
    -o-transition: all .3s ease-out;
    -ms-transition: all .3s ease-out;
    transition: all .3s ease-out;
    }
  
  .postprofile {
    background-color: #4D4D4D;
  }
  
  #page-footer {
    max-height: 87px;
    box-shadow: 0 1px 8px rgba(0,0,0,.3);
    opacity: 0.9;
   }
  
  .footerBar {
    background-color: #2C2C2C;
    box-shadow: none;
  }
  
  .copyright {
    background-color: #2C2C2C;
   }
  
  .navbar {
    background-color: #37474F !important;
    margin-top: -4px;
    border-radius: 0px;
    border-top: 0px solid #3A3F4B;
    box-shadow: 0px 3px 0px #3A3F4B;
    height: 54px;
  }
  
  ul.linklist, .loveNavigation {
    margin-top: 10px;
  }
  
  #page-header {
   min-width: 1120px;
   height: 109px;
   top: 0px;
   left: 0px;
   right: 0px;
   z-index: 3;
   animation: 1s ease 1s normal none 1 running none;
   box-shadow: 0px 3px 8px rgba(0, 0, 0, 0.3);
   position: fixed;
   margin-top: 12px;
   opacity: 0.98;
  }
  
  #top {
    top: 0px;
   }
  
  #entete {
    top: 0px;
    background-image: none;
    font-size: 1em;
    background-image: none;
    text-shadow: none;
    color: rgba(0,0,0,0);
    text-align: center;
    width: 100%;
    height: 24px;
    text-decoration: none;
   }
  
  #search-box {
    z-index: 4;
  }
  
  .panel {
    background-color: #3E3E3E !important;
    color: #fDFDFD;
    padding-top: 10px;
    padding-bottom: 10px;
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);
    border-radius: 2px;
   }
  
  input[type="text"], input[type="password"], textarea:not(#helpbox) {
    padding: 6px 6px;
    box-shadow: none;
    border: 1px solid #4D4D4D;
    color: #EfEfEf;
    transition: box-shadow 300ms;
    -moz-transition: box-shadow 300ms;
    -webkit-transition: box-shadow 300ms;
    border-radius: 3px;
    background-color: #4D4D4D;
   }
  
  .forumNavBar .navlinks {
    margin-top: 0px;
   }
  
  .forumNavBar .navlinks li.current, .forumNavBar .navlinks li:hover, .forumNavBar .harmoniaButton:hover {
    background-color: #5F5F5F;
   }
  
  .forumNavBar .navlinks li.path {
    background-color: #4D4D4D;
    transition: background-color 250ms;
   }
  
   .forumNavBar .navlinks li a {
    color: #efefef;
   }
  
  
  .forabg {
    background-color: #3E3E3E !important;
    color: #fDFDFD;
    padding-top: 10px;
    padding-bottom: 10px;
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);
    border-radius: 2px;
  }
  
  .forumbg {
    margin-bottom: 8px;
    background: rgba( 0,0,0,.5 );
    border-radius: 8px;
    box-shadow: 0 0 4px 0px #FFF;
    background-color: #3E3E3E !important;
    color: #fDFDFD;
    padding-top: 10px;
    padding-bottom: 10px;
    box-shadow: 0 1px 4px 0 rgba(0,0,0,0.14);
    border-radius: 2px;
   }
  
   input[type="text"]:focus, input[type="text"]:hover, input[type="password"]:focus, input[type="password"]:hover, textarea:focus:not(#helpbox), textarea:hover:not(#helpbox) {
    border: 1px solid #336666;
   }
  
   input[type=text]:focus, input[type=password]:focus, textarea:focus:not(#helpbox) {
	  outline: 0;
	  box-shadow: 0px 0px 0px rgba(255, 255, 255, 0.6), inset 0px 0px 9px #336666;
   }
  
  .loveNavigation li:hover {
    background-color: #336666;
    color: #ededed;
   }
  
  .loveNavigation li {
    color: #fefefe;
    border-radius: 2px;
   }
  
  #navigation a:hover {
    background: linear-gradient( 90deg, #7F7F7F 50%, rgba(255,255,255,0));
    color: #2d2d2d;
   }
  
  #navigation #active-subsection a {
    color: #1f1f1f;
    background: linear-gradient(90deg,#6699CC 50%, #3E3E3E);
   }
  
  #navigation #active-subsection a:hover {
    color: #3d3d3d;
   }
  
  #navigation a:hover {
    color: #2d2d2d;
   }
  
  #navigation a {
    color: #efefef;
    background: linear-gradient( 90deg, #4D4D4D 90%, #3E3E3E);
    border-radius: 8px 0 0 8px;
   }
  
  #cp-main span.corners-bottom, #cp-menu span.corners-bottom {
    background-image: none;
   }
  #cp-main span.corners-bottom span, #cp-menu span.corners-bottom span {
     background-image: none;
   }
  
  #cp-main span.corners-top span, #cp-menu span.corners-top span {
    background-image: none;
   }
  
  #cp-main span.corners-top span, #cp-menu span.corners-top span {
    background-image: none;
   }
  
  span {
    background-image: none !important;
   }
  
  span.corners-top span {
    background-image: none;
   }
  
  #abbc3posttable {
    border: 1px solid #5F5F5F;
    border-collapse: collapse;
    vertical-align: top;
   }
  
  #abbc3posttable tr, #abbc3posttable td, #abbc3posttable div {
    background: #5F5F5F !important;
   }
  
  #helpbox {
    background-color: #5F5F5F;
   }
  
  select {
    border-radius: 2px;
    box-shadow: none;
    background: #4D4D4D !important;
    color: #efefef !important;
    border: 1px solid #5F5F5F !important;
   }
  
  #page-body h2, .forumNavBar, .rightside, li.header dt, li.header dd.topics, li.header dd.posts, li.header dd.lastpost, .postbody h3.first, .postbody h3 {
    font-family: "Roboto";
   }
  
  .harmoniaButton, .harmoniaButton:visited, .harmoniaButton:link, .harmoniaButton:hover, .button1, .button2, #tabs a {
    font-family: "Century Gothic", Arial, Helvetica, sans-serif;
  }
  
  
  a.topictitle {
    font-family: "Century Gothic", Arial, Helvetica, sans-serif;
  }
  
  #cp-main h2 {
    font-family: "Century Gothic", Arial, Helvetica, sans-serif !important;
   }
  
  #tabs a span {
    font-family: "Century Gothic", Arial, Helvetica, sans-serif;
   }
  
  #tabs .activetab a {
    background: #6699CC;
   }
  
  #search-box a:link, .navbg a:link, .forumbg .header a:link, .forabg .header a:link, th a:link {
    text-decoration: none;
    font-family: "Century Gothic", Arial, Helvetica, sans-serif;
   }
  
  h1 {
    font-family: "Century Gothic", Arial, Helvetica, sans-serif;
    margin-right: 200px;
    color: #FFFFFF;
    margin-top: 23px;
    font-weight: bold;
    font-size: 2em;
    margin-left: 23px;
    width: 100%;
    text-shadow: 0px 0.5px 0px rgb(204, 204, 204), 0px 1px 0px rgb(201, 201, 201), 0px 1.5px 0px rgb(187, 187, 187), 0px 0px 2.5px rgba(0, 0, 0, 0.1), 0px 0.5px 1.5px rgba(0, 0, 0, 0.3), 0px 1.5px 2.5px rgba(0, 0, 0, 0.2), 0px 2.5px 2.5px rgba(0, 0, 0, 0.25);
    font-size: 2.1rem;
   }

}
