css:
@-moz-document domain("javascript.ru") {
body,
.course-search,
.sidebar,
.login-form_modal .login-form__form,
.login-form__social-logins,
.search-form_fixed,
.tutorial-map__filter,
.lessons-list_screencast .lessons-list__popup-list,
.lessons-list_screencast .lessons-list__popup-list:before,
.summary,
.close-button:before,
.courses-recruitment {
    background-color: #191919 !important;
}
.sitetoolbar,
.text-input__control,
.sitetoolbar__dropdown-body,
.sitetoolbar__dropdown-body:before,
.quiz-selector .quiz-selector__item,
.page-footer,
.course-info,
.lessons-list,
.subscribe_fancy,
.profile__content,
.profile__tab_current .profile__tab-content,
.working-hover .invoice-table__data:hover,
.courses-features__title,
.courses-features__features,
.courses-programm-register,
.tutorial-map-overlay,
.tutorial-map-overlay__close,
.tutorial-map-overlay__close:active,
.switch__control:checked+.switch__label,
.tutorial-map-overlay__close,
.tutorial-map-overlay__close:active,
.working-hover .simple-button:hover,
.main table tr:nth-child(odd),
.courses-system-req__body {
    background-color: #222 !important;
}
body,
.sitetoolbar__link,
.sitetoolbar__dropdown-button,
.sitetoolbar__login,
.sitetoolbar__user,
.course-info .course-info__title,
.sitetoolbar__dropdown-body,
.sitetoolbar__secondary-link,
.intro,
.sidebar__link,
.sitetoolbar__sections .sitetoolbar__section .sitetoolbar__link,
.login-form__label,
.subscribe__line,
.quiz-timeline__number_current~.quiz-timeline__number,
.switch__control:checked+.switch__label,
.lessons-list_screencast .lessons-list__popup-item a,
.course-info_program .course-info__content .course-info__text {
    color: /*[[base-text-color]]*/;
}

/*buttons*/
.button_action,
.button_action:link,
.button_action:visited {
    background: rgb(34, 34, 34);
    color: #c0c0c0 !important
}
.working-hover .button_action:hover {
    background: #3A3D3F;
}

/*links*/
:visited {
    color: rgb(77, 131, 199);
}
.special-links-list .special-links-list__link,
.course-bricks__item a,
.lessons-list__link,
.breadcrumbs__link,
.buy-book-button,
:link,
.error__button-link,
.login-form__button-link,
.task__solution,
.invoice-table__price .price,
.main ul.courses-features__features>li.courses-features__feature:before {
    color: #2e9fff
}
.sidebar__section-title,
.main .main__header-title,
.comments .comments__header-title,
main__anchor,
.main h3.subscribe__title,
.courses-programm-register h2.courses-programm-register__title {
    color: #6F819C !important;
}
.switch__label {
    border: none;
}
.tutorial-map-overlay .tutorial-map:after {
    background: linear-gradient(180deg, hsla(0, 0%, 100%, 0) 0, #333232 80%);
}
#disqus_thread {
    background-color: #CBCBCB;
    padding: 12px;
}
.course-info .course-info__header,
.course-feedback_external {
    background-color: #222 !important;
    border-radius: 0
}
.working-hover .page__nav:hover .page__nav-text:before {
    background: #222;
}
.lessons-list,
.quiz-question__body {
    border: 4px solid #222
}
.tabbed-pane__body {
    border: 10px solid #222
}
.tabbed-pane li.tabbed-pane__tab {
    background: #222;
}
.quiz-timeline,
.lessons-list_screencast .lessons-list__popup-list,
.lessons-list_screencast .lessons-list__popup-list:before,
.quote,
.code-result {
    border: 2px solid #222
}
.switch__option:first-child .switch__label,
.switch__option:last-child .switch__label {
    border-radius: 0;
}
.tabbed-pane_01 li.tabbed-pane__tab_01,
.tabbed-pane_02 li.tabbed-pane__tab_02,
.tabbed-pane_03 li.tabbed-pane__tab_03,
.tabbed-pane_04 li.tabbed-pane__tab_04,
.tabbed-pane_05 li.tabbed-pane__tab_05,
.tabbed-pane_06 li.tabbed-pane__tab_06,
.tabbed-pane_07 li.tabbed-pane__tab_07 {
    background: #333;
}
.courses-faq ul.courses-faq__questions {
    border-radius: 0;
    background: #222;
}
.participants-logos__slider-i:before {
    left: 0;
    background: linear-gradient(90deg, #1A1A1A 0, hsla(0, 0%, 100%, 0));
}
.participants-logos__slider-i:after {
    right: 0;
    background: linear-gradient(270deg, #1A1A1A 0, hsla(0, 0%, 100%, 0));
}
.quiz-selector .quiz-selector__item,
.course-info .course-info__header,
.lessons-list__lesson,
.profile-field,
.profile-field__lcell,
.profile-field__rcell,
.profile-providers,
.profile__action-item,
.profile__linked-account,
.profile__title,
.courses-features__title,
.courses-programm-register li.courses-programm-register__course,
.search-form_fixed {
    border-bottom: 1px solid #191919
}
.page-footer,
.search-form {
    border-top: 2px solid #191919;
}
.course-info__col,
.balance__minuses:before {
    border-left: 1px solid #212121;
}
.tasks__task {
    border-top: 3px solid #212121;
}
.banner-bottom {
    border: 1px solid #212121;
}
.course-info__col {
    border-left: 1px solid #191919
}
.sidebar,
.profile__tab-content,
.courses-features li.courses-features__feature {
    border-right: 1px solid #191919
}
.main table tr,
.courses-programm-register h2.courses-programm-register__title,
.courses-faq li.courses-faq__question {
    border-bottom: 1px solid #191919;
}
.search-form__types {
    border-bottom: 1px solid #222;
}
.search-form__type[disabled],
.summary {
    border: 1px solid #222;
}
.main table tr:first-child th {
    border-bottom: 3px solid #191919;
}
.code-tabs_result_on .code-tabs__content {
    border: solid #212121;
    border-width: 0 3px 3px;
}
.important,
.balance,
.task__answer {
    border: 3px solid #212121
}
.code-tabs__switch_current:after {
    border-top: 8px solid #313131;
}
.sitetoolbar__dropdown-body .sitetoolbar__dropdown-items a:hover,
a:active {
    color: #ba1000 !important;
    text-decoration: underline;
}
.sitetoolbar__secondary-link:hover {
    color: #c0c0c0 !important
}
.buy-book-button {
    box-shadow: inset 0 0 0 2px #191919 !important
}
.buy-book-button:before {
    background: #191919;
}
.sitetoolbar__section_current,
.sitetoolbar__section_current .sitetoolbar__dropdown-button,
.sitetoolbar__section_current .sitetoolbar__link,
.working-hover .sitetoolbar__section_current .sitetoolbar__dropdown-button:hover {
    color: #2e9fff !important;
}
.code-tabs__tools,
.code-tabs__buttons {
    background: #212121
}
.working-hover .code-tabs__button:hover,
.working-hover .code-tabs__switch:hover {
    background: #1D1D1D;
}
.code-tabs__switch {
    background: #171616;
}
.code-tabs__switch_current {
    background: #313131;
}
.text-input__control {
    box-shadow: inset 1px 1px 5px 0 rgba(0, 39, 59, .35);
    border: 1px solid #191919;
}
.sitetoolbar__dropdown-body,
.sitetoolbar__dropdown-body:before {
    border: solid #333;
    border-width: 1px 0 0 1px;
}
.working-hover .profile-field_editable:not(.profile-field_editing):hover,
.profile__tab-content {
    background: #323232;
}
.text-input__control {
    box-shadow: inset 1px 1px 5px 0 rgba(96, 96, 96, 0.35);
    border: 1px solid #050505;
    color: #8DAAC9;
}
.text-input__control:focus {
    border-color: #626262;
    box-shadow: inset 1px 1px 5px 0 #434343;
    outline: 0;
}
.block-highlight .mask,
.inline-highlight .mask {
    background: #474747;
    outline: 1px solid #474747;
}
.line-numbers-rows>span:before,
.line-numbers .line-numbers-rows:after {
    color: #666 !important;
    background: #1D1F21
}
.line-numbers .line-numbers-rows,
.toolbar .toolbar__button {
    background: #1D1F21
}
.working-hover .toolbar__button:hover {
    background: #474747;
}
.token.boolean,
.token.number,
.token.property,
.token.tag {
    color: #B294BB!important;
}
code[class*=language-],
pre[class*=language-] {
    color: #c5c8c6!important;
}
.token.attr-name,
.token.selector {
    color: #F0C674!important;
}
.token.punctuation {
    color: #c0c0c0 !important;
}
.token.atrule,
.token.attr-value {
    color: #B294BB!important;
}
pre[class*=language-] {
    color: #2F2F2F;
    background: #222425;
}
.token.string {
    color: #B294BB!important;
}
.token.keyword {
    color: #DE935F!important;
}
.token.function {
    color: #F0C674!important;
}
.code-result__iframe body * {
    border: 1px solid #63636D !important;
}
.working-hover .sidebar .map:hover,
.working-hover .sidebar__toggle:hover {
    background: #3A3A3A;
    ;
}
.tutorial-map-overlay__close,
.tutorial-map-overlay__close:active,
.close-button {
    color: #3A3A3A;
}
.lessons-list_screencast .lessons-list__lesson>a:before {
    color: #1A1A1A;
    background: #646464;
    border: none;
}
.working-hover .lessons-list_screencast .lessons-list__lesson a:hover:before {
    color: #908C8C;
}
.working-hover .simple-button:hover .simple-button__description,
.working-hover .simple-button:hover .simple-button__text {
    color: #dfc374;
}
.screencast-header_style_gulp,
.screencast-header_style_nodejs,
.screencast-header_style_webpack {
    background-image: url(data:image/jpeg;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
}
.shortcut {
    background: #000000;
    border: 1px solid #616161;
    border-radius: 1px;
}
.main table tr:nth-child(even) {
    background-color: #131313 !important;
}
.lessons-list,
.important {
    border-radius: 0;
}
.task__answer_open .task__solution:after {
    border-bottom: 14px solid #212121 !important;
}
.working-hover .sitetoolbar__link:hover {
    color: #ba1000;
}
code {
    background: transparent !important;
    color: #2694f2;
}

code.comment {
    color: #878787;
}

.page-wrapper_ebook, .page__whitelist, .page-wrapper_jobs, .payment-setting, .main_white-page, .course-header, .courses-frontpage-list {
    background: none;
}

.extract__input:checked~.extract__wrap,
.pay-method__method-radio:checked~.pay-method__method-label {
    background-color: #48557c;
}

.extract__wrap, .pay-method__method-label {
    background-color: #434451;
}




h3.extract__title, .pay-method h3.pay-method__method-title, .course-header__inner, h2.courses-frontpage-list__title, .courses-masters__title, .participants-logos__title, .frontpage-content .list__link, .frontpage-content__title, .frontpage-banner__title {
    color: #e0e0e0 !important;
}

.extract__info, .pay-method h4.pay-method__method-subtitle, .courses-masters__text, .courses-mix, .frontpage-banner__text {
    color: #d2d2d2 !important;
}

.price__main {
    color: #4efea2f2;
}

.pay-method__method {
    border-bottom: 2px solid #141414;
}

.jobs-list, .jobs__aside {
    background: rgba(255, 255, 255, 0.05);
}

.jobs-list__job {
    border-bottom: 2px solid rgba(48, 48, 48, 0.4);
}

.jobs-filter__field-label, .job-detail__list dt, .job-detail, .job-detail__description {
    color: #c0c0c0;
}

.button_common, .button_common:link, .button_common:visited {
    background: rgb(34, 34, 34);
    color: #c0c0c0 !important;
    border: none;
}

.working-hover .button_common:hover {
    background: #3A3D3F;
}

.courses-features__feature-image {
    background-color: #000;
    border-radius: 42px;
    padding: 20px;
}

.participants-logos__list .participants-logos__item {
    border-radius: 100px;
    background: rgba(255, 255, 255, 0.07);
    padding: 30px 20px!important;
    height: 100px;
    display: inline-flex !important
}

.participants-logos__logo {
    width: 60px;
    height: auto;
}

.courses-faq__question-title {
    color: #2e9fff;
}

.courses-faq li.courses-faq__question, .frontpage-search, .frontpage-content {
    background-color: #282828;
}

.courses-faq__answer, .courses-faq__answer:before, .frontpage-banner {
    background: #191919;
}

.frontpage-banner:before {
    display: none;
}


/*[[fold-listings]]*/

/*[[wrap-code]]*/

/*[[content-widt]]*/
}

style_setting_options.value:
.code-tabs__content {
    height: auto !important;
    min-height: 200px !important;
}
.code-tabs__section {
    position: relative;
    height: auto;
    min-height: 200px;
}
.code-tabs .code-tabs__section pre[class*=language-] {
    position: relative;
    min-height: 200px;
    padding-bottom: 12px;
}
