css:
  /* IMAGENS NA NEW TAB */ 
.tabs-newtab-button, #new-tab-button
{
list-style-image: none!important;
background: no-repeat center url(
data:image/png;base64,
) !important;
padding:22px !important;
margin-left: 2px !important;
margin-top: 3px !important;
margin-bottom: 3px !important;
border-radius: 10px !important;
border: 2px solid #cc0000 !important;
background-color: #cc33cc !important; 
text-shadow: black 1px -1px 2px, 
white -1px 1px 2px ,
white -1px 1px 1px !important;
box-shadow: inset 5px -5px 23px -5px red,inset -5px 5px 15px -5px 
white, -5px 5px 15px -5px black !important;
-o-transition-duration: 0.4s;
  -o-transition-timing-function: ease-in;
  -moz-transition-duration: 0.4s;
  -moz-transition-timing-function: ease-in;
  -webkit-transition-duration: 0.4s;
  -webkit-transition-timing-function: ease-in;
}

.tabs-newtab-button:hover, #new-tab-button:hover {
list-style-image: none !important;
border: 2px solid #C0FF3E !important;
border-radius: 100px !important;
background: center no-repeat url(
data:image/x-icon;base64,
) !important;
background-color: #00FF00 !important;
box-shadow: inset 5px -5px 23px -5px red,inset -5px 5px 15px -5px 
white, -5px 5px 15px -5px black !important;
-o-transition-duration: 0.4s;
  -o-transition-timing-function: ease-in;
  -moz-transition-duration: 0.4s;
  -moz-transition-timing-function: ease-in;
  -webkit-transition-duration: 0.4s;
  -webkit-transition-timing-function: ease-in;
}

/*A NEW TAB BUTTON ESTICA SE A VONTADE*/
.tabs-newtab-button
{
-moz-box-flex: 109 !important;
min-width: 55px !important;
}

