css:
@-moz-document regexp("https?://[^/]+/adminer.*") {
/* Adminer 4.1.0 */

body div#menu h1,
body div#menu p,
body div#content h2,
body div#content h3,
body div#content .links {
  padding-top: 4px;
  padding-bottom: 4px;
  margin-top: 0;
  margin-bottom: 0;
}

body div#menu h1,
body div#content #breadcrumb {
  border-bottom: 1px solid #999;
  font-size: 1em;
}
body div#content #breadcrumb {
  position: static;
  height: auto;
  background: #ddf;
}
body div#content h2 {
  font-size: 1em;
  font-weight: bold;

  display: none;
}
body div#content {
  padding-top: 0;
  padding-right: 0;
  padding-left: 18px;
  margin-left: 20em;
  margin-top: 0;
}
body .logout {
  padding: 0;
  margin: 0;
}

body div#menu {
  top: 0;
  margin-top: 0;
  width: 20em;
  padding-bottom: 0;
}

body div#lang {
  display: none;
}

html body {
  /* No Verdana, please! */
  font-family: "Roboto", "Droid Sans", Arial, sans-serif;
}
}
