css:
@-moz-document domain("reshade.me") {
#g-main {
	background: #151515;
}
#g-footer {
	background: #151515;
	border-top: 1px solid rgba(50, 50, 50, 1)!important;
}
#g-navigation {
	background: #151515;
	border-bottom: 1px solid rgba(50, 50, 50, 1)!important;
}
#Kunena div.kmsg-header {
	border-bottom-color: rgba(50, 50, 50, 1)!important;
}
#Kunena div.kblock div.kbody {
	border-color: rgba(50, 50, 50, 1)!important;
}
#Kunena div.kblock {
	border-color: rgba(50, 50, 50, 1)!important;
}
#Kunena tr,
#Kunena td {
	border: solid 1px rgba(50, 50, 50, 1)!important;
}
#Kunena .krow2-stickymsg {
	background: rgba(255, 0, 0, 0.1)!important;
}
#Kunena .krow1-stickymsg {
	background: rgba(200, 0, 0, 0.1)!important;
}
#Kunena img.catavatar,
#Kunena span.kavatar img,
#Kunena div.kmsgattach,
#Kunena div.kmsgtext pre,
#Kunena div.kmsgtext code,
#Kunena span.kmessage-editmarkup #Kunena .kreply-form,
#Kunena span.kmessage-informmarkup,
#Kunena div.kmsgtext-article,
#Kunena div.kmsgtext-quote,
#Kunena div.kmsgtext-hide,
#Kunena div.kmsgtext-confidential,
#Kunena .kreply-form {
	background: #202020!important;
}
.sample-content .info-box {
	background: #151515!important;
	border: 1px solid rgba(50, 50, 50, 1)!important;
}
#g-subfeature {
	background: #151515!important;
	border-top: 1px solid rgba(50, 50, 50, 1)!important;
	border-bottom: 1px solid rgba(50, 50, 50, 1)!important;
}
#Kunena .kspoiler-wrapper {
	border-bottom: 1px solid rgba(50, 50, 50, 1)!important;
	border-top: 1px solid rgba(50, 50, 50, 1)!important;
	background-color: #151515!important;
}
u {
	color: #cccccc;
}
a {
	color: #a5a5a5;
}
u:hover,
span.g-menu-item-title:hover,
a:hover {
	color: #800000!important;
}
textarea,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input {
	background-color: #151515;
	border: 1px solid #777777;
}
#g-page-surround {
	background: #151515;
}
#g-navigation .g-main-nav .g-toplevel > li:not(.g-menu-item-type-particle):not(.g-menu-item-type-module).active {
	background: #252525;
}
div#mbCenter {
	padding: 0px;
}
#mbOverlay {
	;
	background-color: #000;
}
div#mbBottom {
	background: #101010;
}
#mbPrevLink big,
#mbNextLink big,
#mbCloseLink big {
	color: #a0a0a0;
	padding: 5px;
}
a#mbCloseLink,
a#mbNextLink,
a#mbPrevLink {
	margin-top: -5px;
}
hr {
	border-color: #444444;
}
.kheader {
	background-color: #222222;
}
legend {
	color: #cccccc;
}
#g-main > div > div:nth-child(3) > div > div > div > div > div:nth-child(3) > table > tbody > tr > td {
	border-color: #333333;
}
th {
	background: #222222;
}
td {
	border-color: #444444;
}
#Kunena div.kmsgtext pre {
	background-color: #101010 !important;
}
#g-main {
	color: #cecece;
}
}
