css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blocket.se"), domain("blocket.com") {

/* Colas Dark Blocket.se */


body {
   color: #aaa !important; 
   background-color: #111 !important;
   background-image: none !important;
}


/* Removes most ads, to enable, remove the commenting in the next section. */

/*
#panorama, div.eas_expander.index_980.expanded, #footer-logo-container, #blocketd_listning_frimarke_li,#blocketd_visning_frimarke, #blocketd_visning_vi_post_it_1, #blocketd_visning_vi_post_it_2, #blocketd_visning_vi_post_it_3, #blocketd_visning_vi_post_it_4, #eas_load_data_inverted_classifieds {
    display: none !important;
}
*/



#fif, #blocket-footer-partners {
   background-color: transparent !important;
   background-image: none !important;
}

.form-control {
   color: #aaa !important; 
   background-color: #111 !important;
}

.dropdown-menu, .tt-dropdown-menu {
   color: #ccc !important; 
   background-color: #111 !important;
}

.tt-suggestion:nth-child(2n+2), .tt-dropdown-menu:nth-child(2n+2) {
   color: #fff !important; 
   background-color: #333 !important;
}

.suggestion-name {
   color: #fff !important; 

}

.multiselect-container {
   max-height: 700px !important;
}

select {
   color: #ccc !important; 
   background-color: #111 !important;
}

.col-main, #item_list.list_mode_thumb, .image_container, .breadcrumb, .background-primary, .puff, #vi-carousel, .well, #blocket_content, .list_bostad, .item_row, #footer, #footer-info-container, #li.takeover_bostad_start_8 header, .puff_body, .job-list, #blocket, .container.blocket_white, footer#jobb-footer, header#navigation  {
   background-color: #222 !important;
}

#view-about-job.shrink::after {
	background: -webkit-linear-gradient(rgba(255,255,255,0),#000) !important;
	background: -o-linear-gradient(rgba(255,255,255,0),#000) !important;
	background: -moz-linear-gradient(rgba(255,255,255,0),#000) !important;
	background: linear-gradient(rgba(255,255,255,0),#000) !important;
}
   
#item_list, article, h1, a, .caption, .description, .title,.item_details,.filter-designation {
   color: #aaa !important;
}
.searchform-filters .featurebox_col p {
   color: #ccc !important;
}
.searchform-filters .featurebox_col p.selected {
   color: #fff !important;
}
.searchform-filters .featurebox_col .content {
   color: #aaa !important; 
   background-color: #111 !important;
}
.searchform-filters .featurebox_col .content ul li.inactive {
   color: #333 !important; 
}

.searchform-filters .featurebox_col .content ul li {
   color: #aaa !important; 
}

.item_link{
   color: #ccc !important;
}
a.item_link:visited { 
   color: #555 !important;
}

.text-secondary, dt {
   color: #777 !important;
}
.pull-left a {
   color: #777 !important;
}

.thumbnail {
   background-color: #222 !important;
}

.btn-primary, .btn-secondary, .btn-default {
   border-color: #ccc !important;
}

.btn-secondary{
   background-color: #222 !important;
}

.btn-default, .btn{
   background-color: #222 !important;
   background-image: none !important;
   color: #aaa !important; 
}

.btn-default.active, .btn.active{
   background-color: #aaa !important;
   color: #222 !important; 
}

.btn-default:hover, .btn-default:focus, .btn:hover, .btn:focus{
   background-color: #ccc !important;
   color: #222 !important; 
}

.page_nav {
   background-color: #111 !important;
}

.pagination > .active > a {
   border-color: #ccc !important;
   background-color: #111 !important;
}
.pagination > li > a {
   border-color: #555 !important;
   background-color: #111 !important;
}

.param_container, #breadcrumb_container {
    border-top: 1px solid #111 !important;
    border-bottom: 1px solid #111 !important;
}
NAV#breadcrumb.col-xs-12
{
    border-top: 0px solid #111 !important;
    border-bottom: 0px solid #111 !important;
}
UL#breadcrumb_container.breadcrumb.border-top.border-bottom.nmb {
    border-bottom: 0px solid #111 !important;
}

.media {
    border-top: 1px solid #111 !important;
}

hr {
   border-color: #111 !important;
}

#right_bar .content {
    border: 1px solid #111 !important;
}

.puff {
    border-top: 0px solid #111 !important;
    border-bottom: 0px solid #111 !important;
}

#verticals_banner_container #verticals_banner_content.loaded {
   border: 1px solid #111 !important;
}
.list-group-item {
   background-color: transparent !important;
}
.pager li > a {
   background-color: #111 !important;
   color: #fff !important; 
}
.pager li > a:hover {
   background-color: #ccc !important;
   color: #111 !important; 
}

.pager li > span  {
   background-color: transparent !important;
}
.label-default, a.label.label-default.mrxs{
   color: #222 !important;
}
.bootstrap-tagsinput {
   background: transparent !important;
   color: #aaa !important; 
}

#item_list.list_mode_thumb article .image_container.has_multiple_images:before {
   background-color: #222 !important;
}

}
