css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("csgodicegame.com") {
  #navbar{
    background: url(http://i.imgur.com/R7jhRWr.jpg) !important;
    box-shadow: 0px 0px 20px 1px #111 !important;
  }
  
  .btn-primary,.btn-info,.btn-success,.btn-default,#oddsInput{
    background: #131313!important;
    border: none !important;
    text-transform: uppercase !important;
        color: #fff !important;
  }
  
  .input-group-addon,#roll{
    background:#131313 !important;
    border: none !important;
    color: #fff !important;
  }
  
  .chatPhoto, #photo{
    border-style: none;
    border-radius: 50%;
  }
  
  .chatUser, #footer div:last-of-type a,.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus{
    color: #2196f3 !important;
    text-decoration: none !important;
  }
  
  .moderator{
    text-decoration: none!important;
  }
  .chatUser:hover,.moderator:hover{
    text-decoration: underline !important;
  }
  
  .table > thead > tr > td.success, .table > tbody > tr > td.success, .table > tfoot > tr > td.success, .table > thead > tr > th.success, .table > tbody > tr > th.success, .table > tfoot > tr > th.success, .table > thead > tr.success > td, .table > tbody > tr.success > td, .table > tfoot > tr.success > td, .table > thead > tr.success > th, .table > tbody > tr.success > th, .table > tfoot > tr.success > th{
    background: #2381cc !important;
  }
  
  .table > thead > tr > td.danger, .table > tbody > tr > td.danger, .table > tfoot > tr > td.danger, .table > thead > tr > th.danger, .table > tbody > tr > th.danger, .table > tfoot > tr > th.danger, .table > thead > tr.danger > td, .table > tbody > tr.danger > td, .table > tfoot > tr.danger > td, .table > thead > tr.danger > th, .table > tbody > tr.danger > th, .table > tfoot > tr.danger > th{
    background: #cc4538 !important;
  }
}
