#content-right .section .meta {
    display: none !important;
}
#content-left .section p,
div.section-player > p,
.section .meta {
    font-family: 'Karla', sans-serif !important;
    text-decoration: none !important;
    font-size: 12px !important;
    letter-spacing: 1px !important;
    font-weight: normal !important;
    transition: color .3s ease-in-out, background .3s ease-in-out !important;
}
#content-right .section p {
    display: none !important;
}
#content-right #news {
    left: 10px !important;
}
#content-right h3 a,
#content-right #news ul li a .title,
#content-right #news ul li a .dates {
    font-family: 'Karla', sans-serif !important;
    text-decoration: none !important;
    font-size: 12px !important;
    letter-spacing: 1px !important;
    font-weight: normal !important;
    transition: color .3s ease-in-out, background .3s ease-in-out !important;
}

#content-right #tour-dates ul li a .city {
    background: none !important;
}
#content-right #tour-dates * {
    font-family: 'Karla', sans-serif !important;
    text-decoration: none !important;
    font-size: 12px !important;
    letter-spacing: 1px !important;
    font-weight: normal !important;
    transition: color .3s ease-in-out, background .3s ease-in-out !important;
}
#content-right #tour-dates ul li a {
    border-color: #d9d9d9 !important;
}
.section-track.haarp-active {
    background: rgba(0, 0, 0, .04) !important;
}
.locale-blogs-header .blog-mini-icon,
.locale-blogs-header #country-map {
    border-radius: 3% !important;
    opacity: .7 !important;
    transition: 0.4s !important;
}
.locale-blogs-header .blog-mini-icon:hover,
.locale-blogs-header #country-map:hover {
    opacity: 1 !important;
}
.directory-blog-artists {
    background: #333 !important;
}
body,
#container {
    background: #f0f0f0 url('http://i.imgur.com/uDbs4nj.png') fixed !important;
    color: #444444 !important;
    font-family: 'Droid Sans', sans-serif, sans-serif !important;
    font-size: 100%;
    line-height: 1.5;
    word-wrap: break-word;
}
.menu li ul li a:hover {
    text-decoration: underline !important;
}
a.artist {
    color: black !important;
}
.header-box.user .avatar {
    border-radius: 50% !important;
    width: 75px !important;
    height: auto !important;
    opacity: .85 !important;
    transition: .2s !important;
}
.header-box.user .avatar:hover {
    opacity: 1 !important;
}
.user-menu a.user img {
    border-radius: 50% !important;
}
.header-box .chocolate-bar [class^="icon-"],
.header-box .chocolate-bar [class*=" icon-"] {
    top: 2px !important;
}
#twitter-board li em,
#active-users li em {
    font-family: 'Karla', sans-serif !important;
    text-decoration: none !important;
    font-size: 11px !important;
    font-weight: normal !important;
}
#content-left .section h3 .remix-count {
    color: #aaa !important;
    font-size: 11px !important;
}
#submenu a {
    color: #5e5e5e !important;
    font-family: 'Karla', sans-serif !important;
    text-decoration: none !important;
    font-size: 12px !important;
    letter-spacing: 1px !important;
    font-weight: normal !important;
    transition: color .3s ease-in-out, background .3s ease-in-out !important;
}
#submenu #submenu-filter li a {
    border-color: #d9d9d9 !important;
    font-size: 13px !important;
    font-style: normal !important;
    font-weight: 400 !important;
    margin: 0 !important;
    background-color: #f0f0f0 !important;
}
#submenu #submenu-filter li a:hover {
    border-color: #d9d9d9 !important;
    color: #5e5e5e !important;
    text-decoration: underline !important;
    background: rgba(0, 0, 0, .03) !important;
}
#submenu #submenu-filter #submenu-filter-title {
    background: rgba(0, 0, 0, .03) !important;
    color: #5e5e5e !important;
    font-family: 'Karla', sans-serif !important;
    text-decoration: none !important;
    font-size: 12px !important;
    letter-spacing: 1px !important;
    font-weight: normal !important;
    border-color: #d9d9d9 !important;
    transition: color .3s ease-in-out, background .3s ease-in-out !important;
}
#submenu a:hover,
#submenu a.selected {
    background: rgba(0, 0, 0, .03) !important;
    color: #69a032 !important;
    border: none !important;
}
#submenu a.selected:hover {
    color: #5e5e5e !important;
    background: none !important;
}
.unread-notification-icon {
    color: #76b438 !important;
}
#player-inner .count {
    background-color: #76b438 !important;
}
#header,
#footer,
#box-inner h3 {
    background: #76b438 !important;
}
.header-box .chocolate-bar a:hover {
    color: #76b438 !important;
}
#box-inner h3 {
    color: #ddd !important;
}
.tags li a.active,
.tags li a:hover {
    color: #fff !important;
}
#player-container #player-inner #player-controls #player-timebar #player-volume-outer #player-volume-ctrl {
    background: #83c441 !important;
}
#player-container #player-inner #player-controls #player-timebar #player-volume-outer {
    border: 1px solid #3b3b3b !important;
}
#player-container #player-inner #player-controls #player-timebar #player-progress-outer #player-progress-playing {
    background: #76b438 !important;
}
#player-container #player-inner #player-controls #player-nowplaying a:hover,
#player-container #player-inner #player-controls #playerNext:hover,
#player-container #player-inner #player-controls #playerPlay:hover,
#player-container #player-inner #player-controls #playerPrev:hover,
.section .tools a.play-ctrl:hover {
    color: #83c441 !important;
    text-decoration: none !important;
}
a {
    text-decoration: none !important;
    transition: 0.2s !important;
}
#search-form #q {
    font-size: 11px !important;
    font-style: normal !important;
    font-weight: normal !important;
    letter-spacing: 1px !important;
    font-family: 'Karla', sans-serif !important;
    -webkit-appearance: none !important;
    border: 1px solid rgba(0, 0, 0, .1) !important;
    border-radius: 0 !important;
    outline: none !important;
    color: #999 !important;
    background: #eee !important;
    transition: border .3s ease-in-out !important;
    box-shadow: inset 5px 5px 10px -5px rgba(0, 0, 0, 0.05) !important;
}
#search-form #g {
    font-size: 11px !important;
    font-style: normal !important;
    font-weight: normal !important;
    letter-spacing: 1px !important;
    font-family: 'Karla', sans-serif !important;
    -webkit-appearance: none !important;
    border: 1px solid rgba(0, 0, 0, .12) !important;
    background: rgba(0, 0, 0, .1) !important;
    border-radius: 0;
    outline: none !important;
    color: #eee !important;
}
.section {
    background: none !important;
}
#submenu,
.section {
    border-color: #d9d9d9 !important;
}
.header-box .chocolate-bar .bite {
    border-color: #d9d9d9 !important;
    background: rgba(0, 0, 0, .02) !important;
}
#content-left .section h3 a,
#content-left .section.single h3,
#content-left .section.single h3 a,
#player-container #player-inner #player-controls #player-nowplaying {
    font-family: 'Karla', sans-serif !important;
    text-decoration: none !important;
    font-size: 15px !important;
    letter-spacing: 1px !important;
    font-weight: normal !important;
    transition: color .3s ease-in-out, background .3s ease-in-out !important;
}
.track-info {
    font-family: 'Karla', sans-serif !important;
    text-decoration: none !important;
    font-size: 10px !important;
    text-transform: uppercase !important;
    letter-spacing: 1px !important;
    font-weight: normal !important;
    transition: color .3s ease-in-out, background .3s ease-in-out !important;
}
#menu-item-friends .friends-text,
#menu-item-username,
#menu-username ul li a,
.menu li a,
.menu li.active a {
    font-family: 'Karla', sans-serif !important;
    text-decoration: none !important;
    font-size: 11px !important;
    letter-spacing: 1px !important;
    font-weight: normal !important;
    margin-left: -3px !important;
    transition: color .3s ease-in-out, background .3s ease-in-out !important;
}
#submenu {
    font-family: 'Karla', sans-serif !important;
    text-decoration: none !important;
    font-size: 10px !important;
    font-weight: normal !important;
    transition: color .3s ease-in-out, background .3s ease-in-out !important;
}
.section .tools {
    margin-right: 1% !important;
}
/* New playlists */

#submenu li#nav-playlist-1 a {
    background: none !important;
    color: rgba(225, 123, 63, 1) !important;
    border: none !important;
}
#submenu li#nav-playlist-2 a {
    color: rgba(111, 64, 148, 1) !important;
    background: none !important;
    border: none !important;
}
#submenu li#nav-playlist-3 a {
    color: #c3c500 !important;
    background: none !important;
    border: none !important;
}
#submenu li#nav-playlist-1 a.selected {
    background-color: rgba(17, 17, 17, .06) !important;
}
#submenu li#nav-playlist-2 a.selected {
    background-color: rgba(17, 17, 17, .06) !important;
}
#submenu li#nav-playlist-3 a.selected {
    background-color: rgba(17, 17, 17, .06) !important;
}
.section .tools .playlist-ctrls a {
    color: #434359 !important;
}
.section .tools .playlist-ctrls a.playlist-1 {
    background-color: transparent !important;
    color: rgba(225, 123, 63, 0.6) !important;
}
.section .tools .playlist-ctrls a.playlist-2 {
    color: rgba(111, 64, 148, 0.8) !important;
    background-color: transparent !important;
}
.section .tools .playlist-ctrls a.playlist-3 {
    background-color: transparent !important;
    color: #c3c500 !important;
}
.section .tools .playlist-ctrls a.playlist-selected {
    background-color: transparent !important;
}
.section .tools a.play-ctrl-selected,
.section .tools a.play-ctrl:hover {
    background-color: rgba(51, 51, 51, 0.2) !important;
}
/* Hide stuff */
/* ad stuff */

.ad-wrapper,
#sponsored_tracks,
form.ad-report,
.section.same.section-moreinpost {
    display: none!important;
}
img[src$="/images/time_machine_logo.png"] {
    opacity: 0 !important;
}
img[src$="/stack_300.png"] {
    display: none!important;
}
img[src$="/logo-lastfm.jpg"] {
    display: none!important;
}
img[src$="http://ads.pictela.net/rm/ads/110055/101/AB-pencil-01.png"] {
    display: none!important;
}
.twitter_contributors {
    display: none!important;
}
/* Hide listen counter */

.section .sparkdiv {
    display: none!important;
}
#menu-item-friends img {
    display: none!important;
}
#player-container #player-inner .user-menu ul li a.find-more {
    display: none!important;
}
li#menu-item-featured {
    display: none !important;
}
li#menu-item-directory {
    display: none !important;
}
li#menu-item-labs {
    display: none !important;
}
div#popular {
    display: none !important;
}
div.share-icons {
    display: none !important;
}
span.share-links {
    display: none !important;
}
div.favcountlist {
    display: none !important;
}
div#ad-breakout {
    display: none !important;
}
div#ad-leaderboard1 {
    display: none !important;
}
div#ad-leaderboard2 {
    display: none !important;
}
/* Hide the Stack SVG */

#content-right svg:not(:root),
symbol,
image,
marker,
pattern,
foreignObject {
    display: none !important;
}
/* Make right-side content have transparent bg */

div#content-right .section {
    background-color: transparent !important;
    border: 5px solid transparent !important;
    color: #aaa !important;
    font-family: 'Open Sans', sans-serif !important;
    font-size: 0.750rem !important;
    position: relative !important;
}
form#ad-report {
    display: none !important;
}
div#ad-leaderboard3 {
    display: none !important;
}
a.sparkdiv {
    position: static !important;
    float: right !important;
    margin: 35px -107px 0 0 !important;
}
.wb-ad-grid {
    display: none !important;
}
/* Download link (userscript) */

a.gmlink {
    color: grey !important;
    font-family: 'Karla', sans-serif !important;
    text-decoration: none !important;
    font-size: 11px !important;
    font-weight: normal !important;
}
/* Remove certain content on right side, as well as some ads. */

#content-right #your-site,
#content-right .hypem-events,
div#mc_embed_signup,
.ad-breakout,
div#ad-rectangleA-frame,
div#google_image_div,
img.img_ad,
div#ad-rectangleB-frame,
div#ad-rectangleB.ad-wrapper.ad-rectangle-modern.ad-rectangle8,
div#ad-rectangleA.ad-wrapper.ad-rectangle-modern.ad-rectangleA {
    display: none!important;
}
}

@-moz-document domain("blog.hypem.com") {
#menu-item-friends .friends-text,
#menu-item-username,
#menu-username ul li a,
.menu li a,
.menu li.active a {
    letter-spacing: 0 !important;
}
}

