css:
@-moz-document domain(bayonetdiv.com){
#ipbwrapper {
    background: url("/*[[background]]*/") repeat #B3C6D2;
}
body {
    background: #fff;
    color: #333;
    overflow-y: visible;
    font: 13px arial,verdana,tahoma,sans-serif;
    position: relative;
}
#user_navigation a {
    color: #fff;
    height: 33px;
}
#user_link.menu_active, #notify_link.menu_active, #inbox_link.menu_active {
    background-position: bottom;
    background-color: #fff;
    border-right: 2px solid black;
    border-left: 2px solid black;
    -moz-border-radius: 0;
    -webkit-border-top-left-radius: 0;
    -webkit-border-top-right-radius: 0;
    border-radius: 0;
}
#header_bar {
    background: rgba(0, 0, 0, 0.6);
    padding: 0;
    text-align: right;
    box-shadow: none;
    border-bottom: 2px solid black;
}
#kod_content {
    background: none repeat scroll 0 0 white;
    border: none;
    border-left: 2px solid #000000;
    border-right: 2px solid #000000;
    box-shadow: none;
    box-shadow: -10px 0 0 0 rgba(0, 0, 0, 0.1), 10px 0 0 0 rgba(0, 0, 0, 0.1);
}
#primary_nav {
    border: 2px solid black;
    background: rgba(0, 0, 0, 0.5);
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    width: 1150px;
    padding: 0 0px;
    box-shadow: -10px 0 0 0 rgba(0, 0, 0, 0.1), 10px 0 0 0 rgba(0, 0, 0, 0.1);
}
#community_app_menu>li {
    margin: 0px 0px;
}
#community_app_menu>li>a {
    color: white;
    margin-left: 0px;
    padding: 11px 15px;
    transition: all 1s ease;
}
#community_app_menu>li.active>a {
    color: #FFF;
    font-weight: bold;
    margin-top: 0;
    text-shadow: none;
}
.left.active {
    background-color: black;
}
#community_app_menu>li>a:hover {
    color: #fff;
    box-shadow: inset 0 -36px 0 0 rgba(0, 0, 0, 0.5), inset 0 36px 0 0 rgba(0, 0, 0, 0.5);
    font-weight: normal;
    transition: all 0s ease;
}
#community_app_menu>li.active>a:hover {
    font-weight: bold;
}
.topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important, .topic_buttons li a, .topic_buttons li span, .ipsButton {
    background: rgba(0, 0, 0, 0.5);
    border: 2px solid black;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    border-radius: 0;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
    color: #fff;
    text-shadow: none;
    font: 300 12px/1.3 Helvetica, Arial, sans-serif;
    line-height: 30px;
    height: 30px;
    padding: 0 10px;
    text-align: center;
    min-width: 125px;
    display: inline-block;
    cursor: pointer;
    transition: all 1s ease;
}
.topic_buttons li a:hover, .ipsButton:hover {
    color: #fff;
    box-shadow: inset 0 -30px 0 0 rgba(0, 0, 0, 0.5), inset 0 30px 0 0 rgba(0, 0, 0, 0.5);
    transition: all 0s ease;
}
.non_button {
 	margin: 2px 0 0 0 !important;
}
.topic_buttons li.non_button a {
    background: transparent !important;
    background-color: transparent !important;
    border: 0;
    box-shadow: none;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    text-shadow: none;
    min-width: 0px;
    color: #777;
    font-weight: normal;
    transition: all 1s ease;
}
.topic_buttons li.non_button a:hover {
    color: white;
    background-color: black !important;
    transition: all 0s ease;
}
#user_link.menu_active, #notify_link.menu_active, #inbox_link.menu_active {
    background-position: bottom;
    box-shadow: inset 4px 0 0 0 black, 0 2px 0 0 white;
    background-color: #fff;
    border-right: 2px solid black;
    border-left: 2px solid black;
    -moz-border-radius: 0;
    -webkit-border-top-left-radius: 0;
    -webkit-border-top-right-radius: 0;
    border-radius: 0;
}
.boxShadow {
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
}
.ipsHeaderMenu {
    background: #fff;
    padding: 10px;
    border-left: 2px solid black;
    border-right: 2px solid black;
    border-bottom: 2px solid black;
    -moz-border-radius: 0;
    -webkit-border-bottom-right-radius: 0;
    -webkit-border-bottom-left-radius: 0;
    border-radius: 0;
    overflow: hidden;
    width: 340px;
    box-shadow: -4px 0 0 0 black;
}
#branding {
    background: no-repeat url("http://37.200.69.79/uploads/gallery/album_12/gallery_319_12_46351.jpg");
    height: 300px;
    margin: 0 auto;
    width: 1154px;
    box-shadow: -10px 0 0 0 rgba(0, 0, 0, 0.1), 10px 0 0 0 rgba(0, 0, 0, 0.1);
}
#logo {
    display: none;
}
#search_wrap {
    margin-right: 5px;
    position: relative;
    background: #fff;
    display: block;
    padding: 0 26px 0 4px;
    height: 26px;
    line-height: 25px;
    -moz-border-radius: 0;
	-webkit-border-top-left-radius: 0;
    -webkit-border-top-right-radius: 0;
    -webkit-border-bottom-right-radius: 0;
    -webkit-border-bottom-left-radius: 0;
    border-radius: 0;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    min-width: 230px;
}
.maintitle {
    background: none repeat scroll 0 0 #A7BCCD;
}
.maintitle a {
    color: white;
}
.ipsFilterbar li a {
    color: rgb(255, 255, 255);
    opacity: 1;
    text-shadow: none;
    -webkit-transition: all 0.3s ease-in-out;
    -moz-transition: all 0.3s ease-in-out;
}
}
