css:
@-moz-document domain("phoenix.corvidae.org") {
/* Swap out the forum logo */
/* This is due to either Vae or Sar, or some long-forgotten stackoverflow snippet */
#elLogo img {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    /* The image you want to use*/
    
    background: url(http://i.imgur.com/KQ9WitN.png) no-repeat;
    /* Width of new image */
    
    width: 384px;
    /* Height of new image */
    
    height: 34px;
    /* Always padding-left equal to width of new image */
    
    padding-left: 384px;
    padding-top: 40px;
}


/* Sets main body colors and colors on the top navbar */
body,
.ipsNavBar_primary,
#ipsLayout_header header{
    color: #839496;
    background-color: #002b36;
}
#ipsLayout_contentWrapper {
    background: #002b36;
}
.ipsType_richText {
    color: #839496;
}
.ipsType_light {
    color: #93a1a1;
}

/* Default link color */
a {
    color: #2aa198;
}

/* bright background style (headers with titles/buttons and such) */
.ipsAreaBackground {
    background: #073642;
}

/* less bright background (distinguishes quotes, spoilers, and for zebra style between forum sections)  */
.ipsAreaBackground_light {
    background: #002f39;
}

/* Default page title color */
.ipsType_pageTitle {
	color: #6c71c4;   
}

/* Default section title color */
.ipsType_sectionTitle,
.ipsType_sectionTitle a{
    background: #073642;
    color: #93a1a1;
    border-radius: 0px 0px 0px 0px;
}

/* Default section head color */
.ipsType_sectionHead {
    color:#6c71c4; 
}

/* default pager title color -- those forward and backward buttons on the button of some pages */
.ipsPager_type {
	color: #839496;
}
.ipsPager .ipsPager_prev a:after,
.ipsPager .ipsPager_next a:after {
    color: #839496;
}

/* field descriptions (what you should put in a tags for, for example) */
.ipsFieldRow_desc {
  	color: #839496; 
}

/* usernames -- I set a standard color for most linked usernames to make them stand out */
.ipsType_normal a[data-ipshover],
.ipsType_blendLinks a[data-ipshover],
.ipsList_inline a[data-ipshover],
h3.ipsDataItem_title a[data-ipshover],
.ipsDataItem_lastPoster a[data-ipshover]{
	color: #859900;    
}

/* NAVBAR STYLING */

/* Light highlight for navbar */
.ipsNavBar_secondary {
    background: #073642;
}

/* Further navbar styling */
.ipsNavBar_secondary > li.ipsNavBar_active a {
    color: #2aa198;
}

.ipsNavBar_secondary > li > a {
    color: #93a1a1;
}

.ipsNavBar_primary > ul > li.ipsNavBar_active > a {
    background: #073642;
    color: #2aa198;
}

.ipsNavBar_primary > ul > li > a {
    background: rgba(245, 245, 245, 0.04);
    color: #93a1a1;
}

.ipsNavBar_primary:not( .ipsNavBar_noSubBars) > ul:before {
    background: #073642;
}

.ipsNavBar_secondary > li > a:hover {
    color: #2aa198;
}


/* Usernav colors, it's that thing with your avatar and notifications on the top right */

a#elCreateNew,
a#elFullNotifications,
a#elFullInbox,
li#cUserLink > a {
    color: #2aa198 !important;
}

/* COMMON UI ELEMENTS */

/* breadcrumb thing, shows current path from forums index to post being read */
.ipsBreadcrumb.ipsBreadcrumb_top a,
.ipsBreadcrumb > ul > li,
.ipsBreadcrumb [data-action="defaultStream"], 
.ipsBreadcrumb [data-action="markSiteRead"] {
    color: #93a1a1;
}

/* tabs bars (e.g. switching between blogs and activity on the user profile) */
.ipsTabs {
	background: #073642;
}
.ipsTabs_panel {
    background: #002b36;
}
.ipsTabs_item.ipsTabs_activeItem {
    background: #073642;
    color: #2aa198;
}
.ipsTabs_item {
    background: #073642;
    color: #93a1a1;
}

/* Horiztonal widgets, these seem rare */
.ipsWidget.ipsWidget_horizontal .ipsWidget_title {
    color: #93a1a1;
    background: #073642;
}
.ipsWidget.ipsWidget_horizontal .ipsWidget_inner {
    color: #839496;
}
.ipsWidget.ipsWidget_horizontal.ipsBox {
    background: #002b36;
}

/* Vertical widgets (e.g. right side stats on the forum index) */
.ipsWidget.ipsWidget_vertical .ipsWidget_title {
    color: #93a1a1;
    background: #073642;
}
.ipsWidget.ipsWidget_vertical .ipsWidget_inner {
    color: #839496;
}
.ipsWidget.ipsWidget_vertical.ipsBox {
    background: #002b36;
}

/* widget separator */
.ipsDataItem {
    border-color: #073642;
}

/* basic content container, used for comment content and a lot of other things */
.ipsBox:not( .ipsBox_transparent ):not( .ipsModerated ) {
    background: #002b36;
}


/* Buttons */
.ipsApp .ipsButton_light {
    background: #073642;
    color: #2aa198;
}
.ipsApp .ipsButton_alternate {
    background: #002b36;
    color: #2aa198;
}
.ipsApp .ipsButton_important {
    background: #073642;
    color: #2aa198;
}
.ipsApp .ipsButton_primary {
    background: #073642;
    color: #dc322f;
    border-color: #586e75;
}
.ipsApp .ipsButton_link {
    color: #2aa198;
}
.ipsMenu_headerBar,
.ipsMenu_footerBar {
    background: #073642;
    border-color: #586e75;
}

/* filters */
.ipsButtonBar .ipsButtonRow li > a{
    color: #2aa198;
}

/* switch between full and condensed, such as on activity stream */
.ipsButtonRow li > a.ipsButtonRow_active,
.ipsButtonRow li > button.ipsButtonRow_active {
    background: #073642;
    color: #2aa198 !important;
}
.ipsAreaBackground_light .ipsButtonRow li > a:not( .ipsButtonRow_active),
.ipsAreaBackground_light .ipsButtonRow li > button:not( .ipsButtonRow_active),
.ipsAreaBackground_light .ipsButtonRow li > span:not( .ipsButtonRow_active) {
    background: #002b36;
    border-color: #002b36;
    color: #839496;
    text-shadow: none;
}

/* page number navigation bar */
.ipsButtonBar {
    background: #073642;
}
.ipsPagination a[data-page] {
    color: #2aa198 !important;
}
.ipsPagination_active a[data-page] {
    background: #586e75 !important;
}
.ipsApp input[type="number"] {
    background: #073642;
    color: #2aa198;
    border-color: #586e75;
}

/* Tags */
.ipsTags a,
.ipsTag_prefix {
    background: #073642;
    color: #2aa198 !important;
}

.ipsTags a:before,
.ipsTag_prefix:before {
    border-color: transparent #073642 transparent transparent !important;
}

.ipsTags a:after,
.ipsTag_prefix:after {
    background: #2aa198 !important;
}

/* badges (pin, sticky, etc) */
.ipsBadge_positive {
    background: #073642;
    color: #268bd2;
}

/* mini page number tabination by topic titles */
.ipsPagination.ipsPagination_mini a {
    color: #93a1a1
}

/* input fields */
.ipsApp .ipsField_autocomplete,
.ipsApp input[type="text"]{
	background: #002f39;
    border-color: #586e75;
}


/* MENUS */
.ipsMenu {
    background: #002f39;
    border: 1px solid #073642;
}
.ipsMenu_item > a,
.ipsMenu_item > span {
    color: #2aa198
}
.ipsMenu_item:not( .ipsMenu_itemClicked):not( .ipsMenu_itemDisabled) a:hover,
.ipsMenu_item[data-selected] a,
.ipsMenu_item[data-selected] span {
    background: #586e75
}
.ipsMenu_title {
    background: #073642;
}

/* menus : notification background */
.ipsDataList_readStatus .ipsDataItem:not( .ipsDataItem_unread ):not( .ipsDataItem_selected ):not( .ipsModerated ) {
    background: #002b36;
}

/* comment and follow counts */
.ipsCommentCount{
    background: #073642;
    color: #2aa198;
}
.ipsCommentCount:after {
	border-color: transparent #073642 transparent transparent !important;    
}

.ipsFollow .ipsCommentCount{
    background: #002b36;
    color: #2aa198;
}
.ipsFollow .ipsCommentCount:after {
	border-color: transparent #002b36 transparent transparent !important;   
}

/* those horrible before and after pseudo-element speech triangles */
.ipsMenu.ipsMenu_bottomRight:before,
.ipsMenu.ipsMenu_bottomRight:after,
.ipsMenu.ipsMenu_bottomLeft:before,
.ipsMenu.ipsMenu_bottomLeft:after,
.ipsMenu.ipsMenu_bottomCenter:before,
.ipsMenu.ipsMenu_bottomCenter:after{
	border-color: transparent transparent #073642 transparent;
}
.ipsMenu.ipsMenu_topCenter:before,
.ipsMenu.ipsMenu_topCenter:after,
.ipsMenu.ipsMenu_topRight:after,
.ipsMenu.ipsMenu_topRight:before,
.ipsMenu.ipsMenu_topLeft:after,
.ipsMenu.ipsMenu_topLeft:before{
	border-color:  #073642 transparent transparent transparent;
}
.ipsStreamItem:before,
.ipsStreamItem:after {
	border-color: transparent #002b36 transparent transparent !important;   
}

/* theme/copyright footer */

a#elNavTheme,
p#elCopyright a {
    color: #839496;
}

/* content editor */
/* post comment editor */

div.cke_inner,
div.cke_reset {
    background: #002b36;
}

.cke_top,
.cke_button {
    background: #073642;
}

a.cke_button_on {
    background: #268bd2;
}

a.cke_combo_button {
    border: 1px solid #002b36;
    border-bottom-color: #002b36;
    box-shadow: 0 0 0 0;
    background: #002b36;
}

.cke_combo_text {
    color: #839496;
    text-shadow: none;
}

.ipsToggle.ipsToggle_off {
    background: #073642;
}

.ipsToggle.ipsToggle_off span[data-role="status"] {
    background: #839496;
}

.ipsToggle.ipsToggle_off span[data-role="status"]:after {
    color: #839496;
}

.ipsToggle.ipsToggle_on {
    background: #073642;
}

.ipsToggle.ipsToggle_on span[data-role="status"] {
    background: #268bd2;
}

.ipsToggle.ipsToggle_on span[data-role="status"]:after {
    color: #268bd2;
}

/* user hovercard */
.ipsHovercard {
    background: #002b36;
    /*     box-shadow: 0px 7px 20px rgba(0, 0, 0, 0.2), 0px 5px 20px rgba(0,0,0,0.1); */
    
    border: 1px solid #002b36;
    ;
}
.cUserHovercard .ipsPageHead_special {
    background: #073642;
  }
.ipsHovercard_stemTop .ipsHovercard_stem,
.ipsHovercard_stemTop .ipsHovercard_stem:before{
	border-color:  #002b36 transparent transparent transparent;
}

/* user avatar background color */
.ipsUserPhoto {
    background: inherit;
}

div.ipsPageHead_special > h2 > a,
div.ipsPageHead_special > p.ipsType_reset {
    color: #859900;
}

/* tooltip */

.ipsTooltip {
    background: #002f39;
    color: #b58900;
}


/* PAGE SPECIFIC - mostly */
/* MAIN PAGE */

/* zebra backgrounds */
.ipsAreaBackground_reset {
    background: #002b36;
}

.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected):not( .ipsModerated):not( .ipsDataItem_new):not( .ipsDataItem_success):not( .ipsDataItem_warning):not( .ipsDataItem_error):not( .ipsDataItem_info):not( .ipsDataItem_status):nth-child(even) {
    background: #002f39;
}

/* forum title colors */
.cForumList .ipsDataItem_main a {
    color: #268bd2;
}
.ipsItemStatus.ipsItemStatus_large {
    background: inherit;
    color: inherit;
}

/* thumbnail borders */
.ipsImage_thumbnailed {
    border: 1px solid #586e75;
}

/* PAGE SPECIFIC */
/* UNREAD CONTENT */

/* left border */
html[dir="ltr"] .ipsStream.ipsStream_withTimeline {
    border-left: 3px solid #073642;
}

.ipsStreamItem_time {
    background: #073642;
    color: #93a1a1;
}

.ipsStream .ipsStreamItem_contentType {
    background: #002b36;
    border: 2px solid #073642;
}

li.ipsStreamItem_contentBlock:before {
    border-color: transparent #002b36 transparent transparent !important;
}

li.ipsStreamItem_contentBlock:after {
    border-color: transparent #002b36 transparent transparent !important;
}

/* stream filter */
.cStreamFilter {
    background: #073642;
    box-shadow: 0px 1px 0px rgba(0, 0, 0, 0.05);
}

.cStreamFilter h3 {
    color: #93a1a1;
}

.cStreamFilter a {
    color: #268bd2
}

.cStreamFilter_active {
    color: #268bd2 !important;
    background: #002b36 !important;
}

.cStreamFilter > li > a.cStreamFilter_active h3 {
    color: #93a1a1;
}
.ipsSticky {
    background: #073642;
}

/* topic title color */
li.ipsStreamItem > div.ipsStreamItem_container > div.ipsType_blendLinks > a:not([data-ipshover]) {
    color: #2aa198
}

/* PAGE SPECIFIC */
/* VIEW POST */

/* like button background */
.ipsLikeRep {
	background: #073642;   
}
.ipsApp .ipsButton_veryLight {
	background: #073642; 
    color: #268bd2;
}

/* quotes */
.ipsQuote_citation {
    background: #002f39;
    color: #839496;
}

.ipsQuote .ipsQuote_citation a {
    color: #859900;
}

.ipsQuote {
    border-color: #073642;
    background: #002f39;
}

a[data-action="toggleQuote"]:before{
    color: #2aa198 !important;
}

/* spoilers */
.ipsSpoiler,
.ipsSpoiler_header,
.ipsSpoiler_header a[data-action] {
    background: #002f39;
    color: #839496;
}

.ipsStyle_spoiler,
.ipsSpoiler_header,
.ipsSpoiler {
    border-color: #073642;
    background: #002f39;
}

a[data-action="toggleSpoiler"]:before{
    color: #2aa198 !important;
}

/* reply box -- hidden */
.ipsComposeArea_editor {
    background: #002b36;
}

.ipsComposeArea_editor:before {
    border-color: transparent #002b36 transparent transparent !important;
}

.ipsComposeArea_dummy {
    background: #002b36;
    color: #2aa198;
}

/* user status - offline */
.ipsOnlineStatus_offline {
	color: #073642;
}

/* embedded posts */
.ipsEmbedded_headerArea {
	background: #073642;  
}
.ipsEmbedded_content {
	background: #002f39;  
}

/* .ipsLayout_noBackground {
    background: #002f39;  
} */
iframe[data-embedContent] {
	background-color: #073642;     
}

/* user mentions */
a[data-mentionid] {
	background: #073642;   
}

/* INBOX */
.ipsEmpty {
	color: #586e75;   
}

.cMessage_active {
	background: #586e75 !important;
}

.ipsComment {
     background: #002b36;
}

.ipsComment:not( .ipsModerated ) .ipsComment_header
{
	background: #073642;
}

/* inbox composer */
.ipsDialog > div {
     background: #002b36;
}
.ipsDialog_title {
	background: #073642;
    color: #6c71c4;
}

/* USER PROFILE */
.cProfileRepScore_positive {
	background: #073642;
    color: #93a1a1;
}
#elProfileStats {
	background: #002b36;
    color: #93a1a1;
}
/* that pointless pip mark thing distinguishing members and 'advanced members' */
.ipsPip {
    display: none;
    font-size: 0px;
	color: #002b36;
}
}
