@-moz-document url-prefix("https://oc.tc/") {
  /* General */
  body {
    color: #fff;
    background: #333;
  }
  .btn-default {
    color: #fff;
    background: #282828;
    border-color: #1e1e1e;
  }
  .btn-default:hover {
    color: #fff;
    background: #1e1e1e;
    border-color: #141414;
  }
  .form-control,
  .well,
  pre {
    color: #fff;
    background: #282828;
    border-color: #1e1e1e;
  }
  .modal-content {
    background: #333;
  }
  .modal-header,
  .modal-footer {
    border-color: #282828;
  }
  .close {
    color: #fff;
    text-shadow: 0 1px 0 #000;
  }

  /* Navigation */
  .navbar-default {
    background: #282828;
    border-color: #1e1e1e;
  }
  .navbar-default .navbar-nav>li>a,
  .navbar-default .navbar-brand {
    color: #d6d6d6;
  }
  .nav>li>a:focus,
  .nav>li>a:hover,
  .navbar-default .navbar-nav>li>a:focus,
  .navbar-default .navbar-nav>li>a:hover,
  .navbar-default .navbar-nav>.active>a,
  .navbar-default .navbar-nav>.active>a:focus,
  .navbar-default .navbar-nav>.active>a:hover,
  .navbar-default .navbar-nav>.open>a,
  .navbar-default .navbar-nav>.open>a:focus,
  .navbar-default .navbar-nav>.open>a:hover,
  .navbar-collapse ul.nav li:hover {
    color: #fff;
    background: #1e1e1e;
    border-color: #141414;
  }
  .dropdown-menu {
    background: #333;
    border-color: #282828;
  }
  .dropdown-menu>li>a {
    color: #d6d6d6;
  }
  .dropdown-menu>li>a:focus,
  .dropdown-menu>li>a:hover {
    color: #fff;
    background: #282828;
  }

  /* Forums */
  .page-header,
  .forum-header,
  #forum-sidebar h4,
  hr {
    border-color: #282828;
  }
  .listing .row.topic {
    border-color: #1e1e1e;
  }
  .listing>.row:nth-of-type(even) {
    background: #282828;
  }
  .started-by {
    color: #d6d6d6;
  }
  .pagination>li>a,
  .pagination>li>span {
    background: #282828;
    border-color: #1e1e1e;
  }
  .pagination>li>a:focus,
  .pagination>li>a:hover,
  .pagination>li>span:focus,
  .pagination>li>span:hover,
  .pagination>.disabled>a,
  .pagination>.disabled>a:focus,
  .pagination>.disabled>a:hover,
  .pagination>.disabled>span,
  .pagination>.disabled>span:focus,
  .pagination>.disabled>span:hover {
    background: #1e1e1e;
    border-color: #141414;
  }
  blockquote {
    border-color: #282828;
  }

  /* Profiles */
  .nav-tabs {
    border-color: #282828;
  }
  .nav-tabs>li>a:hover {
    background: #282828;
    border-color: #1e1e1e;
  }
  .nav-tabs>li.active>a,
  .nav-tabs>li.active>a:focus,
  .nav-tabs>li.active>a:hover {
    color: #fff;
    background: #333;
    border-color: #282828;
    border-bottom-color: #333;
  }

  /* Misc */
  .thumbnail,
  .img-thumbnail {
    background: #333;
    border-color: #282828;
  }
  .table-bordered,
  .table-bordered>tbody>tr>td,
  .table-bordered>tbody>tr>th,
  .table-bordered>tfoot>tr>td,
  .table-bordered>tfoot>tr>th,
  .table-bordered>thead>tr>td,
  .table-bordered>thead>tr>th {
    border-color: #282828;
  }
  .table-striped>tbody>tr:nth-of-type(odd) {
    background: #282828;
  }
}
