/********** BREADCUMBS **********/

.breadcrumb {
    background-color: #fff;
    border: 1px solid #e5e5e5;
    box-shadow: 0 2px 3px rgba(0, 0, 0, .05);
    border-radius: 3px;
    overflow: hidden;
    position:relative;
}
.breadcrumb li.lastli {
    background-color: #fff;
}
.breadcrumb li {
    margin-bottom:2px;
    line-height: 17px;
    background: #00adef;
    -webkit-border-top-left-radius: 3px;
    -webkit-border-bottom-left-radius: 3px;
    -moz-border-radius-topleft: 3px;
    -moz-border-radius-bottomleft: 3px;
    border-top-left-radius: 3px;
    border-bottom-left-radius: 3px;
}
.breadcrumb li a {
    padding: 9px 5px 0 30px;
}


.breadcrumb li span{
    display:none;
}
.breadcrumb li a span{
    display:block;
}



.breadcrumb li a,
/*.breadcrumb li.lastli span,*/

.breadcrumb li span.spnlink {
    color: #fff;
}
.breadcrumb li .arrow-top,
.breadcrumb li .arrow-bottom {
    content: " ";
    overflow: hidden;
    width: 0;
    height: 0;
    position: absolute;
    top: 50%;
    left: 98%;
    margin-top: -25px;
    border-top-style: solid;
    border-bottom-style: solid;
    border-left-style: solid;
    border-top-width: 25px;
    border-bottom-width: 25px;
    border-left-width: 20px;
    border-top-color: transparent;
    border-bottom-color: transparent;
    
    clip: rect(7px, 25px, 42px, 0px);
}
.breadcrumb li .arrow-top {
    border-left-color: #00adef;
    z-index: 2;
}
.breadcrumb li .arrow-bottom {
    border-left-color: #fff;
    margin-left: 3px;
    z-index: 1;
}
.breadcrumb li:not(.lastli) a:hover {
    background: #138ebd;
}
.breadcrumb li:not(.lastli) a:hover .arrow-top {
    border-left-color: #138ebd;
}
