css:
@-moz-document url-prefix("http://web.roblox.com"), url-prefix("http://www.roblox.com"), url-prefix("https://web.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("https://forum.roblox.com/"), url-prefix("http://forum.roblox.com/forum/") {
/* Everything else. */

#MasterContainer,
#Footer,
.nav-content,
html {
    background: transparent;
}
#Footer > div.FooterNav {
    color: white;
}
body {
    background-color: #F2F2F2;
    /* ^ You can change this setting on the UserStyles page. ^ */
    
    margin-top: 10px;
    font-size: 10pt;
    background-size: cover;
    font-family: Segoe UI;
}
#Footer.footer-container {
    background: none;
}
#navigation > ul > li.rbx-lead:before {
    content: "Message 1 [edit me]";
    /* Changeable :-) */
    
    visibility: visible
}
#header {
    background: #001AAF
}
#navigation {
    background: #F2F3FF
}
#navigation > ul > li.rbx-lead {
    visibility: hidden;
    text-align: center
}
#navigation > ul > li.rbx-lead:after {
    content: "Message 2 [edit me]";
    /* changeable */
    
    text-align: center;
    visibility: visible;
    position: inherit;
}
input[type=text] {
    border: 1px solid #A7A7A7;
}
.forum-post .normalTextSmall {
    white-space: pre-wrap;
    overflow: hidden;
    color: #000000;
    font-family: Arial, 'Helvetica Neue', Helvetica, sans-serif;
    font-size: 14px;
}
.ctl00_cphRoblox_PostView1_ctl00_PostTitle {
    white-space: pre-wrap;
    overflow: hidden;
    color: #000000;
    font-family: Arial, 'Helvetica Neue', Helvetica, sans-serif;
    font-size: 14px;
}

tbody tr td {
    font-family: Georgia;
    font-variant: normal;
    width: 0;
    height: 2;
}

.normalTextSmall {
    font-size: 1.0em;
    font-family: Arial, 'Helvetica Neue', Helvetica, sans-serif;
}
a {
    color: #095fb5;
    font-family: Arial, 'Helvetica Neue', Helvetica, sans-serif;
}
.normalTextSmallBold {
    font-size: 1.0em;
    font-weight: bold;
    font-family: Arial, 'Helvetica Neue', Helvetica, sans-serif;
}
.normalTextSmaller {
    font-size: 0.9em;
    font-family: Arial, 'Helvetica Neue', Helvetica, sans-serif;
}
.normalTextSmall,
.normalTextSmallBold,
.normalTextSmaller {
    color: #000000;
    font-family: Arial, 'Helvetica Neue', Helvetica, sans-serif;
}
.normalTextSmall a,
.normalTextSmallBold a,
.normalTextSmaller a {
    color: #095fb5;
    font-family: Arial, 'Helvetica Neue', Helvetica, sans-serif;
}
.normalTextSmall a:visited,
.normalTextSmallBold a:visited,
.normalTextSmaller a:visited {
    color: #013DA4;
    font-family: Arial, 'Helvetica Neue', Helvetica, sans-serif;
}

.ad,
.ad *,
.img_ad,
.ad-annotations,
#google_image_div,
#aw0,
#AdvertisingLeaderboard {
    display: none !important
}
div.games-list-container.container-0.overflow-hidden {
    height: 290px;
    font-family: Lato;
    color: #000000;
    background-color: #ffffff;
    border-color: #ffffff;
}
div.sidebar-no-ad.games-page-right-sidebar {
    width: 1px;
}
div.games-responsive-wrapper.games-lists-multiple {
    width: 120%;
    margin-left: 1%;
    margin-right: 5%;
}
div.tab-pane.active {
    width: 120%;
}
div.col-xs-12.section.game-main-content {
    width: 120%;
}
}
