css:
@-moz-document domain("christianforums.com") {
/* THREAD */

div#headerMover {   /*PAGE BACKGROUND*/
    background-color: #5e7578 !important
}

/* Adjusted 10/13/16: */
div.thread_view {  /* THREAD WIDTH*/
  /*  width: 800px; */
}

div.pageContent {   /*masthead; table highlights*/
    background-color: #7e9ca1 !important
}
li.message,
div.messageInfo.primaryContent  {             /* POST */
    background-color: #99b8bd !important
}
li[class*="sticky"] a:link,
li[class*="sticky"] {   /* STICKY TOPICS */
    color: gray !important;
    font-size: 14px
}


div.messageHeading,
div.navTabs,
h3.subHeading,
div[class*="categoryStrip"],
dl.sectionHeaders {      /*HEADERS*/
    background-color: #147079 !important
}
div.messageUsserBlock div.extraUserInfo {
    background-color: red !important
}


div[id*="featured-thread"] { /* FEATURED THREAD */
    display: none
}
#pageDescription,
#pageDescription a  {  /* DESC. UNDER THREAD TITLE*/
    color: black !important
}               
body[contenteditable="true"]  { /* EDIT BOX */
    background-color: #c9eadf
}                                            
/* NW 

div[class*="nodeInfo"] {
    background-color: #147079
} */

}
