@-moz-document domain("bbs2.ruliweb.daum.net") {
#daumContent {
    /* 전체 화면 */
    float: left;
    margin: 0 auto;
    width: 90%;
    min-height: 808px;
    height: auto!important;
    height: 808px;
}

.cont_family {
    /* 컨텐츠들을 둘러싼 겉 */
    
    float: left;
    width: 80%;
    min-height: 808px;
    height: auto!important;
    height: 808px;
}
.ruliweb_type4 #mCenter {
    /* 일반 게시물 */
    
    position: relative;
    float: left;
    min-width: 700px;
    width: 80%;
}
.ruliweb_family #mCenter {
    /* 만화 게시물 */
    
    position: relative;
    float: left;
    min-width: 700px;
    width: 80%;
}
.txc-image {
    /* 그림 속성 */
    
    width: 100% !important;
    height: 100% !important;
}
.position {
    /* 오른쪽 베스트 */
    
    float: right;
    width: 100% !important;
}
.ruliweb_type4 #mLeft {
    /* 왼쪽 힛겔 */
    
    float: left;
    width: 10%;
}
.box_sns_link {
    /* 게시글 내 SNS 줄 교정 */
    
    width: 100% !important;
}
}

