css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vector.im") {
/*
  * {
    font-family: "InconsolataGo", "Consolas", monospace;
  }
*/
/* These are the roomlist panel bits */
  .mx_MatrixChat .mx_LeftPanel {
    background-color: #b3b3b3;
  }
  .mx_SearchBox {
    padding-top: 4px;
    padding-bottom: 4px;
    margin-right: 5px;
    background-color: #cccccc;
  }
  /* More noticeable alerts */
  .mx_RoomTile_unread > .mx_RoomTile_name {
    color: goldenrod;
  }

  .mx_RoomTile_invited > .mx_RoomTile_name {
    color: purple;
  }

  .mx_RoomTile_highlight > .mx_RoomTile_name {
    color: red;
  }

  .mx_RoomTile_unread {
    background-color: #595959;
  }
  .mx_RoomTile_highlight {
    background-color: #454545;
  }

  /* More visible current-channel indicator */
  .mx_RoomTile.mx_RoomTile_selected {
    border-top: 3px solid rgba(200, 245, 206, 1);
    border-bottom: 3px solid rgba(200, 245, 206, 1);
    border-left: 3px solid rgba(200, 245, 206, 1);
    border-top-left-radius: .5em;
    border-bottom-left-radius: .5em;
    margin-left: .5em;
    background: white;
   /* overflow-x: hidden;*/
  }

  .mx_RoomTile.mx_RoomTile_selected > .mx_RoomTile_name > span {
    background: white;
  }

  .mx_RoomTile.mx_RoomTile_selected > .mx_RoomTile_avatar {
     padding-left: calc(18px - .5em - 3px);
  }



/* these are the Message Panel bits */
  .mx_MatrixChat .mx_MatrixChat_middlePanel {
    background-color: #cccccc;
    padding-left: 5px;
    padding-right: 5px;
  }
  .mx_EventTile .mx_MessageTimestamp{
    color: black;
  }
  .markdown-body code {
    background-color: whitesmoke;
  }
  linkified a:hover, a:link, a:visited {
    color: darkblue;
  }
  .mx_EventTile_content .markdown-body a {
    color: darkblue;
  }
/*  .mx_RoomView_messagePanel .mx_fadable .gm-autoshow .gm-scrollbar-container {*/
  .mx_RoomView_messagePanel {
	margin-left: -4px;
	/*padding-left: -15px;*/
	/*margin-right: -15px;*/
	/*padding-right: 2px;*/
  }

/* Title area in Message Panel */
  .mx_RoomView .mx_RoomHeader {
    background-color: #181818;
	margin-left: -4px;
	padding-left: 5px;
	margin-right: -5px;
	padding-right: 2px;
  }
  .mx_RoomHeader_rightRow {  /* Leave Room and Search Icons */
    background-color: #181818;
  }
  .mx_RoomHeader_nametext {
    color: #BCC6CC;
  }
  .mx_RoomHeader_topic {
    color: #BCC6CC;
  }

/* Message Composer Bits */
  .mx_RoomView .mx_MessageComposer {
    background-color: #595959;
  }
  .mx_MessageComposer_input textarea {
    background-color: #f2f2f2;
    border-radius: 0.25em;
    padding-left: 2px;
  }
  .mx_RoomHeader_cancelButton img {
    background-color: #cccccc;
    padding: 2px;
    border-radius: 4px;
  }


/* Misc */
  .mx_BaseAvatar_image {
    background-color: white
  }

  .mx_MatrixToolbar_close img {
    background-color: #b5b5b5; padding: 1px;
  }

/* These are the Memberlist Panel bits */
  .mx_MatrixChat .mx_RightPanel {  /* Entire Members List including search */
    background-color: #b3b3b3;
  }
  .mx_RightPanel_headerButtonGroup { /* Members Icon (it expands/collapses the members list) */
    margin-left: 0px;
    background-color: #181818;
  }
  .mx_RightPanel_header {  /* Header Area that is above the members list. contains the Members Icon */
    background-color: #181818;
    margin-right: 0px;
  }
  .mx_SearchableEntityList .mx_SearchableEntityList_query {
    margin-left: 10px;
    margin-right: 10px;
    width: unset;
  }
}
