css:
@-moz-document domain("vk.com") {


.im-mess-stack--content > #content > div > div.im-page.js-im-page.im-page_classic.im-page_history-show > div.im-page--history.page_block._im_page_history > div.im-page-history-w > div.im-page--chat-body > div > div > div > div > div._im_peer_history.im-page-chat-contain > div:nth-child(86) > div.im-mess-stack--content > div > div > a

.vk_menu_block ul {
    list-style: none;
    margin: 2px;
    padding: 0px;
    background: rgba(255,255,255,0.9);
border: none!important;

    box-shadow: none!important;
    overflow: hidden;
}

.im-page.im-page_classic .im-page--header-chat {
border-top:solid 7px #eef2fa;
border-radius: 5px 5px 0 0;
}

.im-page.im-page_classic .im-page--chat-input {
border-bottom:solid 7px #eef2fa;
border-radius: 0 0 5px 5px;
}

.im-page .im-page--toolsw, .im-page.im-page_classic .im-page--header-inner, .wall_module>.page_block:first-child, .post_header, .ui_tabs_header, .ui_tabs_sub_header, .page_block_header, .apps_options_bar {
border-radius: 5px 5px 0 0;
}

.im-chat-input.im-chat-input_classic, .post_full_like_wrap, div._post_content, .feed_has_new .feed_row .page_block, .flat_button.ui_load_more_btn.smaller, .flat_button.ui_load_more_btn, .apps_footer {
border-radius: 0 0 5px 5px;
}

.page_block,
.wall_module .wall_posts.all>.all~.all, .wall_module .wall_posts.own>.own~.own, .wall_module .wall_posts.postponed>.postponed~.postponed, .wall_module .wall_posts.suggested>.suggest~.suggest, .wall_module .wall_posts_search>.post~.post, .feed_has_new .feed_row~.feed_row .page_block {
margin:5px 0 0;
border-radius:5px;
}

.wall_module .post, .narrow_column, #dev_left_nav, #dev_page_wrap1, .dev_page_block, .page_avatar_img, .photos_container_albums .photos_album_thumb, .photos_container .photos_row a, .photos_container .photos_row, .chat_onl_inner {
border-radius:5px;
}

.wall_module .reply_fakebox, .wall_module .reply_form, .wall_module .reply_box, .wall_module .reply_fakebox_wrap, .reply_box_inner_wrap, .wall_module .reply_box_open .replies_wrap, .wall_module .reply_box_open .reply_form_image, .wall_module .reply_box_open .submit_post, div.replies {
border-radius: 0 0 5px 5px;
}


body {background: #eef2fa;}

.profile_online{
font-weight: bold!important;
color: #36962f!important;}

#side_bar .left_label {
    height: 25px;
    line-height: 25px;
}

#side_bar ol {
min-width:155px;
    margin:0;
}

#side_bar ol li {
    margin-left: 0;
background-color: #fff;
border-top: 1px solid #e3e4e8;
border-bottom: 1px solid #d7d8db;
border-right: 1px solid #d7d8db; 
border-left: 1px solid #e3e4e8;
border-radius: 5px;
} 

#side_bar ol li:active
	{
	transform: scale(0.9);
	opacity:0.7;
	}

span.left_label.inl_bl {padding-right:5px;}

#page_body {
    font-size: 15px;
}

#side_bar ol {
   position:fixed;top:46px;}

#musicsig-calendar {
    position:fixed; overflow:hidden; top:525px; padding-left: 10px; font-size: 13px;}

#musicsig-clock {
    position:fixed; overflow:hidden; top:640px; padding-left: 25px;}


.wide_column_right .wide_column_wrap {
    margin-right: 245px;
margin-left: -1px;
}

.wide_column_right .narrow_column_wrap {
    float: right;
}


#side_bar .more_div, .left_menu_more, .left_menu_nav_wrap, 
.close_button, #ads_left {
display:none!important;
}
}
