css:
@-moz-document domain("mpc-hc.org") {
/*
This section is for mpc-hc.org
*/

body {
    background-color: #101010;
}
.navbar-default {
    background-color: #101010;
    border-color: rgba(50, 50, 50, 1);
}
.navbar-default .navbar-nav>.active>a,
.navbar-default .navbar-nav>.active>a:focus,
.navbar-default .navbar-nav>.active>a:hover {
    color: #101010;
    background-color: #337AB7;
}
.main-container {
    background-color: rgba(0, 0, 0, 0);
    box-shadow: 0 0 4px rgba(50, 50, 50, 1), inset 0 0 0 rgb(0, 0, 0);
}
.alert-info {
    background-color: rgba(51, 122, 183, 0.25);
    border-color: #337AB7;
    color: #337AB7;
}
hr {
    border-top: 1px solid rgba(50, 50, 50, 1);
}
.panel {
    background-color: #101010;
    box-shadow: 0 1px 1px rgb(50, 50, 50);
}
.well {
    background-color: #101010;
    border: 1px solid #323232;
    box-shadow: inset 0 1px 1px rgb(50, 50, 50);
}
.btn-default {
    color: #337AB7;
    background-color: #101010;
    border-color: #337AB7;
}
.btn-default:active,
.btn-default:hover,
.open>.dropdown-toggle.btn-default {
    color: #101010;
    background-color: #337AB7;
    border-color: #337AB7;
}
h1,
h2,
h3,
h4 {
    color: #337AB7;
}
.panel-default>.panel-heading {
    color: #337AB7;
    background-color: #101010;
    border-color: #337AB7;
}
.panel-default {
    border-color: #337AB7;
}
.panel-danger>.panel-heading {
    color: #CE4844;
    background-color: #2D0000;
    border-color: #CE4844;
}
.panel-danger {
    border-color: #CE4844;
}
p {
    color: #777777;
}
.footer-center,
.footer-left {
    border-right: 1px solid rgba(50, 50, 50, 1);
}
.navbar-default .navbar-nav>li>a:focus,
.navbar-default .navbar-nav>li>a:hover {
    color: #101010;
    background-color: #337AB7;
}
.dropdown-menu>li>a,
.navbar-default .navbar-nav>li>a {
    color: #337AB7;
}
.col-xs-12.col-sm-6 {
    color: #777;
}
.thumbnail {
    background-color: #101010;
}
.table>tbody>tr>td,
.table>thead>tr>th {
    border-top: 1px solid #337AB7;
}
.table-hover>tbody>tr:hover {
    background-color: #101010;
}
.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #101010;
}
.table>thead>tr>th {
    border-bottom: 1px solid #337AB7;
}
li {
    color: #777;
}
td {
    color: #777;
}
th {
    color: #404040;
}
code {
    color: #ebccd1;
    background-color: #a94442;
}
}

@-moz-document domain("mpc-hc.org") {
/*
This section is for nightly.mpc-hc.org
*/

#crumbbar a.active,
#crumbbar a:active.active,
#crumbbar a:visited.active {
    color: #777;
}
#topbar {
    background: #101010;
    box-shadow: 0 4px #337AB7;
}
div#content {
    background-color: #101010;
}
#view.view-details #items {
    border-radius: 2px;
    box-shadow: 0 0px 0px 0 #337ab7;
    border: 1px solid rgba(50, 50, 50, 1);
    ;
}
#view.view-details .header {
    background: #101010;
    border-bottom: 1px solid rgba(50, 50, 50, 1);
    ;
}
li.header {
    color: #FFF;
}
#view .item {
    background: #101010;
}
#view .item:hover {
    background: #101010;
}
#view.view-details .item {
    border-bottom: 1px solid rgba(50, 50, 50, 1);
}
span.label {
    color: #777;
}
span.label:hover {
    color: #337ab7;
}
#view.view-details .item .label:hover,
#view.view-details .item .date:hover,
#view.view-details .item .size:hover {
    color: #777;
}
#view.view-details .item .label,
#view.view-details .item .date,
#view.view-details .item .size {
    color: #777;
}
#view.view-details .item:hover {
    box-shadow: 0 0 0px 1px #337ab7;
}
::selection {
    background: #337AB7;
    color: #FFF;
    text-shadow: none;
}
#tree {
    background-color: #151515;
}
#pv-content-txt {
    background: #1f1f1f;
    color: #aaaaaa;
}
}
