}
.content {
	color: grey !important;
}
.ok {
	background: #4E4E4E !important;
	border: 0 !important;
	color: white !important;
}
.err {
	background: #101010 !important;
	border: 0 !important;
	color: white !important;
}
li {
	color: grey !important;
}
.navi>b { color: black !important; background: #A9A9A9 !important;}

.block2 { border: 0 !important; color: grey !important;}
.block1 {
