css:
/* i really want this to be global */
* {
    font-family: Avenir Next, Helvetica Neue, Arial, Sans;
}
div#logo {
    font-size: 18px !important;
}
div#logo img.pin_logo {
    content: url("https://dl.dropboxusercontent.com/u/74051/pinboard-32x32.png") !important;
    width: 16px;
    height: 16px;
}
.display {
    line-height: 1.5em !important;
}
#banner,
.user_navbar,
#tag_cloud {
    font-size: 115% !important;
}
.bookmark_title {
    font-size: 1.6em !important;
}
.private {
    margin-top: 15px !important;
}
a.url_display {
    font-size: 1.2em !important;
}
a.url_link {
    padding: 1px 2px 1px 2px !important;
    font-weight: 300 !important;
}
.description {
    margin: 5px 0 5px 0 !important;
    font-size: 1.2em !important;
    line-height: 1.4em !important;
    color: #888 !important;
    font-weight: 400 !important;
}
.when,
.edit_links {
    margin: 5px 0 5px 0 !important;
    font-size: 1.1em !important;
    line-height: 1.4em !important;
    color: #888 !important;
    font-weight: 300 !important;
}
.star,
a.cached {
    font-size: 200% !important;
}
.star,
.cached,
.source {
    display: none !important;
}
.url_link {
    background-color: #FFE7B8 !important;
    border: 1px solid #FFD372 !important;
    font-size: 1.1em !important;
    line-height: 1.4em !important;
    font-weight: 300 !important;
}
a.tag {
    font-weight: 600 !important;
    font-size: 1.2em !important;
}
