css:
@-moz-document url-prefix("http://tieba.baidu.com/f"),url-prefix("http://tieba.baidu.com/p"){
    .no_icon_author {
    padding-left: 0px !important;
}
/*搜索建议错位*/
    .suggestion{top:71px!important;left: 0.7%!important;}

/*楼中楼错位*/
.lzl_panel_wrapper {position:relative!important;}
.lzl_edui_dialog_container {left:18px!important;}
/*title,搜索位置调整*/
    div.card_top_wrap.clearfix.card_top_theme1{height: 95px;}
    html body.plat_skin div.wrap1 div.wrap2 div.wrap_bright div#container.container_bright div.content div.forum_header div#tb_nav.star_nav_wrap.star_nav_wrap_platform div.search_internal_wrap.j_search_internal{top:10px;}
    div.search_nav.j_search_nav a{text-decoration:none!important}
    .search_nav a{color:#E8E8E8!important;}
    .threadlist_title > a:not(:visited){color: rgb(45, 100, 179) !important;}
.threadlist_title > a:visited{color:rgb(101, 48, 150) !important;}
.core_title h1{color:#000 !important}
    /*强制微软雅黑字体*/
    *{font-family:"Microsoft Yahei" !important;}
    
    /*全局背景*/
    .wrap2,.wrap1{background:#E8E8E8!important;}
    .clearfix{background:none!important;}
    .thread_list_bottom.clearfix{background:#fafafa!important;}
    
    /*去掉:头图,看贴条,浮动工具条,彩蛋,签到,logo*/
    .card_banner,.tb_paper_wrap,.tbui_aside_float_bar,#tbmall_score_region,#sign_mod_banner,#balv_mod>.j_wrap
    ,.search_logo,.sign_title_text_bright,#search_logo_small,.icon_zhuanpan,.icon_group,.j_tbnav_tab.tbnav_tab_list
    {display:none!important;}
    
    /*贴子双行*/
    #thread_list li{border-top: 1px solid #FEFEFE !important;border-bottom: 1px solid #CCCCCC !important;}
    #thread_list li:nth-of-type(odd){background:#E7EAEB  !important;}
    #thread_list li:nth-of-type(even){background:#fafafa !important;}
    #topic_post_thread,#head{background:#fafafa !important;}
    .t_con{border:none !important;}
    
    /*贴子回复数*/
    .threadlist_rep_num{background:white!important;padding:0px !important;border:1px solid #ccc;height:25px!important;}
    
    /*贴子拉宽*/
    .content{width:98%!important;}#topic_thread_wrapper:hover{padding-left:15px !Important;}.live_thread_cnt{position:static!important;}
    .live_item_wrapper{width:98%!important;}.live_thread_cnt{width:98%!important;}.live_item_cnt{width:100%!important;}
    #contet_wrap{border:1px solid #ccc;width:calc(100% - 244px) !important;}
    .forum_content .main{border:1px solid #ccc;width:calc(100% - 244px) !important;}
    .threadlist_bright .threadlist_li_right{width:calc(100% - 70px) !important;}
    #aside{background:#fafafa!important;border:1px solid #ccc;}
    html{overflow-x:hidden!important;}
    /*测试*/
    .head_content{width:calc(100% - 30px) !important;margin-top: 30px !important;}
    .tbnav_tab_inner .space{margin-top:0px!important}
    /*搜索*/
   .search_main_fixed{display:none!important;position:static!important;border:none!important;box-shadow:none!important;background:none!important;padding:0px!important;}
    #tb_header_search_form{position:absolute;left:0px;left:13px;top:40px;}
    #container,.content{margin-top:35px;}

    /*导航*/
    .search_nav{
        position:absolute;left:0px;padding-top:0px;padding-left:10px;
        height:30px;line-height:30px;
        width:100%!important;background:#434343!important;
        box-shadow: 1px 1px 2px 1px #8C8C8C !Important;border-bottom:2px solid #BBBCBC !Important;
        color:white !important;
    }
    #tb_nav{
        background:#434343!important;
        box-shadow: 1px 1px 2px 1px #8C8C8C !Important;border-bottom:2px solid #BBBCBC !Important;
        color:white !important;
        height:35px!important;
    }
    .ui_bubble_up{left:-25px!Important;}
    #tb_nav .j_nav_list *{background:none!important;color:white!important;font-size:14px!important;}
.star_nav_wrap *{background:none!important;color:white!important;font-size:14px!important;margin-top:-2px!important;}
.star_nav_btns_wrap{margin-top:10px!important;}
    .nav_list a.nav_icon{padding-left:35px!important;}.nav_list li {line-height:35px!important;}.forum_content.clearfix{margin-top:10px;}
    .forum_header{background:#fafafa!important;border:1px solid #ccc;}#head{background:none!important;}
    .search_nav a[class]{color:white!important;text-decoration:none!important;}
    span.u_username_title:hover{color:black!important;}
    li[class="split"],.u_menu_app,.u_menu_tbmall,.u_menu_tshow{display:none!important;}
    .card_head,.card_head_img{width:80px!important;height:80px!important;}
    .card_head{top:-18px!important;margin-left:5px!important;}
    .card_title,.card_slogan{margin-left:-70px!important;}
    #com_userbar a.j_help-center{color:#53534F!important;}
    
    /*按钮*/
    .j_search_internal_input,.search_internal_btn{margin-top:-5px;}.j_search_internal_placeholder{display:none!important;}
    .j_enter_ba,.j_search_post{background:none!important;border:none!important;color:black!important;}
    .search_btn_enter_ba_wrap,.search_btn_wrap{height:30px!important;}
    .senior-search-link{padding:2px 10px 2px 10px!important;margin-left:5px;}
     input[type="text"],.search_btn_enter_ba_wrap,.search_btn_wrap,.senior-search-link,.lzl_simple_wrapper .tb-editor-editarea{background:#F5F5F5!important;border:1px solid #ccc;color:black!important;;}
     input[type="text"]:focus,#ueditor_replace:focus,.lzl_simple_wrapper .tb-editor-editarea:focus{box-shadow: 0 1px 2px rgba(0,0,0,0.4) inset !important;background:white!important;border:1px solid #537CC4;}
    
    /*回复框*/
    .poster_rewards,#poster_rewards_disabled,.poster_head_surveillance,.edui-btn-medal{display:none!important;}
    .forum_foot{background:none!important;}
    .th_footer_l{display:none!important;}
}
@-moz-document url-prefix("http://tieba.baidu.com/p"),url-prefix("http://tieba.baidu.com/f?ct="){
    /*贴子页*/
    /*帖子title*/
     div.card_top_wrap.clearfix.card_top_theme2{background-color: #fafafa!important;border:1px solid #bcbcbc !important}
    div.card_top.clearfix{border-width: 0px!important;}
    .right_section.right_bright,#often_forum,.user-hide-post-down,.share_btn_wrapper,.d_nameplate
    ,.lzl_panel_wrapper td:first-child,.new_icon,.j_lzl_p_sv,.edui-btn-voice,.pb_bright_share,.card_head
    {display:none!important;}
    .l_container,#thread_theme_7,.pb_footer{width:100%!important;}
    .core_title_theme_bright,.core_title_absolute_bright{position:static !important;border:none!important;}.core_title_wrap{width:100% !important;border:none!important;}.p_postlist_absolute{margin-top:0!important;}
    .left_section,#tb_nav{width:100%!important;}
    .d_post_content_main{width:calc(100% - 160px) !important;}
    .p_content,.l_post_bright,#pb_content,#thread_theme_5{width:100% !important;}
    .core_reply_content,.core_reply_wrapper{width:98.5%!important;margin-top:5px;}
    #thread_theme_5{margin-top:5px!important;}
    .card_title{left:5px!important;}.card_top{background:#fafafa!important;border:1px solid #ccc;}
    #tb_rich_poster{margin-top:20px!important;}
    .p_reply,.lzl_link_fold{background:none!important;border:none!important;}
    .s_tools{position:absolute!important;width:150px!Important;}
    /*贴子样式*/
    .l_post,.d_post_content_main{background:#F2F2F2!important;border:none!important;}
    .lzl_simple_wrapper .tb-editor-editarea{border:1px solid #BCBCBC!important;height:80px!important;}
    .lzl_simple_wrapper{border:1px!Important;}
    .p_content,.p_content_anonym
    {border:1px solid #BCBCBC !important;padding:10px 15px 10px 15px !important;width:98%!important;;
    margin:0px 0px 0px -15px !important;background:#E7EAEB !important;border-radius:2px !important;}
    
    .p_content:hover,.p_content_anonym:hover
    {transition-duration: 0.42s;box-shadow:0 0 3px 0 #ccc !important;}
    
    .core_reply_wrapper{border:none !important;}#j_p_postlist{border:1px solid #ccc;padding-top:5px !important;}
    .core_reply_content{border:1px solid #BCBCBC !important;background:#F7F8FA!important;}
    /*自动隐藏签名档*/
    .d_sign_split{background:none !Important;width:50px !Important;height:auto !Important;border:none!important;}
    .d_sign_split::before{content:"签名档" !important;color:#989898 !Important;cursor:default !Important;transition:0.3s ease all;-webkit-transition:0.3s ease all;}
    .d_sign_split + img{position:absolute !important;z-index:300;max-height:0px!Important;height:auto!Important;width:auto!important;
    box-shadow: 0px 0px 4px 0px #BCBCBC !important;transition:0.3s ease all;-webkit-transition:0.3s ease all;}
    .d_sign_split:hover + img,.d_sign_split + img:hover{max-height:200px !Important;transition:0.3s ease all;-webkit-transition:0.3s ease all;}
}
