css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


.tabbrowser-tab:not([selected="true"]) .tab-close-button:not(:hover):not(:active),
.tabbrowser-tab[selected="true"]:-moz-lwtheme .tab-close-button:not(:hover):not(:active) {
list-style-image: url("chrome://global/skin/icons/close-inverted.png")!important}



  #main-window[tabsintitlebar]:not([inFullscreen]):not(:-moz-lwtheme) #toolbar-menubar[inactive] ~ #TabsToolbar:not(:-moz-lwtheme) {
    background-image: -moz-linear-gradient(#3B3A36, #3B3A36) !important;
  }


.tabs-newtab-button,
#TabsToolbar > #new-tab-button,
#TabsToolbar > toolbarpaletteitem > #new-tab-button {
list-style-image: url("chrome://browser/skin/tabbrowser/newtab-inverted.svg")!important}

.tabbrowser-tabs:not([selected]),#alltabs-button{color:#FFFFFF}



  #main-window[tabsontop=false]:not(:-moz-lwtheme) #titlebar-content,
  #main-window[sizemode=normal]:not(:-moz-lwtheme) #titlebar-content,
  #titlebar-content:not(:-moz-lwtheme) {
    background-image: -moz-linear-gradient(#43423E, #3B3A36, #3B3A36) !important;
    box-shadow: 0 -1px 0 0 #454440 !important;
  }


#main-window .scrollbutton-up, #main-window .scrollbutton-down {
list-style-image: url("chrome://browser/skin/tabbrowser/tab-arrow-left-inverted.svg") !important;
}
