css:
@-moz-document url-prefix("https://gitter.im/") {
/* Hosted at https://userstyles.org/styles/126908/gitter-don-t */

header,
#right-toolbar-layout,
#profile-region,
.menu__create-room-wrapper {
    display: none !important;
}

.chat-and-toolbar-wrapper {
    padding: 2px;
}

#favs-region {
    padding-top: 30px;
}

code {
    max-height: 100px;
    overflow: scroll;
}


body,
#chat-container,
.chat-item,
.room-list-item--current-room {
    background-color: #FAF5E0;
}

.menu__hotspot {
    opacity: 0.1;
    margin-top: -100px;
}
}
