*/

/* SERVER LIST */
.bd-minimal .guilds {
	padding-left:10px !important;
}

.bd-minimal .guilds .friends-icon {
	background-size:60%;
}

.bd-minimal .guilds li .guild-inner:before {
    height:25px;
    border-radius:5px;
    margin-top:-13px;
}

.bd-minimal .guilds li.unread .guild-inner:before {
	width:10px;
	height:10px;
	margin-top:-5px;
	left:-16px;
}

.bd-minimal .guilds li.active .guild-inner:before {
	left:-18px;
	border-radius:5px;
	height:25px;
	margin-top:-13px;
}

.bd-minimal .guilds li,
.bd-minimal .guilds li .avatar-small,
.bd-minimal .guilds li .guild-inner,
.bd-minimal .guilds li .guild-inner a,
.bd-minimal .guilds .friends-icon {
	width:25px;
    height:25px;
    line-height:25px;
}

.bd-minimal .guilds li .avatar-small, #bd-pub-button {
	font-size:10px !important;
}

/* CHAT */
.bd-minimal .chat>.title-wrap>.title {
    font-size: 16px;
}

.bd-minimal .theme-dark .comment {
	border-left:none !important;
	padding-left:10px;
}

.bd-minimal .message-group {
	padding:10px 5px;
}

.bd-minimal .guild-channels ul .channel-text .channel-name {
	padding-left:5px;
}

.bd-minimal .avatar-large {
	border-radius:100%;
}

.bd-minimal .message-group .edit-message .edit-container-outer {
    margin-left:50px;
}

.bd-minimal .message-group .edit-message .edit-container-inner {
	margin-left:10px;
}

/* MEMBERS */
.bd-minimal .channel-members h2 {
	margin-top:20px;
    margin-bottom:3px;
    padding-left:20px;
}

.bd-minimal .channel-members h2:first-of-type {
	margin-top:0px;
    margin-bottom:3px;
}

.bd-minimal .channel-members .member {
    padding:5px 15px 8px 20px;
}

/* CHANNELS */
.bd-minimal .guild-header header span {
	margin-left:-13px;
	font-size:14px;
}

.bd-minimal .guild-channels ul .channel-text.unread:not(.selected):not(.channel-muted):before {
	top:9px;
    width:10px;
    height:10px;
}

.bd-minimal .guild-channels h2 {
	margin-left:10px;
}

/* ACCOUNT BAR */
.bd-minimal #voice-connection {
	width:176px;
    margin-left:-46px;
}

.bd-minimal .account {
	width:176px;
    margin-left:-46px;
}

.bd-minimal .account .avatar-small {
	display:block !important;
}

.bd-minimal .account .username {
	display:none;
}

.bd-minimal .account .btn-group {
	margin-left:10px;
}

.bd-minimal .account .btn-group .btn {
	width:30px;
}


.bot-tag {
    background: transparent;
    font-size: 10px;
    font-family: "Starwars";
    font-weight: 600;
    color: #fff!important;
    margin-left: 6px;
    padding: 1px 2px;
    border-radius: 3px;
    text-transform: capitalize;
    vertical-align: bottom;
    line-height: 16px;
    -webkit-flex-shrink: 0;
    flex-shrink: 0;
}

.bot-tag.bot-tag-invert {
    background: transparent;
    font-family: "Starwars";
    color: #738bd7!important;
}




