css:
@-moz-document domain("realitygaming.fr") {
.avatar img,
.avatar .img,
.avatarCropper {
   /* background-color: rgb(19, 19, 19);
    border: 1px solid rgb(19, 19, 19);*/
        border-radius: 100%;
}
.sidebar .visitorPanel .avatar img:hover {
    display: inline-block;
    -webkit-transform: rotate(360deg);
    -moz-transform: rotate(360deg);
    -o-transform: rotate(360deg);
    -ms-transform: rotate(360deg);
    -webkit-transform: rotate(360deg);
    -moz-transform: rotate(360deg);
    -o-transform: rotate(360deg);
    -ms-transform: rotate(360deg);
    transform: rotate(360deg);
    -webkit-transform: rotate(360deg);
    -moz-transform: rotate(360deg);
    -ms-transform: rotate(360deg);
    -o-transform: rotate(360deg);
    transition: all 0.35s;
    -webkit-transition: all 0.35s;
    -moz-transition: all 0.35s;
    -ms-transition: all 0.35s;
    -o-transition: all 0.35s;
}
.avatarCropper a, .avatarCropper span, .avatarCropper label {
    overflow: hidden;
    position: relative;
    display: block;
    width: 192px;
    height: 192px;
    border-radius: 100%;
   background-color: rgb(75, 123, 143);
}
.navPopup .avatar img {
    width: 32px;
    border-radius: 100%;
    height: 32px;
}
.messageUserBlock div.avatarHolder .onlineMarker {
    position: absolute;
    border: 3px solid transparent;
    /* border-top-color: rgb(127,185,0); */
    /* border-left-color: rgb(127,185,0); */
    -webkit-border-top-left-radius: 5px;
    -moz-border-radius-topleft: 5px;
    -khtml-border-top-left-radius: 5px;
    /* border-top-left-radius: 5px; */
    -webkit-border-top-right-radius: 3px;
    -moz-border-radius-topright: 3px;
    -khtml-border-top-right-radius: 3px;
    /* border-top-right-radius: 3px; */
    -webkit-border-bottom-left-radius: 3px;
    -moz-border-radius-bottomleft: 3px;
    -khtml-border-bottom-left-radius: 3px;
    /* border-bottom-left-radius: 3px; */
    /* border-radius: 100%; */
    border-color: rgb(127,185,0);
    border-radius: 100%;
    width: 96px;
    height: 96px;
    top: 9px;
    left: 25px;
}
.discussionListItem .posterAvatar .miniMe img {
    padding: 1px;
    border: 1px none black;
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    -khtml-border-radius: 2px;
    border-radius: 2px;
    -webkit-box-shadow: 1px 1px 5px rgba(0,0,0,0.5);
    -moz-box-shadow: 1px 1px 5px rgba(0,0,0,0.5);
    -khtml-box-shadow: 1px 1px 5px rgba(0,0,0,0.5);
    box-shadow: 1px 1px 5px rgba(0,0,0,0.5);
    width: 20px;
    height: 20px;
    border-radius: 100%;
}
.avatar .img.m {
    width: 96px;
    height: 96px;
    border-radius: 100%;
}
.avatar.plainImage img, .avatar.plainImage .img {
    border: none;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    -khtml-border-radius: 0;
    border-radius: 0;
    padding: 0;
    background-position: left top;
    border-radius: 100%;
}
}
