css:
@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("korben.info") {

	body, #main, .containervideo, .d-header
	{
		background: none;
	  background-color: #333 !important;
	  color: #fff !important;
	}
	  
	a
	{
	  color: #ff7b10 !important;
	}
	  
	.topbar
	{
	  display: none !important;
	}
	  
	.header
	{
		background: none;
	  background-color: #333 !important;
	}
	
	.subnav li.domotique, .bigwrap-univers .head h2 span {
		background: none !important;
	}
	
	.bigwrap-univers .head h2 span {
		color: #ff7b10 !important;
	}
	  
	.header .baby-korben
	{
	  background: none !important;
	  background-color: #333 !important;
	}
	.header .navigation .current-menu-item a
	{
	  background: none !important;
	  background-color: #333 !important;
	  color: #999 !important;
	}
	
	/* Borders */
	.universe-border-drones, .universe-border-domotique, .universe-border-hacking, .universe-border-streaming, .universe-border-raspberrypi, .universe-border-jeuxvido, .universe-border-android, .universe-border-windows, .universe-border-linux {
		border-top: solid 3px  #ff7b10 !important;
	}
	
	.bigwrap-univers ul.blocs-univers li.bloc-univers {
		background: #333;
	}
	
	.inscription-buttons ul li a, .bigwrap-univers .filters-univers ul#filters li a.btn-blue, .bigwrap-univers ul.blocs-univers .bloc-univers.news a.link {
		background: #444 !important;
	}
	
	.inscription-buttons ul li a.active, .bigwrap-univers .filters-univers ul#filters li a.btn-blue:active, .bigwrap-univers .filters-univers ul#filters li a.btn-blue.active {
		background: #555 !important;
		border: solid 1px  #ff7b10 !important;
	}
	  
	.edito
	{
	  width: 100% !important;
	  margin-top: 44px !important;
	  margin-left: -40px !important;
	  background: none !important;
	  background-color: #444 !important;
	  color: #fff !important;
	}
	  
	.edito-header, .bigwrap-univers ul.blocs-univers li.bloc-univers .post-date, .bigwrap-univers ul.blocs-univers li.bloc-univers h3, .main.tag .head h2
	{
	  background: none !important;
	}
	  
	.edito-footer, div #comments, .bigwrap-univers .bigadbvert, .bigwrap-univers ul.blocs-univers li.bloc-univers.adbvert
	{
	  display: none !important;
	}
	
	.page-numbers a, .page-numbers .current {
		background: none;
		background-color: #444;
		border: solid 1px #333 !important;
	}
	  
	.subnav
	{
	  background: none !important;
	  background-color: #333 !important;
	  border-bottom: solid 1px #222 !important;
	}
	  
	.main
	{
	  width: 100% !important;
	  margin-left: -40px !important;
	  background-color: #444 !important;
	  color: #fff !important;
	}
	  
	.main .post-content, code, blockquote, ol .comments-list
	{
	  background-color: #444 !important;
	  color: #fff !important;
	}
	
	.jorgen-entry-content, .jorgen-story-pager {
		color: #fff !important;
		background-color: #333 !important;
	}
	  
	.maincontent
	{
	  width: 100% !important;
	  margin: 0px auto !important;
	}
	  
	.post-thumbnail
	{
	  text-align: center !important;
	  width: 100% !important;
	}
	  
	.post-content
	{
	  width: 100% !important;
	  margin: 0px auto !important;
	}
	  
	.ribbon
	{
	  display: none !important;
	}
	  
	.side
	{
	  display: none !important;
	}
	  
	.post-subscribe-title
	{
	  display: none !important;
	}
	  
	.post-landing-register
	{
	  display: none !important;
	}
	  
	.post-social-plugins
	{
	  display: none !important;
	}
	  
	.post-share
	{
	  display: none !important;
	}
	  
	.post-footer
	{
	  display: none !important;
	}
	  
	.respond
	{
	  display: none !important;
	}
	  
	.post-subscribe
	{
	  display: none !important;
	}
	  
	.textwidget
	{
	  display: none !important;
	}
	  
	#us_repot_button
	{
	  display: none !important;
	}
	  
	.univers
	{
	  display: none !important;
	}
	  
	.footer
	{
	  display: none !important;
	}
}
