css:
@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("www.journaldugeek.com") {

	body
	{
	  background-color: #333 !important;
	  color: #fff !important;
	}
	  
	a
	{
	  color: #ff7b10 !important;
	}
	  
	.inner .ident
	{
	  display: none !important;
	}
	  
	.topbar
	{
	  display: none !important;
	}
	.header .navigation .current-menu-item a
	{
	  background: none !important;
	  background-color: #333 !important;
	  color: #999 !important;
	}
	  
	.subnav
	{
	  background: none !important;
	  background-color: #333 !important;
	  border-bottom: solid 1px #222 !important;
	}
	  
	strong
	{
	  color: #999 !important;
	}
	  
	h1
	{
	  color: #fff !important;
	}
	  
	h2, h3, h4, h5
	{
	  color: #ff7b10 !important;
	}
	  
	div, .row
	{
	  border: none !important;
	}
	  
	.button
	{
	  background-color: #333 !important;
	  border: solid 1px #222 !important;
	}
	  
	.hashtags p
	{
	  background-color: #333 !important;
	  border: solid 1px #222 !important;
	}
	  
	.single-test
	{
	  display: none !important;
	}
	  
	.tiles-wide
	{
	  background-color: #333 !important;
	  color: #fff !important;
	}
	  
	.tiles .post-cat
	{
	  background-color: transparent !important;
	  color: #fff !important;
	}
	  
	.tests-push
	{
	  background-color: #333 !important;
	  color: #fff !important;
	}
	  
	.comments-bloc input[type=submit]
	{
	  background-color: #ff7b10 !important;
	  color: #fff !important;
	}
	  
	.post-data .category
	{
	  background: #ff7b10 !important;
	  background-color: #ff7b10 !important;
	  color: #fff !important;
	}
	  
	.post-data .author p.comments a
	{
	  background: transparent !important;
	}
	  
	.subscribe-btn .sub-btn
	{
	  background: #ff7b10 !important;
	  background-color: #ff7b10 !important;
	  color: #fff !important;
	}
	  
	.main
	{
	  width: 100% !important;
	  margin-left: -40px !important;
	  background-color: #333 !important;
	  color: #fff !important;
	}
	  
	.maincontent
	{
	  width: 100% !important;
	  margin: 0px auto !important;
	}
	  
	.post-thumbnail
	{
	  text-align: center !important;
	  width: 100% !important;
	}
	  
	.post-content
	{
	  width: 100% !important;
	  margin: 0px auto !important;
	  color: #fff !important;
	}
	  
	.post-content p
	{
	  color: #fff !important;
	}
	  
	.col-md-8
	{
	  width: 100% !important;
	  border: none !important;
	  padding-left: 60px;
	}
	  
	.proofreader-bloc
	{
	  display: none !important;
	}
	  
	.aller-plus-loin
	{
	  display: none !important;
	}
	  
	.others-videos
	{
	  display: none !important;
	}
	  
	.side
	{
	  display: none !important;
	}
	  
	.post .square .border .partner
	{
	  display: none !important;
	}
	  
	.post-subscribe-title
	{
	  display: none !important;
	}
	  
	.post-landing-register
	{
	  display: none !important;
	}
	  
	.post-social-plugins
	{
	  display: none !important;
	}
	  
	.post-share
	{
	  display: none !important;
	}
	  
	.post-footer
	{
	  display: none !important;
	}
	  
	.respond
	{
	  display: none !important;
	}
	  
	.post-subscribe
	{
	  display: none !important;
	}
	  
	.textwidget
	{
	  display: none !important;
	}
	  
	#us_repot_button
	{
	  display: none !important;
	}
	  
	.contenu-relatif
	{
	  display: none !important;
	}
	  
	.comments-bloc
	{
	  border: none !important;
	}
	  
	.comments-bloc .comment-list
	{
	  border: none !important;
	}
	  
	.comments-bloc .comment-list .comment-body
	{
	  padding: 5px !important;
	}
	  
	.comments-bloc .comment-list .comment
	{
	  background-color: #444 !important;
	  color: #fff !important;
	  border: none !important;
	}
	  
	#cookieChoiceInfo
	{
	  display: none !important;
	}
	  
	.sticky-footer
	{
	  display: none !important;
	}

	.blockadd {

		display: none !important;
	}

	footer
	{
	  display: none !important;
	}
}
