css:
@-moz-document domain("www.nokenny.com"), domain("www.nokenny.co") {
body,
.boutonfollow a.nom, .titre_commentaire {
    color: #ccc;
}
body,
.container.contenu *, .commentaires, .titre_commentaire, .card, .tabs-nav {
    background-color: #1b1b1b !important;
}
.similaires_big, #coldroite, .grostitre, .nuage, .boucle, .sousbillet {
    display: none !important;
}
.boucle .item h2 a,
.billet h1,
.box_boucle h3 a:link {
    color: #14D2FF !important; /* bleu des titres */
    text-shadow: none !important;
}
.box_boucle h3 a:visited {
	color: #2BACAD !important; /* bleu des titres déja visités */
}
#coldroite {
    width: 0px !important;
    min-width: 0px !important;
    max-width: 0px !important;
}
#colgauche {
    max-width: 1200px !important;
}
.billet {
    width: 100% !important;
}
.titre,
.billet #tabvideos a,
.box_boucle .infos a,
.blocabonnements .titre a,
visited,
.grostitre {
    color: #ccc !important;
}
.texte,
.blocpopulaires .sujet {
    color: white !important;
}
.commentaires .texte a.reply {
    color: #F96767;
}
.commentaires .boucle .pseudo a {
    color: #DE9494;
}
.boxheader h1 {
    color: #00D487;
}
.lightbox {
    background: transparent !important;
}
.boxheader_partages a.btn,
.commentaires_titre,
.btn-default {
    color: #ccc;
}
.infos a {
    color: #57A1D8 !important;
}
.infos {
    color: #0DEA00 !important;
}
#player_div * {
    background: none !important;
}
.navbar.navbar-principal {
    background-color: #1b1b1b !important;
}
.navbar-default, .navbar-default .navbar-toggle:focus, .navbar-default .navbar-toggle:hover {
    background-color: #1b1b1b;
}
.followbar {
    background-color: #1b1b1b;
}
.navbar-default .navbar-nav>li>a, .navbar-default .navbar-text {
	color: #a2a2a2;
}
.followbar .reactions a, visited {
	border: none;    
}
.followbar a {
    color: #eee;
}
}
