css:
@-moz-document domain("www.phonandroid.com") {

	body, table, td {
	  color: #fff !important;
	  background-color: #333 !important;
	}
	  
	a {
	  color: #508929 !important;
	}
	  
	p {
	  color: #fff !important;
	  text-align: justify !important;
	}
	  
	h2 {
	  color: #999 !important;
	}
	  
	.menu-item a {
	  color: #fff !important;
	}
	  
	.nwcook-message {
	  display: none !important;
	}
	  
	#secondary-nav {
	  display: none !important;
	}
	  
	#main-bg {
	  background: transparent !important;
	}
	  
	#main-container {
	  background: transparent !important;
	  color: #fff !important;
	  background-color: #333 !important;
	  text-shadow: none !important;
	  padding-bottom: 0px !important;
	}
	  
	.mainleft {
	  width: 100% !important;
	  text-align: justify !important;
	}
	  
	.main-content-txt {
	  background: transparent !important;
	  color: #fff !important;
	  background-color: #333 !important;
	}
	  
	div, .main-content-archive-col, .main-content-archive-lgn, .main-content-txt {
	  background: transparent !important;
	  color: #fff !important;
	  background-color: #333 !important;
	  -webkit-box-shadow: none !important;
	  box-shadow: none !important;
	}
	  
	#slider, #slider .slider-img .slider-desc {
	  background-color: #333 !important;
	  -webkit-box-shadow: none !important;
	  box-shadow: none !important;
	}
	  
	article.wrap {
	  background-color: #333 !important;
	  border: none !important;
	  -webkit-box-shadow: none !important;
	  box-shadow: none !important;
	}
	  
	#fil-arianne {
	  display: none !important;
	}
	  
	footer.bgauthordate {
	  border: none !important;
	  background: transparent !important;
	  background-image: none !important;
	  box-shadow: none !important;
	}
	  
	.section-titre {
	  border: none !important;
	  background: transparent !important;
	  background-image: none !important;
	  box-shadow: none !important;
	}
	  
	.titre1, .titre1 a, .titre2, .titre2 a, .titre3, .titre3 a, .titre4, .titre4 a, .titre5, .titre5 a, .titre6, .titre6 a, .titre1 h1 {
	  background: transparent !important;
	  background-image: none !important;
	  text-shadow: none !important;
	  color: #508929 !important;
	}
	  
	.morepost_btn a {
	  background: transparent !important;
	  background-image: none !important;
	  text-shadow: none !important;
	  color: #508929 !important;
	}
	  
	aside {
	  display: none !important;
	}
	  
	.sumome-share-client-wrapper.sumome-share-client-wrapper-left-page {
	  display: none !important;
	}
	  
	.bgtagssocial {
	  display: none !important;
	}
	  
	.single .pagination {
	  display: none !important;
	}
	  
	.single .main-content-txt .section-titre.short-sectiontitre {
	  display: none !important;
	}
	  
	.yarpp-related {
	  display: none !important;
	}
	  
	#disqus_thread {
	  color: #fff !important;
	}
	  
	footer {
	  display: none !important;
	}
}
